    <script>
        // Graph data structure
        const vertices = ['m', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'];
        const fixedWeights = {
            'm-n': 8, 'n-o': 7, 'o-p': 7,
            'i-j': 9, 'j-k': 7, 'k-l': 1,
            'e-f': 1, 'f-g': 7, 'g-h': 6,
            'a-b': 10, 'b-c': 4, 'c-d': 8,
            'm-i': 6, 'i-e': 9, 'e-a': 9,
            'n-j': 7, 'j-f': 6, 'f-b': 5,
            'o-k': 1, 'k-g': 8, 'g-c': 8,
            'p-l': 2, 'l-h': 1, 'h-d': 7
        };

        let currentWeights = {...fixedWeights};
        let graph = {};
        let currentStart = 'm';
        let distances = {};
        let visited = new Set();
        let predecessors = {};
        let p5Instance;

        // Initialize graph adjacency list
        function initializeGraph() {
            graph = {};
            vertices.forEach(v => graph[v] = {});

            for (let edge in currentWeights) {
                const [v1, v2] = edge.split('-');
                graph[v1][v2] = currentWeights[edge];
                graph[v2][v1] = currentWeights[edge];
            }
        }

        // Position mapping for vertices (4x4 grid)
        const positions = {
            'm': {x: 100, y: 100}, 'n': {x: 300, y: 100}, 'o': {x: 500, y: 100}, 'p': {x: 700, y: 100},
            'i': {x: 100, y: 300}, 'j': {x: 300, y: 300}, 'k': {x: 500, y: 300}, 'l': {x: 700, y: 300},
            'e': {x: 100, y: 500}, 'f': {x: 300, y: 500}, 'g': {x: 500, y: 500}, 'h': {x: 700, y: 500},
            'a': {x: 100, y: 700}, 'b': {x: 300, y: 700}, 'c': {x: 500, y: 700}, 'd': {x: 700, y: 700}
        };

        // Reset algorithm state
        function resetAlgorithm() {
            distances = {};
            visited = new Set();
            predecessors = {};

            vertices.forEach(v => {
                distances[v] = v === currentStart ? 0 : Infinity;
            });

            document.getElementById('next-step-btn').disabled = false;
        }

        // p5.js sketch
        const sketch = (p) => {
            p.setup = () => {
                const canvas = p.createCanvas(800, 800);
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);
                p.textAlign(p.CENTER, p.CENTER);

                // Draw edges
                for (let v1 in graph) {
                    for (let v2 in graph[v1]) {
                        if (vertices.indexOf(v1) < vertices.indexOf(v2)) {
                            const pos1 = positions[v1];
                            const pos2 = positions[v2];

                            // Check if this edge is in shortest path tree
                            const inSPT = (predecessors[v2] === v1 || predecessors[v1] === v2);

                            p.strokeWeight(inSPT ? 5 : 1);
                            p.stroke(inSPT ? 0 : 0, 0, inSPT ? 150 : 0);
                            p.line(pos1.x, pos1.y, pos2.x, pos2.y);

                            // Draw weight
                            const midX = (pos1.x + pos2.x) / 2;
                            const midY = (pos1.y + pos2.y) / 2;
                            p.fill(255);
                            p.noStroke();
                            p.rect(midX - 15, midY - 10, 30, 20);
                            p.fill(0);
                            p.text(graph[v1][v2], midX, midY);
                        }
                    }
                }

                // Draw vertices
                vertices.forEach(v => {
                    const pos = positions[v];

                    p.strokeWeight(2);
                    p.stroke(0);
                    p.fill(visited.has(v) ? 255 : 0, 0, 0);
                    p.circle(pos.x, pos.y, 20);

                    // Draw vertex label
                    p.fill(0);
                    p.noStroke();
                    p.textSize(16);
                    p.text(v, pos.x, pos.y + 35);

                    // Draw distance label
                    const dist = distances[v];
                    const distText = dist === Infinity ? '∞' : dist.toString();
                    p.fill(visited.has(v) ? 255 : 0, 0, 0);
                    p.textSize(14);
                    p.text('(' + distText + ')', pos.x, pos.y - 25);
                });
            };
        };

        // Initialize p5
        p5Instance = new p5(sketch);

        // Event handlers
        document.getElementById('start-over-btn').addEventListener('click', () => {
            currentStart = 'm';
            currentWeights = {...fixedWeights};
            initializeGraph();
            resetAlgorithm();
            updateVertexButtons();
            updateTitle();
            p5Instance.redraw();
        });

        // Vertex button handlers
        vertices.forEach(v => {
            document.getElementById(`btn-${v}`).addEventListener('click', () => {
                currentStart = v;
                resetAlgorithm();
                updateVertexButtons();
                updateTitle();
                p5Instance.redraw();
            });
        });

        document.getElementById('fixed-example-btn').addEventListener('click', () => {
            currentWeights = {...fixedWeights};
            initializeGraph();
            resetAlgorithm();
            updateTitle();
            p5Instance.redraw();
        });

        document.getElementById('random-example-btn').addEventListener('click', () => {
            for (let edge in currentWeights) {
                currentWeights[edge] = Math.floor(Math.random() * 10) + 1;
            }
            initializeGraph();
            resetAlgorithm();
            updateTitle();
            p5Instance.redraw();
        });

        document.getElementById('next-step-btn').addEventListener('click', () => {
            // Find unvisited vertex with minimum distance
            let minVertex = null;
            let minDistance = Infinity;

            vertices.forEach(v => {
                if (!visited.has(v) && distances[v] < minDistance) {
                    minDistance = distances[v];
                    minVertex = v;
                }
            });

            if (minVertex !== null) {
                visited.add(minVertex);

                // Update distances to neighbors
                for (let neighbor in graph[minVertex]) {
                    if (!visited.has(neighbor)) {
                        const newDistance = distances[minVertex] + graph[minVertex][neighbor];
                        if (newDistance < distances[neighbor]) {
                            distances[neighbor] = newDistance;
                            predecessors[neighbor] = minVertex;
                        }
                    }
                }

                // Check if algorithm is complete
                if (visited.size === vertices.length) {
                    document.getElementById('next-step-btn').disabled = true;
                }

                p5Instance.redraw();
            }
        });

        function updateVertexButtons() {
            vertices.forEach(v => {
                const btn = document.getElementById(`btn-${v}`);
                btn.classList.toggle('active', v === currentStart);
            });
        }

        function updateTitle() {
            document.getElementById('title-display').textContent = 
                `Dijkstra's algorithm starting at vertex ${currentStart}`;
        }

        // Initialize on page load
        window.addEventListener('load', () => {
            initializeGraph();
            resetAlgorithm();
            updateVertexButtons();
            updateTitle();
        });
    </script>
