<body>
    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label for="slider-sphere-diameter">diameter of spherelabel>
                <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">
                <span class="value-display" id="sphere-diameter-value">1.5span>
            </div>

            <div class="control-group">
                <label>polyhedron verticeslabel>
                <div id="polyhedron-buttons">
                    <button class="poly-btn" id="btn-cube">Cubebutton>
                    <button class="poly-btn" id="btn-dodecahedron">Dodecahedronbutton>
                    <button class="poly-btn" id="btn-icosahedron">Icosahedronbutton>
                    <button class="poly-btn active" id="btn-octahedron">Octahedronbutton>
                    <button class="poly-btn" id="btn-tetrahedron">Tetrahedronbutton>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-cone-height">heightlabel>
                <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">
                <span class="value-display" id="cone-height-value">1.5span>
            </div>

            <div class="control-group">
                <label for="slider-cone-base">baselabel>
                <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
                <span class="value-display" id="cone-base-value">0.4span>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 0, 8);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth - 320, window.innerHeight);
        renderer.shadowMap.enabled = true;
        renderer.shadowMap.type = THREE.PCFSoftShadowMap;
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;
        controls.screenSpacePanning = false;
        controls.minDistance = 3;
        controls.maxDistance = 20;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040, 1.5);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1);
        directionalLight1.castShadow = true;
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Objects
        let sphere, cones = [];
        const defaultSphereDiameter = 1.5;
        const defaultSphereRadius = defaultSphereDiameter / 2;

        // Materials
        const sphereMaterial = new THREE.MeshPhongMaterial({ 
            color: 0xdddddd, 
            shininess: 50,
            specular: 0x222222
        });

        const coneMaterial = new THREE.MeshPhongMaterial({ 
            color: 0xffa500, 
            shininess: 30,
            specular: 0x333333
        });

        // Initialize sphere
        function createSphere() {
            sphere = new THREE.Mesh(
                new THREE.SphereGeometry(defaultSphereRadius, 32, 32),
                sphereMaterial
            );
            sphere.castShadow = true;
            sphere.receiveShadow = true;
            scene.add(sphere);
        }

        // Get polyhedron vertices
        function getPolyhedronVertices(type) {
            let geometry;
            switch(type) {
                case 'cube':
                    geometry = new THREE.BoxGeometry(1, 1, 1);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(1);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(1);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(1);
                    break;
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(1);
                    break;
            }

            const vertices = [];
            const positions = geometry.attributes.position.array;

            for (let i = 0; i < positions.length; i += 3) {
                vertices.push(new THREE.Vector3(positions[i], positions[i + 1], positions[i + 2]));
            }

            // Remove duplicates
            const uniqueVertices = [];
            const vertexSet = new Set();

            vertices.forEach(v => {
                const key = `${v.x.toFixed(3)},${v.y.toFixed(3)},${v.z.toFixed(3)}`;
                if (!vertexSet.has(key)) {
                    vertexSet.add(key);
                    uniqueVertices.push(v);
                }
            });

            geometry.dispose();
            return uniqueVertices;
        }

        // Create cones at vertices
        function createCones(polyhedronType, coneHeight, coneBaseRadius, sphereRadius) {
            // Remove existing cones
            cones.forEach(cone => {
                scene.remove(cone);
                cone.geometry.dispose();
            });
            cones = [];

            const vertices = getPolyhedronVertices(polyhedronType);

            vertices.forEach(vertex => {
                const direction = vertex.normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);

                const coneGeometry = new THREE.ConeGeometry(coneBaseRadius, coneHeight, 32);
                const coneMesh = new THREE.Mesh(coneGeometry, coneMaterial);

                coneMesh.position.copy(position);
                coneMesh.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
                coneMesh.castShadow = true;
                coneMesh.receiveShadow = true;

                scene.add(coneMesh);
                cones.push(coneMesh);
            });
        }

        // Update sphere size
        function updateSphereSize(newDiameter) {
            const scale = newDiameter / defaultSphereDiameter;
            sphere.scale.set(scale, scale, scale);

            // Update cone positions
            const sphereRadius = newDiameter / 2;
            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);

            updateConePositions(sphereRadius, coneHeight);
        }

        // Update cone geometry
        function updateConeGeometry(newHeight, newBaseRadius) {
            cones.forEach(cone => {
                cone.geometry.dispose();
                cone.geometry = new THREE.ConeGeometry(newBaseRadius, newHeight, 32);
            });
        }

        // Update cone positions
        function updateConePositions(sphereRadius, coneHeight) {
            const polyhedronType = document.querySelector('#polyhedron-buttons .active').id.replace('btn-', '');
            const vertices = getPolyhedronVertices(polyhedronType);

            cones.forEach((cone, index) => {
                const direction = vertices[index].normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
            });
        }

        // Event listeners
        document.getElementById('slider-sphere-diameter').addEventListener('input', function(e) {
            const value = parseFloat(e.target.value);
            document.getElementById('sphere-diameter-value').textContent = value.toFixed(2);
            updateSphereSize(value);
        });

        document.getElementById('slider-cone-height').addEventListener('input', function(e) {
            const value = parseFloat(e.target.value);
            document.getElementById('cone-height-value').textContent = value.toFixed(2);

            const sphereRadius = parseFloat(document.getElementById('slider-sphere-diameter').value) / 2;
            const coneBaseRadius = parseFloat(document.getElementById('slider-cone-base').value);

            updateConeGeometry(value, coneBaseRadius);
            updateConePositions(sphereRadius, value);
        });

        document.getElementById('slider-cone-base').addEventListener('input', function(e) {
            const value = parseFloat(e.target.value);
            document.getElementById('cone-base-value').textContent = value.toFixed(2);

            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
            updateConeGeometry(coneHeight, value);
        });

        // Polyhedron button handlers
        document.querySelectorAll('.poly-btn').forEach(btn => {
            btn.addEventListener('click', function(e) {
                document.querySelectorAll('.poly-btn').forEach(b => b.classList.remove('active'));
                this.classList.add('active');

                const polyhedronType = this.id.replace('btn-', '');
                const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
                const coneBaseRadius = parseFloat(document.getElementById('slider-cone-base').value);
                const sphereDiameter = parseFloat(document.getElementById('slider-sphere-diameter').value);
                const sphereRadius = sphereDiameter / 2;

                createCones(polyhedronType, coneHeight, coneBaseRadius, sphereRadius);
            });
        });

        // Window resize handler
        window.addEventListener('resize', () => {
            const width = window.innerWidth - 320;
            const height = window.innerHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            renderer.setSize(width, height);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        createSphere();
        createCones('octahedron', 1.5, 0.4, defaultSphereRadius);
        animate();
    </script>
