                        axis angle relative to ecliptic
                        <div class="slider-value" id="value-tilt">23.5°</div>
                    </div>
                    <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">

                    <div class="slider-label" id="label-equinox">
                        spring equinox point
                        <div class="slider-value" id="value-equinox">0°</div>
                    </div>
                    <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">

                    <div class="slider-label" id="label-eccentricity">
                        orbit eccentricity
                        <div class="slider-value" id="value-eccentricity">0.100</div>
                    </div>
                    <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
                </div>
            </div>

            <div class="control-group" id="display-controls">
                <div class="checkbox-container">
                    <input type="checkbox" id="checkbox-snapshots" checked>
                    <label for="checkbox-snapshots">show sun snapshots</label>
                </div>
