    <script>
        // Planet data
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        // Get DOM elements
        const selectPlanet = document.getElementById('select-planet');
        const checkboxManual = document.getElementById('checkbox-manual');
        const sliderTilt = document.getElementById('slider-tilt');
        const sliderEquinox = document.getElementById('slider-equinox');
        const sliderEccentricity = document.getElementById('slider-eccentricity');
        const checkboxSnapshots = document.getElementById('checkbox-snapshots');
        const checkboxScales = document.getElementById('checkbox-scales');

        // Value display elements
        const valueTilt = document.getElementById('value-tilt');
        const valueEquinox = document.getElementById('value-equinox');
        const valueEccentricity = document.getElementById('value-eccentricity');

        // Initialize p5.js
        let sketch = function(p) {
            let analemmaPoints = [];
            let scale = 200;

            p.setup = function() {
                const canvasContainer = document.getElementById('visualization-area');
                const canvas = p.createCanvas(canvasContainer.clientWidth - 40, canvasContainer.clientHeight - 40);
                canvas.parent('visualization-area');
                p.angleMode(p.RADIANS);
                calculateAnalemma();
            };

            p.draw = function() {
                p.background(255);
                p.translate(p.width / 2, p.height / 2);

                // Get current values
                const tilt = parseFloat(sliderTilt.value) * p.PI / 180;
                const ecc = parseFloat(sliderEccentricity.value);
                const equinoxAngle = parseFloat(sliderEquinox.value) * p.PI / 180;
                const showSnapshots = checkboxSnapshots.checked;
                const showScales = checkboxScales.checked;

                // Draw orbit ellipse
                drawOrbitEllipse(ecc);

                // Draw scales
                if (showScales) {
                    drawScales();
                }

                // Draw analemma curve
                drawAnalemmaCurve();

                // Draw sun snapshots
                if (showSnapshots) {
                    drawSunSnapshots();
                }
            };

            function drawOrbitEllipse(ecc) {
                p.push();
                p.translate(-(p.width / 4), -(p.height / 3));
                p.stroke(139, 69, 19); // Brown color
                p.strokeWeight(2);
                p.noFill();

                const a = 50; // Semi-major axis
                const b = a * p.sqrt(1 - ecc * ecc); // Semi-minor axis
                p.ellipse(0, 0, 2 * a, 2 * b);
                p.pop();
            }

            function drawScales() {
                p.stroke(100);
                p.strokeWeight(1);

                // X-axis
                p.line(-200, 0, 200, 0);
                // Y-axis
                p.line(0, -150, 0, 150);

                // X-axis ticks and labels
                for (let x = -40; x <= 40; x += 10) {
                    const px = x * scale / 40;
                    p.line(px, -5, px, 5);
                    if (x !== 0) {
                        p.push();
                        p.fill(100);
                        p.noStroke();
                        p.align(p.CENTER, p.TOP);
                        p.text(x, px, 10);
                        p.pop();
                    }
                }

                // Y-axis ticks and labels
                for (let y = -30; y <= 30; y += 10) {
                    const py = -y * scale / 30;
                    p.line(-5, py, 5, py);
                    if (y !== 0) {
                        p.push();
                        p.fill(100);
                        p.noStroke();
                        p.align(p.RIGHT, p.CENTER);
                        p.text(y, -10, py);
                        p.pop();
                    }
                }
            }

            function drawAnalemmaCurve() {
                if (analemmaPoints.length < 2) return;

                p.noFill();
                p.stroke(255, 165, 0); // Orange color
                p.strokeWeight(2);

                p.beginShape();
                for (let point of analemmaPoints) {
                    p.vertex(point.x, point.y);
                }
                p.endShape(p.CLOSE);
            }

            function drawSunSnapshots() {
                p.fill(255, 165, 0); // Orange color
                p.noStroke();

                for (let i = 0; i < analemmaPoints.length; i += 20) {
                    const point = analemmaPoints[i];
                    p.circle(point.x, point.y, 8);
                }
            }

            function calculateAnalemma() {
                analemmaPoints = [];
                const tilt = parseFloat(sliderTilt.value) * p.PI / 180;
                const ecc = parseFloat(sliderEccentricity.value);
                const equinoxAngle = parseFloat(sliderEquinox.value) * p.PI / 180;

                for (let d = 0; d < 365; d++) {
                    // Mean anomaly
                    const M = (2 * p.PI * d) / 365.25;

                    // Solve Kepler's equation for eccentric anomaly (Newton's method)
                    let E = M;
                    for (let i = 0; i < 10; i++) {
                        E = E - (E - ecc * p.sin(E) - M) / (1 - ecc * p.cos(E));
                    }

                    // True anomaly
                    let v = 2 * p.atan2(p.sqrt(1 + ecc) * p.sin(E / 2), p.sqrt(1 - ecc) * p.cos(E / 2));

                    // Ecliptic longitude
                    let lambda = v + equinoxAngle;

                    // Y-coordinate (declination)
                    let y = p.asin(p.sin(tilt) * p.sin(lambda));

                    // Right ascension
                    let alpha = p.atan2(p.cos(tilt) * p.sin(lambda), p.cos(lambda));

                    // Equation of Time
                    let EoT = M - alpha;

                    // Handle the -π/π boundary
                    if (EoT > p.PI) {
                        EoT -= 2 * p.PI;
                    } else if (EoT < -p.PI) {
                        EoT += 2 * p.PI;
                    }

                    // Convert to canvas coordinates
                    const x = EoT * scale;
                    const py = -y * scale; // Negative because canvas y is flipped

                    analemmaPoints.push({x: x, y: py});
                }
            }

            p.windowResized = function() {
                const canvasContainer = document.getElementById('visualization-area');
                p.resizeCanvas(canvasContainer.clientWidth - 40, canvasContainer.clientHeight - 40);
            };
        };

        new p5(sketch);

        // Event listeners
        selectPlanet.addEventListener('change', function() {
            if (this.value === 'manual') {
                checkboxManual.checked = true;
                enableManualControls(true);
            } else {
                checkboxManual.checked = false;
                const planet = planetData[this.value];
                sliderTilt.value = planet.tilt;
                sliderEquinox.value = planet.equinox;
                sliderEccentricity.value = planet.eccentricity;
                updateSliderValues();
                enableManualControls(false);
            }
        });

        checkboxManual.addEventListener('change', function() {
            if (this.checked) {
                selectPlanet.value = 'manual';
                enableManualControls(true);
            } else {
                enableManualControls(false);
            }
        });

        sliderTilt.addEventListener('input', updateSliderValues);
        sliderEquinox.addEventListener('input', updateSliderValues);
        sliderEccentricity.addEventListener('input', updateSliderValues);

        checkboxSnapshots.addEventListener('change', function() {
            sketch.draw();
        });

        checkboxScales.addEventListener('change', function() {
            sketch.draw();
        });

        function enableManualControls(enable) {
            sliderTilt.disabled = !enable;
            sliderEquinox.disabled = !enable;
            sliderEccentricity.disabled = !enable;
        }

        function updateSliderValues() {
            valueTilt.textContent = parseFloat(sliderTilt.value).toFixed(1) + '°';
            valueEquinox.textContent = parseFloat(sliderEquinox.value).toFixed(0) + '°';
            valueEccentricity.textContent = parseFloat(sliderEccentricity.value).toFixed(3);

            // Recalculate and redraw
            sketch.calculateAnalemma();
            sketch.draw();
        }

        // Initialize
        updateSliderValues();
    </script>
</body>
</html>
