    <script>
        // Global state
        let graph = {
            vertices: {},
            edges: {},
            optimalPath: [],
            optimalWeight: 138
        };

        let currentAlgorithm = 'nearest-neighbor';
        let selectedVertex = null;
        let algorithmState = {
            path: [],
            visitedVertices: new Set(),
            currentVertex: null,
            stepCount: 0,
            totalWeight: 0,
            sortedEdges: [],
            isComplete: false
        };

        let isFixedExample = true;
        let p5Algo = null;
        let p5Optimal = null;

        // Fixed graph data
        const fixedVertices = {
            A: { x: 200, y: 60 },
            B: { x: 80, y: 120 },
            C: { x: 60, y: 240 },
            D: { x: 150, y: 340 },
            E: { x: 250, y: 340 },
            F: { x: 340, y: 240 },
            G: { x: 320, y: 120 }
        };

        const fixedEdges = {
            'A-B': 11, 'A-C': 16, 'A-D': 44, 'A-E': 30, 'A-F': 44, 'A-G': 23,
            'B-C': 12, 'B-D': 12, 'B-E': 10, 'B-F': 30, 'B-G': 25,
            'C-D': 43, 'C-E': 32, 'C-F': 36, 'C-G': 44,
            'D-E': 41, 'D-F': 30, 'D-G': 35,
            'E-F': 31, 'E-G': 20,
            'F-G': 18
        };

        // Initialize the application
        function init() {
            loadFixedExample();
            setupEventListeners();
            createP5Sketch('canvas-algo-wrapper', (p) => { p5Algo = p; });
        }

        // Load fixed example
        function loadFixedExample() {
            isFixedExample = true;
            graph.vertices = { ...fixedVertices };
            graph.edges = { ...fixedEdges };

            // Pre-calculated optimal path for fixed example
            graph.optimalPath = ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'];
            graph.optimalWeight = 138;

            resetAlgorithm();
            updateUIForNewExample();
        }

        // Generate random example
        function generateRandomExample() {
            isFixedExample = false;

            // Generate random vertex positions
            const vertexLabels = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];
            graph.vertices = {};
            graph.edges = {};

            for (let i = 0; i < vertexLabels.length; i++) {
                const angle = (i / vertexLabels.length) * 2 * Math.PI - Math.PI / 2;
                const radius = 140;
                const centerX = 200;
                const centerY = 200;

                graph.vertices[vertexLabels[i]] = {
                    x: centerX + radius * Math.cos(angle),
                    y: centerY + radius * Math.sin(angle)
                };
            }

            // Generate random edge weights
            for (let i = 0; i < vertexLabels.length; i++) {
                for (let j = i + 1; j < vertexLabels.length; j++) {
                    const key = `${vertexLabels[i]}-${vertexLabels[j]}`;
                    graph.edges[key] = Math.floor(Math.random() * 41) + 10;
                }
            }

            // Calculate optimal solution via brute force
            calculateOptimalSolution();
            resetAlgorithm();
            updateUIForNewExample();
        }

        // Calculate optimal solution using brute force
        function calculateOptimalSolution() {
            const vertexLabels = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];
            let minWeight = Infinity;
            let minPath = [];

            // Generate all permutations
            function permute(arr, start = 0) {
                if (start === arr.length - 1) {
                    // Complete the cycle
                    const path = [...arr, arr[0]];
                    let weight = 0;

                    for (let i = 0; i < path.length - 1; i++) {
                        const edgeKey = [path[i], path[i + 1]].sort().join('-');
                        weight += graph.edges[edgeKey];
                    }

                    if (weight < minWeight) {
                        minWeight = weight;
                        minPath = path;
                    }
                    return;
                }

                for (let i = start; i < arr.length; i++) {
                    [arr[start], arr[i]] = [arr[i], arr[start]];
                    permute(arr, start + 1);
                    [arr[start], arr[i]] = [arr[i], arr[start]];
                }
            }

            permute(vertexLabels);
            graph.optimalPath = minPath;
            graph.optimalWeight = minWeight;
        }

        // Reset algorithm state
        function resetAlgorithm() {
            algorithmState = {
                path: [],
                visitedVertices: new Set(),
                currentVertex: null,
                stepCount: 0,
                totalWeight: 0,
                sortedEdges: [],
                isComplete: false
            };

            selectedVertex = null;
            document.getElementById('btn-next-step').disabled = true;
            document.getElementById('text-total-weight').textContent = '';

            // Clear table
            document.querySelector('#table-edge-list tbody').innerHTML = '';

            // Reset vertex buttons
            if (currentAlgorithm === 'nearest-neighbor') {
                document.querySelectorAll('.vertex-btn').forEach(btn => {
                    btn.disabled = false;
                    btn.classList.remove('selected');
                });
            }

            updateUIForAlgorithmChange();
        }

        // Setup event listeners
        function setupEventListeners() {
            // Algorithm selection
            document.getElementById('algo-nearest-neighbor').addEventListener('change', (e) => {
                if (e.target.checked) {
                    currentAlgorithm = 'nearest-neighbor';
                    resetAlgorithm();
                }
            });

            document.getElementById('algo-cheapest-link').addEventListener('change', (e) => {
                if (e.target.checked) {
                    currentAlgorithm = 'cheapest-link';
                    resetAlgorithm();
                }
            });

            // Control buttons
            document.getElementById('btn-start-over').addEventListener('click', resetAlgorithm);
            document.getElementById('btn-fixed-example').addEventListener('click', loadFixedExample);
            document.getElementById('btn-random-example').addEventListener('click', generateRandomExample);
            document.getElementById('btn-next-step').addEventListener('click', executeAlgorithmStep);

            // Vertex selection buttons
            document.querySelectorAll('.vertex-btn').forEach(btn => {
                btn.addEventListener('click', (e) => {
                    selectVertex(e.target.dataset.vertex);
                });
            });

            // Optimal solution checkbox
            document.getElementById('checkbox-optimal').addEventListener('change', (e) => {
                const container = document.getElementById('optimal-result-container');
                container.style.display = e.target.checked ? 'block' : 'none';

                if (e.target.checked && !p5Optimal) {
                    createP5Sketch('canvas-optimal-wrapper', (p) => { p5Optimal = p; }, true);
                }
            });
        }

        // Select starting vertex
        function selectVertex(vertex) {
            selectedVertex = vertex;
            algorithmState.currentVertex = vertex;
            algorithmState.visitedVertices.add(vertex);

            document.querySelectorAll('.vertex-btn').forEach(btn => {
                btn.disabled = true;
                btn.classList.remove('selected');
            });
            document.getElementById(`btn-vertex-${vertex}`).classList.add('selected');

            document.getElementById('btn-next-step').disabled = false;
            document.getElementById('title-algo').textContent = `The nearest neighbor algorithm starting at vertex ${vertex}`;
        }

        // Execute one step of the algorithm
        function executeAlgorithmStep() {
            if (algorithmState.isComplete) return;

            if (currentAlgorithm === 'nearest-neighbor') {
                executeNearestNeighborStep();
            } else {
                executeCheapestLinkStep();
            }

            algorithmState.stepCount++;
            updateAlgorithmDisplay();

            if (algorithmState.isComplete) {
                document.getElementById('btn-next-step').disabled = true;
                document.getElementById('text-total-weight').textContent = `Total weight: ${algorithmState.totalWeight}`;
            }
        }

        // Execute Nearest Neighbor step
        function executeNearestNeighborStep() {
            const current = algorithmState.currentVertex;
            let minDistance = Infinity;
            let nextVertex = null;

            // Find nearest unvisited neighbor
            for (const edge in graph.edges) {
                const [v1, v2] = edge.split('-');
                let neighbor = null;

                if (v1 === current && !algorithmState.visitedVertices.has(v2)) {
                    neighbor = v2;
                } else if (v2 === current && !algorithmState.visitedVertices.has(v1)) {
                    neighbor = v1;
                }

                if (neighbor) {
                    const distance = graph.edges[edge];
                    if (distance < minDistance) {
                        minDistance = distance;
                        nextVertex = neighbor;
                    }
                }
            }

            if (nextVertex) {
                // Add edge to path
                algorithmState.path.push([current, nextVertex]);
                algorithmState.totalWeight += minDistance;
                algorithmState.visitedVertices.add(nextVertex);
                algorithmState.currentVertex = nextVertex;

                // Check if all vertices visited
                if (algorithmState.visitedVertices.size === Object.keys(graph.vertices).length) {
                    // Add return edge to start
                    const lastEdge = [algorithmState.currentVertex, selectedVertex].sort();
                    const lastEdgeKey = lastEdge.join('-');
                    algorithmState.path.push([algorithmState.currentVertex, selectedVertex]);
                    algorithmState.totalWeight += graph.edges[lastEdgeKey];
                    algorithmState.isComplete = true;
                }
            }
        }

        // Execute Cheapest Link step
        function executeCheapestLinkStep() {
            if (algorithmState.sortedEdges.length === 0) {
                // Sort all edges by weight
                algorithmState.sortedEdges = Object.entries(graph.edges)
                    .sort((a, b) => a[1] - b[1])
                    .map(([edge, weight]) => ({ edge, weight, vertices: edge.split('-') }));
            }

            // Find next valid edge
            while (algorithmState.sortedEdges.length > 0 && algorithmState.path.length < 7) {
                const { edge, weight, vertices } = algorithmState.sortedEdges.shift();

                if (canAddEdge(vertices)) {
                    algorithmState.path.push(vertices);
                    algorithmState.totalWeight += weight;

                    if (algorithmState.path.length === 7) {
                        algorithmState.isComplete = true;
                    }
                    break;
                }
            }
        }

        // Check if edge can be added in Cheapest Link
        function canAddEdge(edge) {
            const [v1, v2] = edge;

            // Count edges per vertex
            const vertexEdgeCount = {};
            for (const [e1, e2] of algorithmState.path) {
                vertexEdgeCount[e1] = (vertexEdgeCount[e1] || 0) + 1;
                vertexEdgeCount[e2] = (vertexEdgeCount[e2] || 0) + 1;
            }

            // Check if vertices already have 2 edges
            if ((vertexEdgeCount[v1] || 0) >= 2 || (vertexEdgeCount[v2] || 0) >= 2) {
                return false;
            }

            // Check if it creates a cycle before all vertices are added
            if (algorithmState.path.length >= 2) {
                const neighborMap = {};
                for (const [e1, e2] of algorithmState.path) {
                    neighborMap[e1] = neighborMap[e1] || [];
                    neighborMap[e2] = neighborMap[e2] || [];
                    neighborMap[e1].push(e2);
                    neighborMap[e2].push(e1);
                }

                neighborMap[v1] = neighborMap[v1] || [];
                neighborMap[v2] = neighborMap[v2] || [];
                neighborMap[v1].push(v2);
                neighborMap[v2].push(v1);

                // Check if adding this edge creates a cycle
                const visited = new Set();
                const stack = [v1];

                while (stack.length > 0) {
                    const current = stack.pop();
                    if (visited.has(current)) continue;
                    visited.add(current);

                    for (const neighbor of neighborMap[current]) {
                        if (neighbor === v1 && visited.size < 7) {
                            return false; // Cycle detected before all vertices
                        }
                        if (!visited.has(neighbor)) {
                            stack.push(neighbor);
                        }
                    }
                }
            }

            return true;
        }

        // Update algorithm display
        function updateAlgorithmDisplay() {
            // Update table
            const tbody = document.querySelector('#table-edge-list tbody');
            for (let i = algorithmState.path.length - (algorithmState.isComplete ? 1 : 0); i < algorithmState.path.length; i++) {
                const [from, to] = algorithmState.path[i];
                const row = tbody.insertRow();
                row.insertCell(0).textContent = algorithmState.path.length;
                row.insertCell(1).textContent = from;
                row.insertCell(2).textContent = to;
                row.insertCell(3).textContent = graph.edges[[from, to].sort().join('-')];
            }
        }

        // Update UI for algorithm change
        function updateUIForAlgorithmChange() {
            const selector = document.getElementById('starting-vertex-selector');
            if (currentAlgorithm === 'nearest-neighbor') {
                selector.style.display = 'block';
                document.getElementById('title-algo').textContent = 'The nearest neighbor algorithm';
            } else {
                selector.style.display = 'none';
                document.getElementById('title-algo').textContent = 'The cheapest link algorithm';
                document.getElementById('btn-next-step').disabled = false;
            }
        }

        // Update UI for new example
        function updateUIForNewExample() {
            document.getElementById('text-example-type-algo').textContent = isFixedExample ? 'fixed example' : 'random example';
            document.getElementById('text-example-type-optimal').textContent = isFixedExample ? 'fixed example' : 'random example';
            document.getElementById('text-optimal-weight').textContent = `Total weight: ${graph.optimalWeight}`;
        }

        // Create p5.js sketch
        function createP5Sketch(wrapperId, setCallback, isOptimal = false) {
            const sketch = (p) => {
                p.setup = () => {
                    const canvas = p.createCanvas(400, 400);
                    canvas.parent(wrapperId);
                    p.textAlign(p.CENTER, p.CENTER);
                    drawGraph(p, isOptimal);
                };

                p.draw = () => {
                    if (!isOptimal) {
                        drawGraph(p, isOptimal);
                    }
                };
            };

            new p5(sketch);
            setCallback ? setCallback(p) : null;
        }

        // Draw graph
        function drawGraph(p, isOptimal) {
            p.background(255);

            // Draw edges
            p.strokeWeight(1);
            p.stroke(200);
            for (const edge in graph.edges) {
                const [v1, v2] = edge.split('-');
                const pos1 = graph.vertices[v1];
                const pos2 = graph.vertices[v2];

                p.line(pos1.x, pos1.y, pos2.x, pos2.y);
            }

            // Draw algorithm path or optimal path
            const path = isOptimal ? graph.optimalPath : algorithmState.path.map(edge => [edge[0], edge[1]]);
            if (path.length > 0) {
                p.strokeWeight(3);
                p.stroke(isOptimal ? [0, 100, 200] : [255, 150, 0]);

                for (let i = 0; i < path.length - 1; i++) {
                    const from = path[i];
                    const to = path[i + 1];
                    const pos1 = graph.vertices[from];
                    const pos2 = graph.vertices[to];

                    p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                }
            }

            // Draw edge weights
            p.stroke(0);
            p.fill(255);
            p.textSize(12);
            for (const edge in graph.edges) {
                const [v1, v2] = edge.split('-');
                const pos1 = graph.vertices[v1];
                const pos2 = graph.vertices[v2];

                const midX = (pos1.x + pos2.x) / 2;
                const midY = (pos1.y + pos2.y) / 2;

                p.text(graph.edges[edge], midX, midY);
            }

            // Draw vertices
            p.strokeWeight(2);
            p.stroke(0);
            p.fill(255);
            p.textSize(16);

            for (const vertex in graph.vertices) {
                const pos = graph.vertices[vertex];
                p.ellipse(pos.x, pos.y, 30, 30);
                p.fill(0);
                p.text(vertex, pos.x, pos.y);
                p.fill(255);
            }
        }

        // Initialize on page load
        window.addEventListener('load', init);
    </script>
</body>
</html>
