    <script>
        // Global variables for storing calculated vectors and scales
        let vectors = { A: null, B: null, u: null, v: null, w: null };
        let scales = { x: 80, y: 80 };
        let canvas;

        // Get all sliders and value displays
        const sliders = {
            au: document.getElementById('slider-au'),
            bu: document.getElementById('slider-bu'),
            av: document.getElementById('slider-av'),
            bv: document.getElementById('slider-bv'),
            aw: document.getElementById('slider-aw'),
            bw: document.getElementById('slider-bw'),
            xScale: document.getElementById('slider-x-scale'),
            yScale: document.getElementById('slider-y-scale')
        };

        const valueDisplays = {
            au: document.getElementById('value-au'),
            bu: document.getElementById('value-bu'),
            av: document.getElementById('value-av'),
            bv: document.getElementById('value-bv'),
            aw: document.getElementById('value-aw'),
            bw: document.getElementById('value-bw'),
            xScale: document.getElementById('value-x-scale'),
            yScale: document.getElementById('value-y-scale')
        };

        // p5.js sketch
        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();

            // Attach event listeners to all sliders
            Object.keys(sliders).forEach(key => {
                sliders[key].addEventListener('input', handleSliderChange);
            });

            // Initial calculation and draw
            calculateAndDraw();
        }

        function draw() {
            background('#F0F0F0');

            // Set up coordinate system
            push();
            translate(width / 2, height / 2);
            scale(scales.x, -scales.y);

            // Draw camera axes
            drawAxis(vectors.u, '#0000CD', 'u');
            drawAxis(vectors.v, '#6495ED', 'v');
            drawAxis(vectors.w, '#0000CD', 'w');

            // Draw points
            drawPoint(vectors.A, '#FF0000', 'A');
            drawPoint(vectors.B, '#FFA500', 'B');
            drawPoint([0, 0], '#FF0000', 'O');

            // Draw projections
            drawProjections();

            pop();
        }

        function drawAxis(vector, color, label) {
            if (!vector) return;

            stroke(color);
            strokeWeight(3 / scales.x);

            // Draw line through origin
            const endX = vector[0] * 2;
            const endY = vector[1] * 2;
            line(-endX, -endY, endX, endY);

            // Draw label
            noStroke();
            fill(color);
            textAlign(CENTER, CENTER);
            textSize(0.3 / scales.x);
            text(label, vector[0] * 1.2, vector[1] * 1.2);
        }

        function drawPoint(point, color, label) {
            if (!point) return;

            fill(color);
            noStroke();
            circle(point[0], point[1], 0.1 / scales.x);

            // Draw label
            fill(color);
            textAlign(CENTER, CENTER);
            textSize(0.3 / scales.x);
            text(label, point[0] + 0.2 / scales.x, point[1] + 0.2 / scales.x);
        }

        function drawProjections() {
            if (!vectors.A || !vectors.B || !vectors.u || !vectors.v || !vectors.w) return;

            const points = [
                { point: vectors.A, label: 'A', color: '#FF0000' },
                { point: vectors.B, label: 'B', color: '#FFA500' }
            ];

            const axes = [
                { vector: vectors.u, name: 'u', value: parseFloat(sliders.au.value) },
                { vector: vectors.v, name: 'v', value: parseFloat(sliders.av.value) },
                { vector: vectors.w, name: 'w', value: parseFloat(sliders.aw.value) }
            ];

            points.forEach(p => {
                axes.forEach(a => {
                    // Calculate projection point
                    const projX = a.value * a.vector[0];
                    const projY = a.value * a.vector[1];

                    // Draw projection point
                    fill('#00FF00');
                    noStroke();
                    circle(projX, projY, 0.05 / scales.x);

                    // Draw line from point to projection
                    stroke('#808080');
                    strokeWeight(1 / scales.x);
                    line(p.point[0], p.point[1], projX, projY);
                });
            });
        }

        function handleSliderChange(event) {
            const sliderId = event.target.id;
            const value = event.target.value;

            // Update value display
            if (sliderId === 'slider-x-scale') {
                valueDisplays.xScale.textContent = value;
                scales.x = parseFloat(value);
            } else if (sliderId === 'slider-y-scale') {
                valueDisplays.yScale.textContent = value;
                scales.y = parseFloat(value);
            } else {
                const key = sliderId.replace('slider-', '');
                valueDisplays[key].textContent = value;
            }

            // Recalculate and redraw
            calculateAndDraw();
        }

        function calculateAndDraw() {
            try {
                // Build measurement matrix M
                const M = [
                    [parseFloat(sliders.au.value), parseFloat(sliders.bu.value)],
                    [parseFloat(sliders.av.value), parseFloat(sliders.bv.value)],
                    [parseFloat(sliders.aw.value), parseFloat(sliders.bw.value)]
                ];

                // Perform SVD
                const svdResult = math.svd(M);
                const U = svdResult.U;
                const D = math.diag(svdResult.S);
                const V = svdResult.V;

                // Construct K matrix for solving K * l_vec = [1,1,1]
                const K = [
                    [U[0][0] * U[0][0], U[0][0] * U[0][1], U[0][1] * U[0][1]],
                    [U[1][0] * U[1][0], U[1][0] * U[1][1], U[1][1] * U[1][1]],
                    [U[2][0] * U[2][0], U[2][0] * U[2][1], U[2][1] * U[2][1]]
                ];

                // Solve for l_vec
                const b = [1, 1, 1];
                const l_vec = math.lusolve(K, b).flat();

                // Construct L matrix
                const L = [
                    [l_vec[0], l_vec[1]],
                    [l_vec[1], l_vec[2]]
                ];

                // Check if L is positive definite
                const eigenvals = math.eigs(L).values;
                if (eigenvals.some(val => val <= 0)) {
                    throw new Error('L is not positive definite');
                }

                // Perform Cholesky decomposition
                const Q = math.cholesky(L);

                // Calculate S and P
                const S = math.multiply(U, Q);
                const Q_inv = math.inv(Q);
                const P = math.multiply(math.multiply(Q_inv, D), math.transpose(V));

                // Extract vectors
                vectors.u = [S[0][0], S[0][1]];
                vectors.v = [S[1][0], S[1][1]];
                vectors.w = [S[2][0], S[2][1]];
                vectors.A = [P[0][0], P[1][0]];
                vectors.B = [P[0][1], P[1][1]];

            } catch (error) {
                console.error('Calculation error:', error);
                // Clear vectors on error
                vectors = { A: null, B: null, u: null, v: null, w: null };
            }

            redraw();
        }
    </script>
</body>
</html>
