        </div>

        <div id="visualization-container"></div>
    </div>

    <script>
        // Global state
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0;
        let t2 = 0;

        // Three.js variables
        let scene, renderer, camera, controls;
        let geometry, material, mesh;

        // Initialize Three.js
        function init() {
            const container = document.getElementById('visualization-container');

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.shadowMap.enabled = true;
            container.appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 5);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);

            // Initialize visualization
            updateVisualization();
            animate();
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            if (controls) {
                controls.update();
            }
            renderer.render(scene, camera);
        }

        // Parametric functions
        const parametricFunctions = {
            '2A1': (u, v) => {
                const U = (u - 0.5) * 4; // Map [0,1] to [-2,2]
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U + T1, U * U + T2, 0);
            },

            '2A2': (u, v) => {
                const U = (u - 0.5) * 3; // Map [0,1] to [-1.5,1.5]
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(2 * Math.pow(U, 3) + T1, 3 * Math.pow(U, 2) + T2, 0);
            },

            '2B2': (u, v) => {
                const U = (u - 0.5) * 4; // Map [0,1] to [-2,2]
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U * U + T1, Math.pow(U, 4) + T2, 0);
            },

            '3A1p': (u, v) => {
                const U = (u - 0.5) * 4; // Map [0,1] to [-2,2]
                const V = (v - 0.5) * 4; // Map [0,1] to [-2,2]
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U, V, 0.3 * (U * U + V * V) + T1 * U + T2 * V);
            },

            '3A1m': (u, v) => {
                const U = (u - 0.5) * 4;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U, V, -0.3 * (U * U + V * V) + T1 * U + T2 * V);
            },

            '3A2': (u, v) => {
                const U = (u - 0.5) * 3;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(2 * Math.pow(U, 3) + T1, 3 * Math.pow(U, 2) + T2, V);
            },

            '3A3': (u, v) => {
                const U = (u - 0.5) * 3;
                const V = (v - 0.5) * 8;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(
                    3 * Math.pow(U, 4) + U * U * V,
                    -4 * Math.pow(U, 3) - 2 * U * V + T1 * 2,
                    V + T2 * 2
                );
            },

            '3B2p': (u, v) => {
                const U = (u - 0.5) * 4;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U, V, Math.pow(U * U + T1, 2) + V * V + T2);
            },

            '3B2m': (u, v) => {
                const U = (u - 0.5) * 4;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U, V, -(Math.pow(U, 4) + T1 * 2 * U * U + V * V - T2));
            },

            '3B3': (u, v) => {
                const U = (u - 0.5) * 4;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(U, V, U * U * U + T1 * U + V * V + T2);
            },

            '3C3p': (u, v) => {
                const U = (u - 0.5) * 4;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(
                    U, V,
                    U * U * U - 3 * U * V * V + T1 * 2 * (U * U + V * V) + T2 * 4 * U
                );
            },

            '3C3m': (u, v) => {
                const U = (u - 0.5) * 4;
                const V = (v - 0.5) * 4;
                const T1 = t1 * 1.5;
                const T2 = t2 * 1.5;
                return new THREE.Vector3(
                    U, V,
                    U * U * U + V * V * V + T1 * 4 * U + T2 * 4 * V
                );
            }
        };

        function updateVisualization() {
            // Clear existing objects
            if (mesh) {
                scene.remove(mesh);
                if (mesh.parent) {
                    mesh.parent.remove(mesh);
                }
            }

            // Remove camera and controls
            if (camera) {
                scene.remove(camera);
            }
            if (controls) {
                controls.dispose();
                controls = null;
            }

            const container = document.getElementById('visualization-container');
            const aspect = container.clientWidth / container.clientHeight;

            if (currentDimension === '2D') {
                // Setup 2D camera
                camera = new THREE.OrthographicCamera(
                    -5 * aspect, 5 * aspect,
                    5, -5,
                    0.1, 1000
                );
                camera.position.z = 10;

                // Create 2D line
                const points = [];
                const func = parametricFunctions[currentType];
                for (let i = 0; i <= 200; i++) {
                    const u = i / 200;
                    points.push(func(u, 0));
                }

                const lineGeometry = new THREE.BufferGeometry().setFromPoints(points);
                const lineMaterial = new THREE.LineBasicMaterial({ 
                    color: 0x4a69bd,
                    linewidth: 2
                });
                mesh = new THREE.Line(lineGeometry, lineMaterial);
                scene.add(mesh);
            } else {
                // Setup 3D camera
                camera = new THREE.PerspectiveCamera(
                    75,
                    aspect,
                    0.1,
                    1000
                );
                camera.position.set(5, 5, 5);
                camera.lookAt(0, 0, 0);

                // Add orbit controls
                controls = new THREE.OrbitControls(camera, renderer.domElement);
                controls.enableDamping = true;
                controls.dampingFactor = 0.05;

                // Create 3D surface
                const func = parametricFunctions[currentType];
                const surfaceGeometry = new THREE.ParametricGeometry(func, 50, 50);

                // Create surface mesh
                const surfaceMaterial = new THREE.MeshPhongMaterial({
                    color: 0xffc34d,
                    side: THREE.DoubleSide,
                    shininess: 100
                });
                mesh = new THREE.Mesh(surfaceGeometry, surfaceMaterial);

                // Add wireframe for certain types
                if (currentType === '3A3' || currentType === '3C3p' || currentType === '3C3m') {
                    const wireframeGeometry = new THREE.ParametricGeometry(func, 25, 25);
                    const wireframeMaterial = new THREE.LineBasicMaterial({
                        color: 0x000000,
                        linewidth: 1
                    });
                    const wireframe = new THREE.LineSegments(
                        new THREE.WireframeGeometry(wireframeGeometry),
                        wireframeMaterial
                    );
                    mesh.add(wireframe);
                }

                scene.add(mesh);
            }

            scene.add(camera);
        }

        // Button event handlers
        function setupButtonHandlers() {
            const buttons = document.querySelectorAll('button');

            buttons.forEach(button => {
                button.addEventListener('click', function() {
                    // Remove active class from all buttons
                    buttons.forEach(btn => btn.classList.remove('active'));

                    // Add active class to clicked button
                    this.classList.add('active');

                    // Update current type and dimension
                    currentType = this.id.replace('btn-', '');
                    currentDimension = currentType.startsWith('3') ? '3D' : '2D';

                    // Update visualization
                    updateVisualization();
                });
            });

            // Set initial active button
            document.getElementById('btn-2A2').classList.add('active');
        }

        // Time control handlers
        function setupTimeControl() {
            const timePad = document.getElementById('time-pad');
            const timeHandle = document.getElementById('time-handle');
            let isDragging = false;

            function updateHandlePosition(clientX, clientY) {
                const rect = timePad.getBoundingClientRect();
                const x = clientX - rect.left;
                const y = clientY - rect.top;

                // Constrain to pad bounds
                const constrainedX = Math.max(10, Math.min(rect.width - 10, x));
                const constrainedY = Math.max(10, Math.min(rect.height - 10, y));

                // Update handle position
                timeHandle.style.left = constrainedX + 'px';
                timeHandle.style.top = constrainedY + 'px';

                // Update t1 and t2 values
                t1 = ((constrainedX / rect.width) - 0.5) * 2; // [-1, 1]
                t2 = (0.5 - (constrainedY / rect.height)) * 2; // [-1, 1]

                // Update visualization
                updateVisualization();
            }

            timeHandle.addEventListener('mousedown', (e) => {
                isDragging = true;
                e.preventDefault();
            });

            document.addEventListener('mousemove', (e) => {
                if (isDragging) {
                    updateHandlePosition(e.clientX, e.clientY);
                }
            });

            document.addEventListener('mouseup', () => {
                isDragging = false;
            });

            timePad.addEventListener('click', (e) => {
                if (e.target !== timeHandle) {
                    updateHandlePosition(e.clientX, e.clientY);
                }
            });
        }

        // Initialize everything
        window.addEventListener('load', () => {
            init();
            setupButtonHandlers();
            setupTimeControl();
        });
