        </div>
    </div>

    <script>
        // Global variables
        let plotType = 'semibatch';
        let feedConfig = 'feed A to B';
        let feedRate = 10;
        let UA = 15000;
        let DH1 = 1000;
        let DH2 = 3000;

        // Physical constants
        const V = 100; // L
        const T_initial = 330; // K
        const T_coolant = 330; // K
        const T_feed = 330; // K
        const R = 8.314; // J/mol/K
        const Cp_total = 150000; // J/K
        const k1_0 = 3e4; // L²/(mol²·h)
        const E1 = 30000; // J/mol
        const k2_0 = 1e5; // L/(mol·h)
        const E2 = 40000; // J/mol

        // Simulation parameters
        const t_max = 2.0; // hours
        const dt = 0.01; // hours

        // Data storage
        let simulationData = {
            time: [],
            Na: [],
            Nb: [],
            Nd: [],
            Nu: [],
            T: [],
            S_ratio: []
        };

        // Initialize controls
        function initializeControls() {
            // Plot type selector
            document.getElementById('select-plot').addEventListener('change', (e) => {
                plotType = e.target.value;
                runSimulation();
            });

            // Feed cycler
            const feedOptions = ['feed A to B', 'feed B to A'];
            let feedIndex = 0;

            document.getElementById('btn-feed-up').addEventListener('click', () => {
                feedIndex = (feedIndex + 1) % feedOptions.length;
                feedConfig = feedOptions[feedIndex];
                document.getElementById('cycler-feed-label').textContent = feedConfig;
                runSimulation();
            });

            document.getElementById('btn-feed-down').addEventListener('click', () => {
                feedIndex = (feedIndex - 1 + feedOptions.length) % feedOptions.length;
                feedConfig = feedOptions[feedIndex];
                document.getElementById('cycler-feed-label').textContent = feedConfig;
                runSimulation();
            });

            // Slider synchronization
            const sliderPairs = [
                ['slider-feed-rate', 'input-feed-rate', 'feedRate'],
                ['slider-ua', 'input-ua', 'UA'],
                ['slider-dh1', 'input-dh1', 'DH1'],
                ['slider-dh2', 'input-dh2', 'DH2']
            ];

            sliderPairs.forEach(([sliderId, inputId, varName]) => {
                const slider = document.getElementById(sliderId);
                const input = document.getElementById(inputId);

                slider.addEventListener('input', (e) => {
                    const value = parseFloat(e.target.value);
                    input.value = value;
                    window[varName] = value;
                    runSimulation();
                });

                input.addEventListener('change', (e) => {
                    const value = parseFloat(e.target.value);
                    slider.value = value;
                    window[varName] = value;
                    runSimulation();
                });
            });
        }

        // ODE system
        function odeSystem(Y, t) {
            const [Na, Nb, Nd, Nu, T] = Y;

            // Rate constants
            const k1 = k1_0 * Math.exp(-E1 / (R * T));
            const k2 = k2_0 * Math.exp(-E2 / (R * T));

            // Reaction rates
            const r1 = k1 * (Na/V) * Math.pow(Nb/V, 2);
            const r2 = k2 * (Na/V) * (Nb/V);

            // Energy balance
            const dTdt = (UA * (T_coolant - T) + (-DH1 * r1 - DH2 * r2) * V) / Cp_total;

            return [r1, r2, dTdt];
        }

        // RK4 solver
        function rk4(Y, t, dt) {
            const k1 = odeSystem(Y, t);
            const k2 = odeSystem(Y.map((y, i) => y + k1[i] * dt/2), t + dt/2);
            const k3 = odeSystem(Y.map((y, i) => y + k2[i] * dt/2), t + dt/2);
            const k4 = odeSystem(Y.map((y, i) => y + k3[i] * dt), t + dt);

            return Y.map((y, i) => y + dt/6 * (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
        }

        // Run simulation
        function runSimulation() {
            // Clear previous data
            simulationData = {
                time: [],
                Na: [],
                Nb: [],
                Nd: [],
                Nu: [],
                T: [],
                S_ratio: []
            };

            if (plotType === 'selectivity') {
                // Run batch simulation
                const batchData = runBatchSimulation();
                // Run semibatch simulation
                const semiData = runSemibatchSimulation();

                // Calculate selectivity ratio
                for (let i = 1; i < batchData.time.length; i++) {
                    if (batchData.Nu[i] > 0 && semiData.Nu[i] > 0) {
                        const S_batch = batchData.Nd[i] / batchData.Nu[i];
                        const S_semi = semiData.Nd[i] / semiData.Nu[i];
                        simulationData.time.push(batchData.time[i]);
                        simulationData.S_ratio.push(S_semi / S_batch);
                    }
                }
            } else if (plotType === 'batch') {
                const data = runBatchSimulation();
                simulationData = data;
            } else if (plotType === 'semibatch') {
                const data = runSemibatchSimulation();
                simulationData = data;
            }

            // Redraw plot
            if (window.p5Instance) {
                window.p5Instance.redraw();
            }

            // Update legend
            updateLegend();
        }

        function runBatchSimulation() {
            const data = {
                time: [],
                Na: [],
                Nb: [],
                Nd: [],
                Nu: [],
                T: []
            };

            let Y = [100, 100, 0, 0, T_initial];
            let t = 0;

            while (t <= t_max) {
                data.time.push(t);
                data.Na.push(Y[0]);
                data.Nb.push(Y[1]);
                data.Nd.push(Y[2]);
                data.Nu.push(Y[3]);
                data.T.push(Y[4]);

                const [r1, r2, dTdt] = odeSystem(Y, t);

                // Update state
                Y[0] += -(r1 + r2) * V * dt; // dNa/dt
                Y[1] += -(r1 + r2) * V * dt; // dNb/dt
                Y[2] += r1 * V * dt; // dNd/dt
                Y[3] += r2 * V * dt; // dNu/dt
                Y[4] += dTdt * dt; // dT/dt

                t += dt;
            }

            return data;
        }

        function runSemibatchSimulation() {
            const data = {
                time: [],
                Na: [],
                Nb: [],
                Nd: [],
                Nu: [],
                T: []
            };

            const t_f = 100 / feedRate;
            let Y;

            if (feedConfig === 'feed A to B') {
                Y = [0, 100, 0, 0, T_initial];
            } else {
                Y = [100, 0, 0, 0, T_initial];
            }

            let t = 0;

            while (t <= t_max) {
                data.time.push(t);
                data.Na.push(Y[0]);
                data.Nb.push(Y[1]);
                data.Nd.push(Y[2]);
                data.Nu.push(Y[3]);
                data.T.push(Y[4]);

                const [r1, r2, dTdt] = odeSystem(Y, t);

                // Feed terms
                let FA0 = 0, FB0 = 0;
                if (t < t_f) {
                    if (feedConfig === 'feed A to B') {
                        FA0 = feedRate;
                    } else {
                        FB0 = feedRate;
                    }
                }

                // Update state
                Y[0] += (FA0 - (r1 + r2) * V) * dt; // dNa/dt
                Y[1] += (FB0 - (r1 + r2) * V) * dt; // dNb/dt
                Y[2] += r1 * V * dt; // dNd/dt
                Y[3] += r2 * V * dt; // dNu/dt
                Y[4] += dTdt * dt; // dT/dt

                t += dt;
            }

            return data;
        }

        // Update legend
        function updateLegend() {
            const legendContainer = document.getElementById('legend-container');
            legendContainer.innerHTML = '';

            if (plotType === 'selectivity') {
                const item = document.createElement('div');
                item.className = 'legend-item';
                item.innerHTML = `
                    <div class="legend-line" style="background-color: #800080;"></div>
                    <span>semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)</span>
                `;
                legendContainer.appendChild(item);
            } else {
                const curves = [
                    { label: '\\(N_a\\)', color: '#2ca02c', dashed: true },
                    { label: '\\(N_b\\)', color: '#000000', dashed: true },
                    { label: '\\(N_d\\)', color: '#1f77b4', dashed: false },
                    { label: '\\(N_u\\)', color: '#d62728', dashed: false }
                ];

                curves.forEach(curve => {
                    const item = document.createElement('div');
                    item.className = 'legend-item';
                    const lineStyle = curve.dashed ? 'background: repeating-linear-gradient(90deg, ' + curve.color + ', ' + curve.color + ' 5px, transparent 5px, transparent 10px);' : 'background-color: ' + curve.color + ';';
                    item.innerHTML = `
                        <div class="legend-line" style="${lineStyle} height: 2px;"></div>
                        <span>${curve.label}</span>
                    `;
                    legendContainer.appendChild(item);
                });
            }

            // Re-render MathJax
            if (window.MathJax) {
                MathJax.typesetPromise([legendContainer]).catch((e) => console.log(e));
            }
        }

        // p5.js sketch
        const sketch = (p) => {
            let canvas;

            p.setup = () => {
                canvas = p.createCanvas(p.windowWidth - 80, 500);
                canvas.parent('plot-canvas');
                p.noLoop();
            };

            p.draw = () => {
                p.background(240);

                // Plot margins
                const margin = { top: 60, right: 40, bottom: 60, left: 60 };
                const plotWidth = p.width - margin.left - margin.right;
                const plotHeight = p.height - margin.top - margin.bottom;

                // Draw grid
                p.stroke(200);
                p.strokeWeight(1);

                // Vertical grid lines
                for (let i = 0; i <= 4; i++) {
                    const x = margin.left + (i * plotWidth / 4);
                    p.line(x, margin.top, x, margin.top + plotHeight);
                }

                // Horizontal grid lines
                const yTicks = plotType === 'selectivity' ? 5 : 6;
                for (let i = 0; i <= yTicks; i++) {
                    const y = margin.top + (i * plotHeight / yTicks);
                    p.line(margin.left, y, margin.left + plotWidth, y);
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(margin.left, margin.top + plotHeight, margin.left + plotWidth, margin.top + plotHeight); // X-axis
                p.line(margin.left, margin.top, margin.left, margin.top + plotHeight); // Y-axis

                // Draw axis labels
                p.noStroke();
                p.fill(0);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(14);
                p.text('time (h)', p.width / 2, p.height - 20);

                p.push();
                p.translate(20, p.height / 2);
                p.rotate(-p.PI / 2);
                if (plotType === 'selectivity') {
                    p.text('selectivity ratio', 0, 0);
                } else {
                    p.text('moles (mol)', 0, 0);
                }
                p.pop();

                // Draw tick labels
                p.textSize(12);
                p.textAlign(p.CENTER, p.TOP);

                // X-axis ticks
                for (let i = 0; i <= 4; i++) {
                    const x = margin.left + (i * plotWidth / 4);
                    const value = (i * t_max / 4).toFixed(1);
                    p.text(value, x, margin.top + plotHeight + 5);
                }

                // Y-axis ticks
                p.textAlign(p.RIGHT, p.CENTER);
                let yMin, yMax;

                if (plotType === 'selectivity') {
                    if (simulationData.S_ratio.length > 0) {
                        yMin = Math.min(...simulationData.S_ratio);
                        yMax = Math.max(...simulationData.S_ratio);
                        const padding = (yMax - yMin) * 0.1;
                        yMin -= padding;
                        yMax += padding;
                    } else {
                        yMin = 0;
                        yMax = 1;
                    }
                } else {
                    yMin = 0;
                    yMax = 100;
                }

                for (let i = 0; i <= yTicks; i++) {
                    const y = margin.top + plotHeight - (i * plotHeight / yTicks);
                    const value = (yMin + (i * (yMax - yMin) / yTicks)).toFixed(plotType === 'selectivity' ? 2 : 0);
                    p.text(value, margin.left - 5, y);
                }

                // Draw data curves
                if (simulationData.time.length > 0) {
                    const xScale = (t) => margin.left + (t / t_max) * plotWidth;
                    const yScale = (val) => margin.top + plotHeight - ((val - yMin) / (yMax - yMin)) * plotHeight;

                    if (plotType === 'selectivity') {
                        // Draw S_ratio curve
                        p.stroke('#800080');
                        p.strokeWeight(2);
                        p.noFill();
                        p.beginShape();
                        for (let i = 0; i < simulationData.time.length; i++) {
                            p.vertex(xScale(simulationData.time[i]), yScale(simulationData.S_ratio[i]));
                        }
                        p.endShape();
                    } else {
                        // Draw mole curves
                        const curves = [
                            { data: simulationData.Na, color: '#2ca02c', dashed: true },
                            { data: simulationData.Nb, color: '#000000', dashed: true },
                            { data: simulationData.Nd, color: '#1f77b4', dashed: false },
                            { data: simulationData.Nu, color: '#d62728', dashed: false }
                        ];

                        curves.forEach(curve => {
                            p.stroke(curve.color);
                            p.strokeWeight(2);
                            p.noFill();

                            if (curve.dashed) {
                                // Draw dashed line
                                for (let i = 0; i < curve.data.length - 1; i++) {
                                    const x1 = xScale(simulationData.time[i]);
                                    const y1 = yScale(curve.data[i]);
                                    const x2 = xScale(simulationData.time[i + 1]);
                                    const y2 = yScale(curve.data[i + 1]);

                                    // Draw segments with gaps
                                    const segments = 5;
                                    for (let j = 0; j < segments; j += 2) {
                                        const t1 = j / segments;
                                        const t2 = Math.min((j + 1) / segments, 1);
                                        p.line(
                                            x1 + (x2 - x1) * t1,
                                            y1 + (y2 - y1) * t1,
                                            x1 + (x2 - x1) * t2,
                                            y1 + (y2 - y1) * t2
                                        );
                                    }
                                }
                            } else {
                                // Draw solid line
                                p.beginShape();
                                for (let i = 0; i < curve.data.length; i++) {
                                    p.vertex(xScale(simulationData.time[i]), yScale(curve.data[i]));
                                }
                                p.endShape();
                            }
                        });
                    }
                }
            };

            p.windowResized = () => {
                p.resizeCanvas(p.windowWidth - 80, 500);
            };
        };

        // Initialize everything
        window.addEventListener('load', () => {
            initializeControls();
            window.p5Instance = new p5(sketch);
            runSimulation();
        });
    </script>
</body>
