<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <h4>Tree type</h4>
                <div id="tree-type-buttons">
                    <button id="btn-rt" class="active">Random Tree</button>
                    <button id="btn-rrt">RRT</button>
                    <button id="btn-rrt-star">RRT*</button>
                </div>
            </div>

            <div>
                <h4>obstacle type</h4>
                <select id="select-obstacle">
                    <option value="narrow-passage" selected>narrow passage</option>
                    <option value="empty">empty</option>
                    <option value="random-rects">random rects</option>
                </select>
            </div>

            <div>
                <h4>number of nodes to add:</h4>
                <div id="add-nodes-buttons">
                    <button id="btn-add-1">1</button>
                    <button id="btn-add-10">10</button>
                    <button id="btn-add-100">100</button>
                    <button id="btn-add-200">200</button>
                    <button id="btn-add-500">500</button>
                </div>
            </div>

            <div>
                <h4>exploration bias</h4>
                <div class="slider-container">
                    <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
                    <div class="slider-label">
                        <span>0</span>
                        <span id="bias-value-label">0</span>
                        <span>1</span>
                    </div>
                </div>
            </div>

            <div>
                <h4>goal radius</h4>
                <div class="slider-container">
                    <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
                    <div class="slider-label">
                        <span>1</span>
                        <span id="radius-value-label">1</span>
                        <span>10</span>
                    </div>
                </div>
            </div>
        </div>

        <div id="canvas-container">
            <p id="status-text">1 node, goal not yet reached</p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script>
        // Global variables
        let treeType = 'rt';
        let obstacleType = 'narrow-passage';
        let nodes = [];
        let edges = [];
        let pathNodes = [];
        let pathLength = 0;
        let goalReached = false;
        let explorationBias = 0;
        let goalRadius = 1;
        let startNode = { x: 50, y: 250 };
        let goalNode = { x: 450, y: 450 };
        let draggingGoal = false;
        let canvas;

        // Obstacle configurations
        const obstacles = {
            'narrow-passage': [
                { x: 0, y: 0, w: 10, h: 500 },
                { x: 490, y: 0, w: 10, h: 500 },
                { x: 0, y: 0, w: 500, h: 10 },
                { x: 0, y: 490, w: 500, h: 10 },
                { x: 150, y: 0, w: 20, h: 200 },
                { x: 330, y: 300, w: 20, h: 200 }
            ],
            'empty': [
                { x: 0, y: 0, w: 500, h: 500 }
            ],
            'random-rects': [
                { x: 0, y: 0, w: 500, h: 500 },
                { x: 100, y: 100, w: 80, h: 150 },
                { x: 300, y: 200, w: 100, h: 80 },
                { x: 200, y: 350, w: 150, h: 60 }
            ]
        };

        // P5.js sketch
        function setup() {
            canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');

            // Initialize with start node
            nodes.push({
                x: startNode.x,
                y: startNode.y,
                parent: null,
                cost: 0
            });

            updateStatusText();
            draw();
        }

        function draw() {
            background(255);

            // Draw obstacles
            stroke('blue');
            strokeWeight(8);
            noFill();
            const currentObstacles = obstacles[obstacleType];
            for (let obs of currentObstacles) {
                rect(obs.x, obs.y, obs.w, obs.h);
            }

            // Draw edges for RRT*
            if (treeType === 'rrt-star') {
                stroke(0);
                strokeWeight(0.5);
                for (let edge of edges) {
                    line(edge.from.x, edge.from.y, edge.to.x, edge.to.y);
                }
            }

            // Draw path if goal reached
            if (goalReached && pathNodes.length > 0) {
                stroke('green');
                strokeWeight(3);
                noFill();
                beginShape();
                for (let node of pathNodes) {
                    vertex(node.x, node.y);
                }
                endShape();
            }

            // Draw nodes
            noStroke();
            for (let node of nodes) {
                fill('red');
                circle(node.x, node.y, 4);
            }

            // Draw start node (green if simulation started)
            fill(nodes.length > 1 ? 'green' : 'red');
            circle(startNode.x, startNode.y, 8);

            // Draw goal
            fill(255, 255, 0, 100);
            noStroke();
            circle(goalNode.x, goalNode.y, goalRadius * 20);

            // Draw goal crosshair
            stroke(0);
            strokeWeight(2);
            const crossSize = 10;
            line(goalNode.x - crossSize, goalNode.y, goalNode.x + crossSize, goalNode.y);
            line(goalNode.x, goalNode.y - crossSize, goalNode.x, goalNode.y + crossSize);
        }

        // Mouse interaction for goal dragging
        function mousePressed() {
            if (mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                const distToGoal = dist(mouseX, mouseY, goalNode.x, goalNode.y);
                if (distToGoal < goalRadius * 10) {
                    draggingGoal = true;
                }
            }
        }

        function mouseDragged() {
            if (draggingGoal) {
                goalNode.x = constrain(mouseX, 20, width - 20);
                goalNode.y = constrain(mouseY, 20, height - 20);
                draw();
            }
        }

        function mouseReleased() {
            if (draggingGoal) {
                draggingGoal = false;
                resetSimulation();
            }
        }

        // Utility functions
        function updateStatusText() {
            const status = document.getElementById('status-text');
            if (goalReached) {
                status.textContent = `${nodes.length} nodes, path length ${pathLength.toFixed(2)}`;
            } else {
                status.textContent = `${nodes.length} nodes, goal not yet reached`;
            }
        }

        function resetSimulation() {
            nodes = [{
                x: startNode.x,
                y: startNode.y,
                parent: null,
                cost: 0
            }];
            edges = [];
            pathNodes = [];
            pathLength = 0;
            goalReached = false;
            updateStatusText();
            draw();
        }

        function getRandomPoint() {
            if (Math.random() < explorationBias) {
                return { x: goalNode.x, y: goalNode.y };
            }
            return {
                x: Math.random() * width,
                y: Math.random() * height
            };
        }

        function getNearestNode(point) {
            let minDist = Infinity;
            let nearest = null;

            for (let node of nodes) {
                const d = dist(node.x, node.y, point.x, point.y);
                if (d < minDist) {
                    minDist = d;
                    nearest = node;
                }
            }

            return nearest;
        }

        function isCollisionFree(x1, y1, x2, y2) {
            const currentObstacles = obstacles[obstacleType];

            for (let obs of currentObstacles) {
                if (lineRectCollision(x1, y1, x2, y2, obs.x, obs.y, obs.w, obs.h)) {
                    return false;
                }
            }

            return true;
        }

        function lineRectCollision(x1, y1, x2, y2, rx, ry, rw, rh) {
            const left = lineLineCollision(x1, y1, x2, y2, rx, ry, rx, ry + rh);
            const right = lineLineCollision(x1, y1, x2, y2, rx + rw, ry, rx + rw, ry + rh);
            const top = lineLineCollision(x1, y1, x2, y2, rx, ry, rx + rw, ry);
            const bottom = lineLineCollision(x1, y1, x2, y2, rx, ry + rh, rx + rw, ry + rh);

            if (left || right || top || bottom) {
                return true;
            }

            // Check if line is completely inside rectangle
            if (x1 > rx && x1 < rx + rw && y1 > ry && y1 < ry + rh &&
                x2 > rx && x2 < rx + rw && y2 > ry && y2 < ry + rh) {
                return true;
            }

            return false;
        }

        function lineLineCollision(x1, y1, x2, y2, x3, y3, x4, y4) {
            const denom = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
            if (Math.abs(denom) < 0.001) return false;

            const t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / denom;
            const u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / denom;

            return t >= 0 && t <= 1 && u >= 0 && u <= 1;
        }

        function getSteeredNode(from, to, stepSize = 10) {
            const d = dist(from.x, from.y, to.x, to.y);
            if (d <= stepSize) {
                return { x: to.x, y: to.y, valid: true };
            }

            const angle = atan2(to.y - from.y, to.x - from.x);
            const newX = from.x + cos(angle) * stepSize;
            const newY = from.y + sin(angle) * stepSize;

            return { x: newX, y: newY, valid: true };
        }

        // Tree algorithms
        function addRandomTreeNode() {
            const point = getRandomPoint();
            const newNode = { x: point.x, y: point.y, parent: null };

            if (!isCollisionFree(newNode.x, newNode.y, newNode.x, newNode.y)) {
                return null;
            }

            nodes.push(newNode);
            return newNode;
        }

        function addRRTNode() {
            const point = getRandomPoint();
            const nearest = getNearestNode(point);

            if (!nearest) return null;

            const steered = getSteeredNode(nearest, point);

            if (!isCollisionFree(nearest.x, nearest.y, steered.x, steered.y)) {
                return null;
            }

            const newNode = {
                x: steered.x,
                y: steered.y,
                parent: nearest
            };

            nodes.push(newNode);
            return newNode;
        }

        function addRRTStarNode() {
            const point = getRandomPoint();
            const nearest = getNearestNode(point);

            if (!nearest) return null;

            const steered = getSteeredNode(nearest, point);

            if (!isCollisionFree(nearest.x, nearest.y, steered.x, steered.y)) {
                return null;
            }

            // Find neighbors
            const neighbors = [];
            const neighborRadius = 50;

            for (let node of nodes) {
                if (node === nearest) continue;
                const d = dist(node.x, node.y, steered.x, steered.y);
                if (d < neighborRadius && isCollisionFree(node.x, node.y, steered.x, steered.y)) {
                    neighbors.push(node);
                }
            }

            // Choose parent with minimum cost
            let minCost = nearest.cost + dist(nearest.x, nearest.y, steered.x, steered.y);
            let bestParent = nearest;

            for (let n of neighbors) {
                const cost = n.cost + dist(n.x, n.y, steered.x, steered.y);
                if (cost < minCost) {
                    minCost = cost;
                    bestParent = n;
                }
            }

            const newNode = {
                x: steered.x,
                y: steered.y,
                parent: bestParent,
                cost: minCost
            };

            // Rewire neighbors
            for (let n of neighbors) {
                const cost = minCost + dist(steered.x, steered.y, n.x, n.y);
                if (cost < n.cost && isCollisionFree(steered.x, steered.y, n.x, n.y)) {
                    n.parent = newNode;
                    n.cost = cost;
                }
            }

            nodes.push(newNode);
            edges.push({ from: bestParent, to: newNode });

            return newNode;
        }

        function addNode() {
            let newNode;

            switch (treeType) {
                case 'rt':
                    newNode = addRandomTreeNode();
                    break;
                case 'rrt':
                    newNode = addRRTNode();
                    break;
                case 'rrt-star':
                    newNode = addRRTStarNode();
                    break;
            }

            if (!newNode) return;

            // Check if goal reached
            const distToGoal = dist(newNode.x, newNode.y, goalNode.x, goalNode.y);
            if (distToGoal <= goalRadius * 10 && !goalReached) {
