    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let pathObject;

        // Initialize the scene
        function initScene() {
            // Create scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Create camera
            camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(4, 4, 4);
            camera.lookAt(0, 1, 0);

            // Create renderer
            const canvas = document.getElementById('three-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            // Create controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Add lighting
            const ambientLight = new THREE.AmbientLight(0x666666);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Add axes helper
            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            // Create potential surface
            createPotentialSurface();

            // Create central cylinder
            createCentralCylinder();

            // Handle window resize
            window.addEventListener('resize', onWindowResize, false);
        }

        function createPotentialSurface() {
            const parametricFunction = function(u, v, target) {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;
                const x = rho * Math.cos(theta);
                const y_surf = rho * rho + 1 / (rho * rho);
                const z_surf = -rho * Math.sin(theta);
                target.set(x, y_surf, z_surf);
            };

            const geometry = new THREE.ParametricGeometry(parametricFunction, 50, 50);
            const material = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });

            const surface = new THREE.Mesh(geometry, material);
            scene.add(surface);
        }

        function createCentralCylinder() {
            const geometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const material = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });

            const cylinder = new THREE.Mesh(geometry, material);
            cylinder.position.set(0, 1.25, 0);
            scene.add(cylinder);
        }

        function onWindowResize() {
            const canvas = document.getElementById('three-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        // RK4 integration
        function rk4(f, y0, s0, t_max, ds) {
            const points = [];
            let y = [...y0];
            let s = s0;

            while (s <= t_max) {
                points.push([...y]);

                const k1 = f(s, y);
                const k2 = f(s + ds/2, y.map((val, i) => val + k1[i] * ds/2));
                const k3 = f(s + ds/2, y.map((val, i) => val + k2[i] * ds/2));
                const k4 = f(s + ds, y.map((val, i) => val + k3[i] * ds));

                y = y.map((val, i) => val + (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]) * ds/6);
                s += ds;
            }

            return points;
        }

        // ODE system
        function odeSystem(s, Y) {
            const rho = Y[0];
            const drho_ds = Y[1];
            const phi = Y[2];

            // Parameters
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            // Derivatives
            const d2rho_ds2 = -(2 * rho - 2 / Math.pow(rho, 3)) - 4 * Math.pow(lambda, 2) * rho + 4 * e0 / Math.pow(rho, 3);
            const dphi_ds = 2 * (e0 / Math.pow(rho, 2) - lambda);

            return [drho_ds, d2rho_ds2, dphi_ds];
        }

        // Update visualization
        function updateVisualization() {
            // Get parameter values
            const t = parseFloat(document.getElementById('input-t').value);
            const rho0 = parseFloat(document.getElementById('input-rho0').value);
            const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const nu = parseFloat(document.getElementById('input-v').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            // Initial conditions
            const Y0 = [rho0, 0, 0];

            // Integrate
            const ds = 0.05;
            const solution = rk4(odeSystem, Y0, 0, t, ds);

            // Convert to 3D points
            const points = [];
            solution.forEach(Y => {
                const rho = Y[0];
                const phi = Y[2];
                const s = solution.indexOf(Y) * ds;

                const u_rho = Math.pow(rho, 2) + 1 / Math.pow(rho, 2);
                const sqrt_arg = e0 - u_rho;
                const z_calc = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * nu * s + zeta0);

                const x = rho * Math.cos(phi);
                const y = rho * Math.sin(phi);

                // Map to three.js coordinates
                points.push(new THREE.Vector3(x, z_calc, -y));
            });

            // Remove old path
            if (pathObject) {
                scene.remove(pathObject);
                if (pathObject.geometry) pathObject.geometry.dispose();
                if (pathObject.material) pathObject.material.dispose();
            }

            // Create new path
            if (points.length > 1) {
                const curve = new THREE.CatmullRomCurve3(points);
                const geometry = new THREE.TubeGeometry(curve, points.length * 4, 0.04, 8, false);
                const material = new THREE.MeshBasicMaterial({ color: 0x61AEEF });

                pathObject = new THREE.Mesh(geometry, material);
                pathObject.name = 'ion-path';
                scene.add(pathObject);
            }
        }

        // Link sliders and inputs
        function setupControls() {
            const controls = [
                { slider: 'slider-t', input: 'input-t' },
                { slider: 'slider-rho0', input: 'input-rho0' },
                { slider: 'slider-zeta0', input: 'input-zeta0' },
                { slider: 'slider-e0', input: 'input-e0' },
                { slider: 'slider-v', input: 'input-v' },
                { slider: 'slider-lambda', input: 'input-lambda' }
            ];

            controls.forEach(control => {
                const slider = document.getElementById(control.slider);
                const input = document.getElementById(control.input);

                slider.addEventListener('input', function() {
                    input.value = this.value;
                    updateVisualization();
                });

                input.addEventListener('input', function() {
                    slider.value = this.value;
                    updateVisualization();
                });
            });
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize everything
        window.addEventListener('load', function() {
            initScene();
            setupControls();
            updateVisualization();
            animate();
        });
    </script>
</body>
</html>
