<body>
    <div id="app-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <label for="slider-r">R (Å)</label>
            <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
            <output id="slider-r-value">0.001output>
        </div>

        <!-- Potential Energy Plot Section -->
        <div id="potential-energy-plot-container" class="plot-section"></div>

        <!-- Electron Density Plots Section -->
        <div id="density-plots-container" class="plot-section">
            <div id="bonding-plot-container" class="density-plot"></div>
            <div id="antibonding-plot-container" class="density-plot"></div>
        </div>
    </div>

    <script>
        // Constants
        const ANGSTROM_TO_BOHR = 0.529177;

        // Get DOM elements
        const sliderR = document.getElementById('slider-r');
        const sliderRValue = document.getElementById('slider-r-value');
        const bondingPlotContainer = document.getElementById('bonding-plot-container');
        const antibondingPlotContainer = document.getElementById('antibonding-plot-container');
        const potentialEnergyPlotContainer = document.getElementById('potential-energy-plot-container');

        // Calculate potential energy curves
        function calculatePotentialEnergyCurves() {
            const R_values_au = [];
            const E_b_values = [];
            const E_a_values = [];
            const R_min = 0.2;
            const R_max = 15;
            const numPoints = 200;

            for (let i = 0; i < numPoints; i++) {
                const R = R_min + (R_max - R_min) * i / (numPoints - 1);
                R_values_au.push(R);

                // Calculate integrals
                const S_ab = (1 + R + R * R / 3) * Math.exp(-R);
                const J = (1 / R) - (1 + 1 / R) * Math.exp(-2 * R);
                const K = (1 + R) * Math.exp(-R);

                // Calculate energies
                const E_b = (J + K) / (1 + S_ab);
                const E_a = (J - K) / (1 - S_ab);

                E_b_values.push(E_b);
                E_a_values.push(E_a);
            }

            // Convert R values to Angstroms for display
            const R_values_angstrom = R_values_au.map(R => R * ANGSTROM_TO_BOHR);

            return {
                R_values: R_values_angstrom,
                E_b_values: E_b_values,
                E_a_values: E_a_values
            };
        }

        // Calculate electron density for a given R
        function calculateElectronDensity(R_au) {
            const gridResolution = 50;
            const x_min = -3;
            const x_max = 3;
            const y_min = -3;
            const y_max = 3;

            const x = [];
            const y = [];
            const z_bonding = [];
            const z_antibonding = [];

            // Create grid
            for (let i = 0; i < gridResolution; i++) {
                x.push(x_min + (x_max - x_min) * i / (gridResolution - 1));
            }
            for (let j = 0; j < gridResolution; j++) {
                y.push(y_min + (y_max - y_min) * j / (gridResolution - 1));
            }

            // Calculate overlap integral for normalization
            const S_ab = (1 + R_au + R_au * R_au / 3) * Math.exp(-R_au);
            const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
            const N_a = 1 / Math.sqrt(2 * (1 - S_ab));

            // Calculate density for each grid point
            for (let j = 0; j < gridResolution; j++) {
                const row_bonding = [];
                const row_antibonding = [];

                for (let i = 0; i < gridResolution; i++) {
                    const x_val = x[i];
                    const y_val = y[j];

                    // Distances to nuclei
                    const dist_A = Math.sqrt(Math.pow(x_val + R_au / 2, 2) + Math.pow(y_val, 2));
                    const dist_B = Math.sqrt(Math.pow(x_val - R_au / 2, 2) + Math.pow(y_val, 2));

                    // 1s orbital values
                    const phi_A = Math.exp(-dist_A);
                    const phi_B = Math.exp(-dist_B);

                    // Molecular orbitals
                    const psi_b = N_b * (phi_A + phi_B);
                    const psi_a = N_a * (phi_A - phi_B);

                    // Probability densities
                    row_bonding.push(psi_b * psi_b);
                    row_antibonding.push(psi_a * psi_a);
                }

                z_bonding.push(row_bonding);
                z_antibonding.push(row_antibonding);
            }

            return { x, y, z_bonding, z_antibonding };
        }

        // Create potential energy plot
        function createPotentialEnergyPlot() {
            const data = calculatePotentialEnergyCurves();

            const trace1 = {
                x: data.R_values,
                y: data.E_b_values,
                type: 'scatter',
                mode: 'lines',
                name: 'bonding PE',
                line: { color: 'blue' }
            };

            const trace2 = {
                x: data.R_values,
                y: data.E_a_values,
                type: 'scatter',
                mode: 'lines',
                name: 'antibonding PE',
                line: { color: 'red' }
            };

            const layout = {
                title: 'potential energy',
                xaxis: {
                    title: 'R',
                    range: [0, 8]
                },
                yaxis: {
                    range: [-0.6, 0.4]
                },
                showlegend: true,
                font: { size: 14 }
            };

            Plotly.newPlot(potentialEnergyPlotContainer, [trace1, trace2], layout);
        }

        // Create electron density plots
        function createElectronDensityPlots(R_angstroms) {
            const R_au = R_angstroms / ANGSTROM_TO_BOHR;
            const densityData = calculateElectronDensity(R_au);

            const bondingLayout = {
                title: 'Bonding Orbital Density',
                showlegend: false,
                autosize: true,
                scene: {
                    xaxis: { range: [-3, 3], title: '' },
                    yaxis: { range: [-3, 3], title: '' },
                    zaxis: { range: [0, 0.5], title: '' },
                    camera: {
                        eye: { x: 1.5, y: 1.5, z: 1.5 }
                    }
                },
                margin: { l: 0, r: 0, t: 30, b: 0 }
            };

            const antibondingLayout = {
                title: 'Antibonding Orbital Density',
                showlegend: false,
                autosize: true,
                scene: {
                    xaxis: { range: [-3, 3], title: '' },
                    yaxis: { range: [-3, 3], title: '' },
                    zaxis: { range: [0, 0.5], title: '' },
                    camera: {
                        eye: { x: 1.5, y: 1.5, z: 1.5 }
                    }
                },
                margin: { l: 0, r: 0, t: 30, b: 0 }
            };

            const bondingData = [{
                x: densityData.x,
                y: densityData.y,
                z: densityData.z_bonding,
                type: 'surface',
                colorscale: 'YlOrBr',
                showscale: false
            }];

            const antibondingData = [{
                x: densityData.x,
                y: densityData.y,
                z: densityData.z_antibonding,
                type: 'surface',
                colorscale: 'YlOrBr',
                showscale: false
            }];

            Plotly.newPlot(bondingPlotContainer, bondingData, bondingLayout, {responsive: true});
            Plotly.newPlot(antibondingPlotContainer, antibondingData, antibondingLayout, {responsive: true});
        }

        // Update electron density plots
        function updateElectronDensityPlots(R_angstroms) {
            const R_au = R_angstroms / ANGSTROM_TO_BOHR;
            const densityData = calculateElectronDensity(R_au);

            const update = {
                z: [densityData.z_bonding],
                x: [densityData.x],
                y: [densityData.y]
            };

            Plotly.restyle(bondingPlotContainer, update, [0]);

            const updateAnti = {
                z: [densityData.z_antibonding],
                x: [densityData.x],
                y: [densityData.y]
            };

            Plotly.restyle(antibondingPlotContainer, updateAnti, [0]);
        }

        // Slider event handler
        sliderR.addEventListener('input', function() {
            const R_angstroms = parseFloat(this.value);
            sliderRValue.textContent = R_angstroms.toFixed(3);
            updateElectronDensityPlots(R_angstroms);
        });

        // Initialize plots on page load
        window.addEventListener('load', function() {
            createPotentialEnergyPlot();
            createElectronDensityPlots(0.001);
        });
    </script>
