<body>
    <div id="app-container">
        <div id="control-panel">
            <h4>time</h4>
            <input type="range" id="slider-time" min="0" max="672" step="1" value="0">
            <div>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-back">«</button>
                <button id="btn-reset">⥀</button>
                <button id="btn-step-forward">»</button>
            </div>
            <p id="display-time">0 d 0 h</p>

            <hr>

            <h4>Moon's rotational period related to the current value</h4>
            <div id="rotation-selector">
                <button id="btn-rot-half">half</button>
                <button id="btn-rot-normal" class="active">normal</button>
                <button id="btn-rot-double">double</button>
                <button id="btn-rot-none">no rotation</button>
            </div>

            <hr>

            <div class="checkbox-container">
                <input type="checkbox" id="check-day-night">
                <label for="check-day-night">day/night</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="check-near-side" checked>
                <label for="check-near-side">near side of the Moon</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="check-earth-moon-line" checked>
                <label for="check-earth-moon-line">Earth-Moon line</label>
            </div>

            <div class="checkbox-container">
                <input type="checkbox" id="check-moon-only">
                <label for="check-moon-only">the Moon only</label>
            </div>
        </div>

        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global variables
        let time = 0;
        let isPlaying = false;
        let animationId = null;
        let rotationMode = 'normal';

        // Display options
        let showDayNight = false;
        let showNearSide = true;
        let showEarthMoonLine = true;
        let showMoonOnly = false;

        // P5.js setup
        function setup() {
            const canvas = createCanvas(window.innerWidth - 300, window.innerHeight);
            canvas.parent('visualization-area');
            canvas.canvas.id = 'main-canvas';
        }

        function windowResized() {
            resizeCanvas(window.innerWidth - 300, window.innerHeight);
        }

        // P5.js draw loop
        function draw() {
            background(10, 10, 20);

            // Move origin to center
            translate(width / 2, height / 2);

            // Calculate angles
            const earthRotation = (time / 24) * TWO_PI;
            const moonOrbit = (time / 672) * TWO_PI;
            let moonRotation;

            // Set moon rotation based on mode
            switch(rotationMode) {
                case 'half':
                    moonRotation = moonOrbit * 2;
                    break;
                case 'normal':
                    moonRotation = moonOrbit;
                    break;
                case 'double':
                    moonRotation = moonOrbit / 2;
                    break;
                case 'none':
                    moonRotation = 0;
                    break;
            }

            // Moon position
            const orbitRadius = 250;
            let mx = orbitRadius * cos(moonOrbit - PI/2);
            let my = orbitRadius * sin(moonOrbit - PI/2);

            if (showMoonOnly) {
                mx = 0;
                my = 0;
            }

            // Draw Earth (if not moon-only mode)
            if (!showMoonOnly) {
                push();

                // Earth body
                fill(59, 130, 246); // Blue
                circle(0, 0, 160);

                // North pole
                fill(255);
                circle(0, -80, 20);

                // Continents (simplified)
                fill(34, 197, 94); // Green
                beginShape();
                vertex(-20, -40);
                vertex(10, -35);
                vertex(15, -10);
                vertex(5, 10);
                vertex(-10, 15);
                vertex(-25, 5);
                vertex(-30, -20);
                endShape(CLOSE);

                beginShape();
                vertex(40, 20);
                vertex(50, 30);
                vertex(45, 50);
                vertex(30, 55);
                vertex(20, 45);
                vertex(25, 25);
                endShape(CLOSE);

                // Rotate and draw stick figure
                rotate(earthRotation);
                stroke(255, 0, 0);
                strokeWeight(2);
                noFill();

                // Stick figure body
                line(0, -80, 0, -40);
                line(0, -60, -10, -50);
                line(0, -60, 10, -50);
                line(0, -40, -10, -20);
                line(0, -40, 10, -20);

                // Stick figure head
                fill(255, 0, 0);
                noStroke();
                circle(0, -85, 10);

                // Day/night terminator
                if (showDayNight) {
                    rotate(-earthRotation);
                    fill(0, 0, 0, 128);
                    noStroke();
                    arc(0, 0, 160, 160, PI/2, 3*PI/2);
                }

                pop();
            }

            // Draw Moon
            push();
            translate(mx, my);

            // Moon body
            fill(128, 128, 128);
            circle(0, 0, 50);

            // Near side marker
            if (showNearSide) {
                push();
                rotate(moonRotation);

                // Green arc
                stroke(34, 197, 94);
                strokeWeight(3);
                noFill();
                arc(0, 0, 50, 50, -PI/2, PI/2);

                // Magenta stick figure on near side
                stroke(217, 70, 239);
                strokeWeight(2);
                line(0, -25, 0, -10);
                line(0, -17, -5, -12);
                line(0, -17, 5, -12);
                line(0, -10, -5, 0);
                line(0, -10, 5, 0);

                fill(217, 70, 239);
                noStroke();
                circle(0, -28, 6);

                pop();
            }

            // Day/night terminator for Moon
            if (showDayNight) {
                fill(0, 0, 0, 128);
                noStroke();
                arc(0, 0, 50, 50, PI/2, 3*PI/2);
            }

            pop();

            // Earth-Moon line
            if (showEarthMoonLine && !showMoonOnly) {
                stroke(255, 255, 0, 150);
                strokeWeight(2);
                drawingContext.setLineDash([5, 5]);
                line(0, 0, mx, my);
                drawingContext.setLineDash([]);
            }

            // Update time display
            updateTimeDisplay();
        }

        // Utility functions
        function updateTimeDisplay() {
            const days = Math.floor(time / 24);
            const hours = time % 24;
            document.getElementById('display-time').textContent = `${days} d ${hours} h`;
        }

        function updateSlider() {
            document.getElementById('slider-time').value = time;
        }

        // Animation control
        function animate() {
            if (isPlaying && time < 672) {
                time++;
                updateSlider();
                if (time === 672) {
                    isPlaying = false;
                    document.getElementById('btn-play-pause').textContent = '▶';
                }
            }
        }

        // Event listeners
        document.getElementById('slider-time').addEventListener('input', function(e) {
            time = parseInt(e.target.value);
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '❚❚' : '▶';
        });

        document.getElementById('btn-step-back').addEventListener('click', function() {
            if (time > 0) {
                time--;
                updateSlider();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            time = 0;
            updateSlider();
        });

        document.getElementById('btn-step-forward').addEventListener('click', function() {
            if (time < 672) {
                time++;
                updateSlider();
            }
        });

        // Rotation mode buttons
        document.getElementById('rotation-selector').addEventListener('click', function(e) {
            if (e.target.tagName === 'BUTTON') {
                // Remove active class from all buttons
                this.querySelectorAll('button').forEach(btn => {
                    btn.classList.remove('active');
                });
                // Add active class to clicked button
                e.target.classList.add('active');
                // Set rotation mode
                rotationMode = e.target.id.replace('btn-rot-', '');
            }
        });

        // Checkbox change listeners
        document.getElementById('check-day-night').addEventListener('change', function(e) {
            showDayNight = e.target.checked;
        });

        document.getElementById('check-near-side').addEventListener('change', function(e) {
            showNearSide = e.target.checked;
        });

        document.getElementById('check-earth-moon-line').addEventListener('change', function(e) {
            showEarthMoonLine = e.target.checked;
        });

        document.getElementById('check-moon-only').addEventListener('change', function(e) {
            showMoonOnly = e.target.checked;
        });

        // Start animation loop
        setInterval(animate, 100);
    </script>
</body>
</html>
