            <input type="range" id="slider-x-lower" min="900" max="1100" step="1" value="902">
            <span id="span-x-lower-value" class="value-display">902</span>
        </div>
        <div class="zoom-control-row">
            <label for="slider-x-upper">x axis upper boundary</label>
            <input type="range" id="slider-x-upper" min="900" max="1100" step="1" value="1091">
            <span id="span-x-upper-value" class="value-display">1091</span>
        </div>
    </div>

    <script>
        // Constants for spectrum calculation
        const CONSTANTS = {
            nu0: 1000,
            B_upper: 0.98,
            A_upper: 1.95,
            B_lower: 1.0,
            A_lower: 2.0,
            zeta: 0.2,
            T: 300,
            k: 0.695,
            J_max: 60
        };

        // Initial state
        let currentView = 'full';
        let currentK = 0;
        let currentSubbandType = 'full';
        let currentBranch = 'all';

        // Initialize plot
        function initializePlot() {
            const data = generateSpectrumData();
            const layout = {
                title: {
                    text: currentView === 'full' ? 'Rovibronic Spectrum' : `K = ${currentK} sub-band`,
                    font: { size: 16 }
                },
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [900, 1100],
                    autorange: false
                },
                yaxis: {
                    title: 'absorbance',
                    range: [0, 1.2],
                    autorange: false
                },
                showlegend: false,
                bargap: 0,
                margin: { t: 50, b: 50, l: 50, r: 30 }
            };
            const config = { responsive: true, displayModeBar: false };
            Plotly.newPlot('plot-div', data, layout, config);
        }

        // Generate spectrum data
        function generateSpectrumData() {
            const traces = [];

            if (currentView === 'full') {
                traces.push(generateFullSpectrumTrace());
            } else {
                if (currentSubbandType === 'full') {
                    // Both positive and negative sub-bands
                    if (currentK > 0) {
                        traces.push(generateSubbandTrace('negative', currentK, 'all'));
                    }
                    traces.push(generateSubbandTrace('positive', currentK, 'all'));
                } else {
                    // Specific sub-band type
                    traces.push(generateSubbandTrace(currentSubbandType, currentK, currentBranch));
                }
            }

            return traces;
        }

        // Generate full spectrum trace
        function generateFullSpectrumTrace() {
            const x = [];
            const y = [];
            const width = [];

            for (let J_lower = 0; J_lower <= CONSTANTS.J_max; J_lower++) {
                for (let K_lower = 0; K_lower <= J_lower; K_lower++) {
                    // Positive sub-band (ΔK = +1)
                    if (K_lower + 1 <= CONSTANTS.J_max) {
                        addTransitionLines(x, y, width, J_lower, K_lower, 'positive', 'P');
                        addTransitionLines(x, y, width, J_lower, K_lower, 'positive', 'Q');
                        addTransitionLines(x, y, width, J_lower, K_lower, 'positive', 'R');
                    }

                    // Negative sub-band (ΔK = -1)
                    if (K_lower > 0 && K_lower - 1 <= CONSTANTS.J_max) {
                        addTransitionLines(x, y, width, J_lower, K_lower, 'negative', 'P');
                        addTransitionLines(x, y, width, J_lower, K_lower, 'negative', 'Q');
                        addTransitionLines(x, y, width, J_lower, K_lower, 'negative', 'R');
                    }
                }
            }

            return {
                x: x,
                y: y,
                width: width,
                type: 'bar',
                marker: { color: 'black' }
            };
        }

        // Generate sub-band trace
        function generateSubbandTrace(subbandType, K_value, branchFilter) {
            const x = [];
            const y = [];
            const width = [];
            const color = subbandType === 'positive' ? 'red' : 'blue';

            for (let J_lower = 0; J_lower <= CONSTANTS.J_max; J_lower++) {
                if (branchFilter === 'all' || branchFilter === 'P') {
                    addTransitionLines(x, y, width, J_lower, K_value, subbandType, 'P');
                }
                if (branchFilter === 'all' || branchFilter === 'Q') {
                    addTransitionLines(x, y, width, J_lower, K_value, subbandType, 'Q');
                }
                if (branchFilter === 'all' || branchFilter === 'R') {
                    addTransitionLines(x, y, width, J_lower, K_value, subbandType, 'R');
                }
            }

            return {
                x: x,
                y: y,
                width: width,
                type: 'bar',
                marker: { color: color }
            };
        }

        // Add transition lines to arrays
        function addTransitionLines(x, y, width, J_lower, K_lower, subbandType, branch) {
            // Calculate transition J_upper based on branch
            let J_upper;
            if (branch === 'P') J_upper = J_lower - 1;
            else if (branch === 'Q') J_upper = J_lower;
            else if (branch === 'R') J_upper = J_lower + 1;

            // Skip if J_upper is out of bounds
            if (J_upper < 0 || J_upper > CONSTANTS.J_max) return;

            // Calculate K_upper based on subband type
            let K_upper;
            if (subbandType === 'positive') K_upper = K_lower + 1;
            else if (subbandType === 'negative') K_upper = K_lower - 1;

            // Skip if K_upper is negative or greater than J_upper
            if (K_upper > J_upper || K_upper < 0) return;

            // Calculate wavenumber
            const E_lower = CONSTANTS.B_lower * J_lower * (J_lower + 1) + 
                            (CONSTANTS.A_lower - CONSTANTS.B_lower) * K_lower * K_lower;
            const E_upper = CONSTANTS.B_upper * J_upper * (J_upper + 1) + 
                            (CONSTANTS.A_upper - CONSTANTS.B_upper) * K_upper * K_upper;
            const wavenumber = CONSTANTS.nu0 + E_upper - E_lower;

            // Calculate intensity
            const degeneracy = K_lower === 0 ? 1 : 2;
            const boltzmann = Math.exp(-E_lower / (CONSTANTS.k * CONSTANTS.T));
            const intensity = (2 * J_lower + 1) * degeneracy * boltzmann * calculateLineStrength(J_lower, K_lower, subbandType, branch);

            // Normalize intensity (max intensity is 1.0)
            x.push(wavenumber);
            y.push(intensity);
            width.push(0.1);
        }

        // Calculate line intensity (simplified model)
        function calculateLineStrength(J_lower, K_lower, subbandType, branch) {
            // Simplified line strength calculation
            if (subbandType === 'positive') {
                if (branch === 'P') return (J_lower - K_lower + 1) * (J_lower + K_lower);
                if (branch === 'Q') return (J_lower + K_lower + 1) * (J_lower - K_lower);
                if (branch === 'R') return (J_lower + K_lower + 1) * (J_lower + K_lower + 2);
            } else { // negative sub-band
                if (branch === 'P') return (J_lower + K_lower) * (J_lower - K_lower - 1);
                if (branch === 'Q') return (J_lower - K_lower) * (J_lower + K_lower - 1);
                if (branch === 'R') return (J_lower + K_lower - 1) * (J_lower + K_lower);
            }
            return 1;
        }

        // Update plot based on current settings
        function updatePlot() {
            const data = generateSpectrumData();
            const layout = {
                title: {
                    text: getPlotTitle(),
                    font: { size: 16 }
                },
                xaxis: {
                    range: [
                        parseInt(document.getElementById('slider-x-lower').value),
                        parseInt(document.getElementById('slider-x-upper').value)
                    ],
                    autorange: false
                }
            };

            Plotly.react('plot-div', data, layout);
        }

        // Get plot title based on current settings
        function getPlotTitle() {
            if (currentView === 'full') {
                return 'Rovibronic Spectrum';
            } else {
                let title = `K = ${currentK} sub-band`;
                if (currentSubbandType !== 'full') {
                    title += `<br>"${currentSubbandType}" sub-band`;
                }
                return title;
            }
        }

        // Event Listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize the plot
            initializePlot();

            // View selector
            document.querySelectorAll('input[name="view-selector"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    currentView = this.value;

                    const kGroup = document.getElementById('k-selector-group');
                    const subbandGroup = document.getElementById('subband-type-group');
                    const branchGroup = document.getElementById('branch-selector-group');

                    if (currentView === 'full') {
                        kGroup.classList.add('disabled');
                        subbandGroup.classList.add('disabled');
                        branchGroup.classList.add('hidden');
                    } else {
                        kGroup.classList.remove('disabled');
                        subbandGroup.classList.remove('disabled');

                        // Update branch selector visibility based on subband type
                        if (currentSubbandType !== 'full') {
                            branchGroup.classList.remove('hidden');
                        }
                    }

                    updatePlot();
                });
            });

            // K selector
            document.querySelectorAll('input[name="k-selector"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    currentK = parseInt(this.value);

                    // Handle K=0 special case (no negative sub-band)
                    const negativeOption = document.getElementById('radio-subband-negative');
                    if (currentK === 0) {
                        negativeOption.disabled = true;

                        // If negative sub-band was selected, switch to full
                        if (currentSubbandType === 'negative') {
                            document.getElementById('radio-subband-full').checked = true;
                            currentSubbandType = 'full';
                            document.getElementById('branch-selector-group').classList.add('hidden');
                        }
                    } else {
                        negativeOption.disabled = false;
                    }

                    updatePlot();
                });
            });

            // Subband type selector
            document.querySelectorAll('input[name="subband-type"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    currentSubbandType = this.value;

                    const branchGroup = document.getElementById('branch-selector-group');
                    if (currentSubbandType === 'full') {
                        branchGroup.classList.add('hidden');
                    } else {
                        branchGroup.classList.remove('hidden');
                    }

                    updatePlot();
                });
            });

            // Branch selector
            document.querySelectorAll('input[name="branch-selector"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    currentBranch = this.value;
                    updatePlot();
                });
            });

            // Zoom sliders
            const lowerSlider = document.getElementById('slider-x-lower');
            const upperSlider = document.getElementById('slider-x-upper');
            const lowerValue = document.getElementById('span-x-lower-value');
            const upperValue = document.getElementById('span-x-upper-value');

            lowerSlider.addEventListener('input', function() {
                let newValue = parseInt(this.value);
                let upperValue = parseInt(upperSlider.value);

                // Prevent lower bound from exceeding upper bound
                if (newValue >= upperValue) {
                    newValue = upperValue - 1;
                    this.value = newValue;
                }

                lowerValue.textContent = newValue;
                updatePlot();
            });

            upperSlider.addEventListener('input', function() {
                let newValue = parseInt(this.value);
                let lowerValue = parseInt(lowerSlider.value);

                // Prevent upper bound from being below lower bound
                if (newValue <= lowerValue) {
                    newValue = lowerValue + 1;
                    this.value = newValue;
                }

                upperValue.textContent = newValue;
                updatePlot();
            });

            // Initialize the branch selector visibility
            if (currentSubbandType !== 'full') {
                document.getElementById('branch-selector-group').classList.remove('hidden');
            }
        });
    </script>
</body>
</html>
