    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // Global variables
        let scene, camera, renderer, controls;
        let radiationPattern, patternParent;
        let boundingBox;
        let compassGroup;
        let currentWaveType = 'P';

        // Initialize the scene
        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight - document.getElementById('control-panel').offsetHeight);
            renderer.shadowMap.enabled = true;
            renderer.shadowMap.type = THREE.PCFSoftShadowMap;
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Orbit controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight1.position.set(5, 5, 5);
            directionalLight1.castShadow = true;
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.3);
            directionalLight2.position.set(-5, 3, -5);
            scene.add(directionalLight2);

            // Create radiation pattern
            createRadiationPattern();

            // Create compass
            createCompass();

            // Create "U" arrow
            createUArrow();

            // Set up event listeners
            setupEventListeners();

            // Start animation loop
            animate();
        }

        // Create radiation pattern based on wave type
        function createRadiationPattern() {
            if (radiationPattern) {
                scene.remove(patternParent);
                if (boundingBox) {
                    scene.remove(boundingBox);
                }
            }

            patternParent = new THREE.Object3D();

            // Define parametric function based on wave type
            let parametricFunction;
            switch (currentWaveType) {
                case 'P':
                    parametricFunction = function(u, v, target) {
                        const theta = u * Math.PI;
                        const phi = v * 2 * Math.PI;
                        const r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                        const x = r * Math.sin(theta) * Math.cos(phi);
                        const y = r * Math.sin(theta) * Math.sin(phi);
                        const z = r * Math.cos(theta);
                        target.set(x, y, z);
                    };
                    break;
                case 'SV':
                    parametricFunction = function(u, v, target) {
                        const theta = u * Math.PI;
                        const phi = v * 2 * Math.PI;
                        const r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                        const x = r * Math.sin(theta) * Math.cos(phi);
                        const y = r * Math.sin(theta) * Math.sin(phi);
                        const z = r * Math.cos(theta);
                        target.set(x, y, z);
                    };
                    break;
                case 'SH':
                    parametricFunction = function(u, v, target) {
                        const theta = u * Math.PI;
                        const phi = v * 2 * Math.PI;
                        const r = Math.abs(Math.cos(theta) * Math.cos(phi));
                        const x = r * Math.sin(theta) * Math.cos(phi);
                        const y = r * Math.sin(theta) * Math.sin(phi);
                        const z = r * Math.cos(theta);
                        target.set(x, y, z);
                    };
                    break;
            }

            // Create parametric geometry
            const geometry = new THREE.ParametricGeometry(parametricFunction, 50, 50);
            const material = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                side: THREE.DoubleSide,
                roughness: 0.5,
                metalness: 0.1
            });

            radiationPattern = new THREE.Mesh(geometry, material);
            radiationPattern.castShadow = true;
            radiationPattern.receiveShadow = true;
            patternParent.add(radiationPattern);

            // Apply initial rotations
            updatePatternRotation();

            scene.add(patternParent);

            // Create bounding box
            boundingBox = new THREE.BoxHelper(patternParent, 0x000000);
            boundingBox.visible = false;
            scene.add(boundingBox);
        }

        // Create compass overlay
        function createCompass() {
            compassGroup = new THREE.Object3D();

            // Outer circle
            const circleGeometry = new THREE.BufferGeometry();
            const circlePoints = [];
            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * Math.PI * 2;
                circlePoints.push(new THREE.Vector3(Math.cos(angle) * 2, Math.sin(angle) * 2, 0));
            }
            circleGeometry.setFromPoints(circlePoints);
            const circleMaterial = new THREE.LineDashedMaterial({ color: 0x000000, dashSize: 0.1, gapSize: 0.05 });
            const circle = new THREE.Line(circleGeometry, circleMaterial);
            circle.computeLineDistances();
            compassGroup.add(circle);

            // Cardinal directions
            const directions = [
                { text: 'N', x: 0, y: 2.2 },
                { text: 'S', x: 0, y: -2.2 },
                { text: 'E', x: 2.2, y: 0 },
                { text: 'W', x: -2.2, y: 0 }
            ];

            directions.forEach(dir => {
                const sprite = createTextSprite(dir.text);
                sprite.position.set(dir.x, dir.y, 0);
                compassGroup.add(sprite);
            });

            // Degree markings
            for (let angle = 30; angle < 360; angle += 30) {
                const rad = (angle * Math.PI) / 180;
                const x = Math.cos(rad) * 1.8;
                const y = Math.sin(rad) * 1.8;
                const sprite = createTextSprite(angle + '°');
                sprite.position.set(x, y, 0);
                sprite.scale.set(0.5, 0.5, 1);
                compassGroup.add(sprite);
            }

            compassGroup.position.set(0, 0, -3);
            scene.add(compassGroup);
        }

        // Create text sprite
        function createTextSprite(text) {
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            canvas.width = 128;
            canvas.height = 64;

            context.fillStyle = 'white';
            context.fillRect(0, 0, canvas.width, canvas.height);
            context.font = '32px Arial';
            context.fillStyle = 'black';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText(text, canvas.width / 2, canvas.height / 2);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(0.5, 0.25, 1);

            return sprite;
        }

        // Create "U" arrow
        function createUArrow() {
            const arrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 1),
                new THREE.Vector3(0, 0, 0),
                1.5,
                0x000000,
                0.5,
                0.3
            );
            scene.add(arrow);

            const uLabel = createTextSprite('U');
            uLabel.position.set(0, 0, 1.7);
            scene.add(uLabel);
        }

        // Update pattern rotation based on slider values
        function updatePatternRotation() {
            if (!patternParent) return;

            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const rake = parseFloat(document.getElementById('slider-rake').value);

            // Convert to radians
            const strikeRad = (strike * Math.PI) / 180;
            const dipRad = (dip * Math.PI) / 180;
            const rakeRad = (rake * Math.PI) / 180;

            // Reset rotation
            patternParent.rotation.set(0, 0, 0);

            // Apply rotations in order: strike (Z), dip (X), rake (Y)
            patternParent.rotation.z = strikeRad;
            patternParent.rotation.x = dipRad;
            patternParent.rotation.y = rakeRad;

            // Update bounding box
            if (boundingBox) {
                boundingBox.update();
            }
        }

        // Set up event listeners
        function setupEventListeners() {
            // Slider listeners
            document.getElementById('slider-strike').addEventListener('input', function() {
                document.getElementById('strike-value').textContent = this.value;
                updatePatternRotation();
            });

            document.getElementById('slider-dip').addEventListener('input', function() {
                document.getElementById('dip-value').textContent = this.value;
                updatePatternRotation();
            });

            document.getElementById('slider-rake').addEventListener('input', function() {
                document.getElementById('rake-value').textContent = this.value;
                updatePatternRotation();
            });

            // Wave type button listeners
            const buttons = ['btn-p', 'btn-sv', 'btn-sh'];
            buttons.forEach(btnId => {
                document.getElementById(btnId).addEventListener('click', function() {
                    // Remove active class from all buttons
                    buttons.forEach(id => {
                        document.getElementById(id).classList.remove('active');
                    });

                    // Add active class to clicked button
                    this.classList.add('active');

                    // Update wave type
                    currentWaveType = this.textContent;
                    createRadiationPattern();
                });
            });

            // Checkbox listener
            document.getElementById('checkbox-box').addEventListener('change', function() {
                if (boundingBox) {
                    boundingBox.visible = this.checked;
                }
            });

            // Window resize listener
            window.addEventListener('resize', onWindowResize);
        }

        // Handle window resize
        function onWindowResize() {
            const panelHeight = document.getElementById('control-panel').offsetHeight;
            camera.aspect = window.innerWidth / (window.innerHeight - panelHeight);
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight - panelHeight);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            // Make compass always face camera
            if (compassGroup) {
                compassGroup.lookAt(camera.position);
            }

            renderer.render(scene, camera);
        }

        // Initialize when page loads
        window.addEventListener('load', function() {
            init();
            // Render MathJax
            if (typeof MathJax !== 'undefined') {
                MathJax.typeset();
            }
        });
    </script>
</body>
</html>
