                        <label for="radio-euler-0">0label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="radio-euler-1" name="euler-transformations" value="1">
                        <label for="radio-euler-1">1label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="radio-euler-2" name="euler-transformations" value="2">
                        <label for="radio-euler-2">2label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="radio-euler-3" name="euler-transformations" value="3">
                        <label for="radio-euler-3">3label>
