                <div class="radio-group">
                    <div class="radio-option">
                        <input type="radio" id="radio-series-pi" name="series-select" value="pi" checked>
                        <label for="radio-series-pi">πlabel>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="radio-series-ln2" name="series-select" value="ln2">
                        <label for="radio-series-ln2">ln(2)</label>
                    </div>
                    <div class="radio-option">
                        <input type="radio" id="radio-series-sqrt2" name="series-select" value="sqrt2">
                        <label for="radio-series-sqrt2">√2label>
                    </div>
