            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
        }

        #btn-reset:active {
            transform: scale(0.95);
        }
    </style>
</head>
<body>
    <!-- Main container for the whole demo -->
    <div id="demo-container">

        <!-- Left side: Control Panel -->
        <div id="control-panel">

            <!-- Share 1 Control -->
            <div class="share-control">
                <label for="joystick-share1">share 1</label>
                <div id="joystick-share1" class="joystick-container">
                    <div id="joystick-handle1" class="joystick-handle"></div>
                </div>
            </div>

            <!-- Share 2 Control -->
            <div class="share-control">
                <label for="joystick-share2">share 2</label>
                <div id="joystick-share2" class="joystick-container">
                    <div id="joystick-handle2" class="joystick-handle"></div>
                </div>
            </div>

        </div>

        <!-- Right side: Visualization -->
        <div id="visualization-area">
            <!-- The p5.js canvas will be attached here -->
        </div>

        <!-- Reset Button -->
        <button id="btn-reset">+</button>

    </div>

    <script>
        // Joystick functionality
        class Joystick {
            constructor(containerId, handleId, initialTop, initialLeft) {
                this.container = document.getElementById(containerId);
                this.handle = document.getElementById(handleId);
                this.isDragging = false;
                this.initialTop = initialTop;
                this.initialLeft = initialLeft;

                this.handle.style.top = initialTop + '%';
                this.handle.style.left = initialLeft + '%';

                this.setupEvents();
            }

            setupEvents() {
                this.handle.addEventListener('mousedown', (e) => {
                    this.isDragging = true;
                    e.preventDefault();
                });

                document.addEventListener('mousemove', (e) => {
                    if (!this.isDragging) return;

                    const rect = this.container.getBoundingClientRect();
                    const x = e.clientX - rect.left;
                    const y = e.clientY - rect.top;

                    const percentageX = Math.max(0, Math.min(100, (x / rect.width) * 100));
                    const percentageY = Math.max(0, Math.min(100, (y / rect.height) * 100));

                    this.handle.style.left = percentageX + '%';
                    this.handle.style.top = percentageY + '%';

                    // Update positions for p5.js
                    const centerX = 50;
                    const centerY = 50;
                    const offsetX = (percentageX - centerX) / centerX;
                    const offsetY = (percentageY - centerY) / centerY;

                    if (this.container.id === 'joystick-share1') {
                        window.share1OffsetX = offsetX;
                        window.share1OffsetY = offsetY;
                    } else {
                        window.share2OffsetX = offsetX;
                        window.share2OffsetY = offsetY;
                    }
                });

                document.addEventListener('mouseup', () => {
                    this.isDragging = false;
                });
            }

            reset() {
                this.handle.style.top = this.initialTop + '%';
                this.handle.style.left = this.initialLeft + '%';

                if (this.container.id === 'joystick-share1') {
                    window.share1OffsetX = (this.initialLeft - 50) / 50;
                    window.share1OffsetY = (this.initialTop - 50) / 50;
                } else {
                    window.share2OffsetX = (this.initialLeft - 50) / 50;
                    window.share2OffsetY = (this.initialTop - 50) / 50;
                }
            }
        }

        // Initialize joysticks
        const joystick1 = new Joystick('joystick-share1', 'joystick-handle1', 25, 75);
        const joystick2 = new Joystick('joystick-share2', 'joystick-handle2', 75, 25);

        // Initialize global offset variables
        window.share1OffsetX = (75 - 50) / 50;
        window.share1OffsetY = (25 - 50) / 50;
        window.share2OffsetX = (25 - 50) / 50;
        window.share2OffsetY = (75 - 50) / 50;

        // Reset button functionality
        document.getElementById('btn-reset').addEventListener('click', () => {
            joystick1.reset();
            joystick2.reset();
        });

        // p5.js sketch
        let share1_graphic, share2_graphic;
        const secretSize = 64;
        const shareSize = secretSize * 2;

        // Create smiley face pattern
        function createSecretImage() {
            const secret = [];
            for (let i = 0; i < secretSize; i++) {
                secret[i] = [];
                for (let j = 0; j < secretSize; j++) {
                    // Create circle for face
                    const cx = secretSize / 2;
                    const cy = secretSize / 2;
                    const radius = secretSize / 2 - 8;
                    const dist = Math.sqrt((i - cx) ** 2 + (j - cy) ** 2);

                    if (dist <= radius) {
                        // Check if it's an eye
                        const eye1dist = Math.sqrt((i - cx + 8) ** 2 + (j - cy - 8) ** 2);
                        const eye2dist = Math.sqrt((i - cx + 8) ** 2 + (j - cy + 8) ** 2);

                        // Check if it's the smile
                        const smileDist = Math.sqrt((i - cx) ** 2 + (j - cy + 5) ** 2);
                        const inSmile = smileDist >= 8 && smileDist <= 12 && j > cy;

                        if (eye1dist <= 3 || eye2dist <= 3 || inSmile) {
                            secret[i][j] = 0; // White
                        } else {
                            secret[i][j] = 1; // Black (face outline)
                        }
                    } else {
                        secret[i][j] = 0; // White background
                    }
                }
            }
            return secret;
        }

        function setup() {
            const canvas = createCanvas(shareSize * 2, shareSize * 2);
            canvas.parent('visualization-area');

            // Create graphics buffers for shares
            share1_graphic = createGraphics(shareSize, shareSize);
            share2_graphic = createGraphics(shareSize, shareSize);

            // Generate shares
            const secret = createSecretImage();
            generateShares(secret);
        }

        function generateShares(secret) {
            share1_graphic.clear();
            share2_graphic.clear();

            const patterns = [
                [[1, 0], [0, 1]], // Black, Transparent / Transparent, Black
                [[0, 1], [1, 0]]  // Transparent, Black / Black, Transparent
            ];

            for (let i = 0; i < secretSize; i++) {
                for (let j = 0; j < secretSize; j++) {
                    const patternChoice = Math.floor(Math.random() * 2);
                    const pattern = patterns[patternChoice];
                    const complementary = patterns[1 - patternChoice];

                    for (let di = 0; di < 2; di++) {
                        for (let dj = 0; dj < 2; dj++) {
                            const x = i * 2 + di;
                            const y = j * 2 + dj;

                            if (secret[i][j] === 0) {
                                // White pixel - same pattern for both shares
                                share1_graphic.noStroke();
                                share2_graphic.noStroke();

                                if (pattern[di][dj] === 1) {
                                    share1_graphic.fill(0);
                                    share1_graphic.rect(x, y, 1, 1);
                                    share2_graphic.fill(0);
                                    share2_graphic.rect(x, y, 1, 1);
                                }
                            } else {
                                // Black pixel - complementary patterns
                                share1_graphic.noStroke();
                                share2_graphic.noStroke();

                                if (pattern[di][dj] === 1) {
                                    share1_graphic.fill(0);
                                    share1_graphic.rect(x, y, 1, 1);
                                }

                                if (complementary[di][dj] === 1) {
                                    share2_graphic.fill(0);
                                    share2_graphic.rect(x, y, 1, 1);
                                }
                            }
                        }
                    }
                }
            }
        }

        function draw() {
            background(255);

            // Calculate positions based on joystick offsets
            const maxOffset = shareSize / 2;
            const x1 = width / 2 - shareSize / 2 + window.share1OffsetX * maxOffset;
            const y1 = height / 2 - shareSize / 2 + window.share1OffsetY * maxOffset;
            const x2 = width / 2 - shareSize / 2 + window.share2OffsetX * maxOffset;
            const y2 = height / 2 - shareSize / 2 + window.share2OffsetY * maxOffset;

            // Draw shares with transparency effect
            push();
            image(share1_graphic, x1, y1);
            image(share2_graphic, x2, y2);
            pop();
        }
    </script>
</body>
</html>
