                </div>
            </div>

            <div id="circles-per-ring-control-group">
                <label for="cpr-radios">circles per ring</label>
                <div class="radio-group">
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                </div>
                <div class="radio-group">
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                </div>
                <div class="radio-group">
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                </div>
                <div class="radio-group">
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                </div>
                <div class="radio-group">
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                </div>
                <div class="radio-group">
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>

            <div id="inverse-control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>

        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let locators;
        let draggedLocator = null;
        let canvasSize = 550;

        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            angleMode(RADIANS);

            // Initialize locators
            locators = [
                new p5.Vector(-50, 0),
                new p5.Vector(50, 0)
            ];

            // Add event listeners
            document.querySelectorAll('input[type="radio"]').forEach(radio => {
                radio.addEventListener('change', draw);
            });

            document.getElementById('checkbox-inverse').addEventListener('change', draw);
        }

        function draw() {
            background('#C1D4E6');
            translate(width / 2, height / 2);

            let rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            let cpr = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            let inverse = document.getElementById('checkbox-inverse').checked;

            if (inverse) {
                drawInverse(rings, cpr);
            } else {
                drawNormal(rings, cpr);
            }

            noLoop();
        }

        function drawNormal(rings, cpr) {
            let N = cpr;
            let M = rings;
            let R_max = width/2 * 0.9;
            let k = (1 - sin(PI/N)) / (1 + sin(PI/N));
            let R_c = R_max / (1 + sin(PI/N));
            let r = R_c * sin(PI/N);

            // Draw guide lines for outermost ring
            stroke('#A0A0A0');
            strokeWeight(1);
            for (let j = 0; j < N; j++) {
                let angle = j * 2 * PI / N;
                line(0, 0, R_c * cos(angle), R_c * sin(angle));
            }

            // Draw circles for each ring
            fill('#8C929D');
            stroke(0);
            strokeWeight(1);

            for (let i = 0; i < M; i++) {
                let current_R_c = R_c * pow(k, i);
                let current_r = r * pow(k, i);

                for (let j = 0; j < N; j++) {
                    let angle = j * 2 * PI / N;
                    let x = current_R_c * cos(angle);
                    let y = current_R_c * sin(angle);
                    circle(x, y, current_r * 2);
                }
            }
        }

        function drawInverse(rings, cpr) {
            // First, get all original circles
            let originalCircles = getOriginalCircles(rings, cpr);

            // Draw locators
            fill('#FF0000');
            noStroke();
            circle(locators[0].x, locators[0].y, 8);
            circle(locators[1].x, locators[1].y, 8);

            // Draw connecting line
            stroke('#FF0000');
            strokeWeight(1);
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            // Calculate inversion circle
            let C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            let R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;

            // Draw inversion circle
            fill('rgba(255, 0, 0, 0.3)');
            stroke(0);
            strokeWeight(1);
            circle(C_inv.x, C_inv.y, R_inv * 2);

            // Draw inverted circles
            fill('rgba(220, 100, 220, 0.5)');
            stroke(0);
            strokeWeight(1);

            for (let origCircle of originalCircles) {
                invertCircle(origCircle.center, origCircle.radius, C_inv, R_inv);
            }
        }

        function getOriginalCircles(rings, cpr) {
            let circles = [];
            let N = cpr;
            let M = rings;
            let R_max = width/2 * 0.9;
            let k = (1 - sin(PI/N)) / (1 + sin(PI/N));
            let R_c = R_max / (1 + sin(PI/N));
            let r = R_c * sin(PI/N);

            for (let i = 0; i < M; i++) {
                let current_R_c = R_c * pow(k, i);
                let current_r = r * pow(k, i);

                for (let j = 0; j < N; j++) {
                    let angle = j * 2 * PI / N;
                    let x = current_R_c * cos(angle);
                    let y = current_R_c * sin(angle);
                    circles.push({
                        center: new p5.Vector(x, y),
                        radius: current_r
                    });
                }
            }

            return circles;
        }

        function invertCircle(C_orig, r_orig, C_inv, R_inv) {
            let d = p5.Vector.dist(C_inv, C_orig);

            // Check if circle passes through inversion center
            if (abs(d - r_orig) < 0.001) {
                // Inverse is a line
                let lineDist = pow(R_inv, 2) / (2 * d);
                let dir = p5.Vector.sub(C_orig, C_inv).normalize();

                // Line is perpendicular to dir, at distance lineDist from C_inv
                let normal = new p5.Vector(-dir.y, dir.x);

                // Two points on the line
                let p1 = p5.Vector.add(C_inv, p5.Vector.mult(normal, lineDist));
                let p2 = p5.Vector.add(C_inv, p5.Vector.mult(normal, -lineDist));

                // Extend line to canvas bounds
                let canvasHalf = width / 2;
                drawClippedLine(p1, p2, canvasHalf);
            } else {
                // Inverse is a circle
                let denom = pow(d, 2) - pow(r_orig, 2);
                let scale = pow(R_inv, 2) / denom;
                let C_new = p5.Vector.add(C_inv, p5.Vector.mult(p5.Vector.sub(C_orig, C_inv), scale));
                let r_new = abs(scale * r_orig);

                circle(C_new.x, C_new.y, r_new * 2);
            }
        }

        function drawClippedLine(p1, p2, bound) {
            // Calculate intersections with canvas boundaries
            let intersections = [];

            // Check intersection with each boundary
            // Left boundary: x = -bound
            if (p1.x !== p2.x) {
                let t = (-bound - p1.x) / (p2.x - p1.x);
                if (t >= 0 && t <= 1) {
                    let y = p1.y + t * (p2.y - p1.y);
                    if (y >= -bound && y <= bound) {
                        intersections.push(new p5.Vector(-bound, y));
                    }
                }
            }

            // Right boundary: x = bound
            if (p1.x !== p2.x) {
                let t = (bound - p1.x) / (p2.x - p1.x);
                if (t >= 0 && t <= 1) {
                    let y = p1.y + t * (p2.y - p1.y);
                    if (y >= -bound && y <= bound) {
                        intersections.push(new p5.Vector(bound, y));
                    }
                }
            }

            // Top boundary: y = -bound
            if (p1.y !== p2.y) {
                let t = (-bound - p1.y) / (p2.y - p1.y);
                if (t >= 0 && t <= 1) {
                    let x = p1.x + t * (p2.x - p1.x);
                    if (x >= -bound && x <= bound) {
                        intersections.push(new p5.Vector(x, -bound));
                    }
                }
            }

            // Bottom boundary: y = bound
            if (p1.y !== p2.y) {
                let t = (bound - p1.y) / (p2.y - p1.y);
                if (t >= 0 && t <= 1) {
                    let x = p1.x + t * (p2.x - p1.x);
                    if (x >= -bound && x <= bound) {
                        intersections.push(new p5.Vector(x, bound));
                    }
                }
            }

            // Draw line segment between intersections
            if (intersections.length === 2) {
                line(intersections[0].x, intersections[0].y, intersections[1].x, intersections[1].y);
            }
        }

        function mousePressed() {
            if (!document.getElementById('checkbox-inverse').checked) {
                return false;
            }

            let mx = mouseX - width/2;
            let my = mouseY - height/2;

            // Check if clicking on a locator
            for (let i = 0; i < locators.length; i++) {
                let d = dist(mx, my, locators[i].x, locators[i].y);
                if (d < 8) {
                    draggedLocator = i;
                    return true;
                }
            }

            return false;
        }

        function mouseDragged() {
            if (draggedLocator !== null && document.getElementById('checkbox-inverse').checked) {
                let mx = mouseX - width/2;
                let my = mouseY - height/2;
                locators[draggedLocator].x = mx;
                locators[draggedLocator].y = my;

                // Redraw
                background('#C1D4E6');
                translate(width / 2, height / 2);

                let rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
                let cpr = parseInt(document.querySelector('input[name="cpr"]:checked').value);

                drawInverse(rings, cpr);
            }
        }

        function mouseReleased() {
            draggedLocator = null;
        }

        // Initial draw
        window.addEventListener('load', () => {
            setTimeout(() => {
                let canvas = document.getElementById('defaultCanvas0');
                if (canvas) {
                    let ctx = canvas.getContext('2d');
                    ctx.fillStyle = '#C1D4E6';
                    ctx.fillRect(0, 0, canvas.width, canvas.height);
                }
                draw();
            }, 100);
        });
    </script>
</body>
</html>
