    </div>

    <script>
        // Global variables
        let logCa = -1;
        let pKa = 1.2;
        let canvas;
        let ctx;

        // Setup function for p5.js
        function setup() {
            canvas = createCanvas(window.innerWidth - 40, window.innerHeight - 120);
            canvas.parent('visualization-container');
            canvas.id('canvas');

            // Get actual canvas context for better control
            ctx = canvas.drawingContext;

            // Set up event listeners
            setupEventListeners();

            // Initial render
            MathJax.typeset();
            draw();
        }

        function windowResized() {
            resizeCanvas(window.innerWidth - 40, window.innerHeight - 120);
            draw();
        }

        function setupEventListeners() {
            const logCaSlider = document.getElementById('slider-logCa');
            const pkaSlider = document.getElementById('slider-pka');
            const logCaValue = document.getElementById('logCa-value');
            const pkaValue = document.getElementById('pka-value');

            logCaSlider.addEventListener('input', function() {
                logCa = parseFloat(this.value);
                logCaValue.textContent = logCa.toFixed(2);
                draw();
            });

            pkaSlider.addEventListener('input', function() {
                pKa = parseFloat(this.value);
                pkaValue.textContent = pKa.toFixed(1);
                draw();
            });
        }

        function draw() {
            // Clear canvas
            background(255);

            // Set up coordinate system
            const xMin = -4, xMax = 7;
            const yMin = 0, yMax = 7;

            // Draw axes
            drawAxes(xMin, xMax, yMin, yMax);

            // Draw curves
            drawStrongAcidLine(logCa, xMin, xMax, yMin, yMax);
            drawWeakAcidLine(logCa, xMin, xMax, yMin, yMax);
            drawExactCurve(logCa, xMin, xMax, yMin, yMax);

            // Draw indicator point
            drawIndicatorPoint(logCa, pKa, xMin, xMax, yMin, yMax);
        }

        function drawAxes(xMin, xMax, yMin, yMax) {
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            stroke(100);
            strokeWeight(1);

            // Calculate tick intervals
            const xStep = 1;
            const yStep = 1;

            // Draw x-axis
            line(margin, height - margin, width - margin, height - margin);

            // Draw y-axis
            line(margin, height - margin, margin, margin);

            // Draw x-axis ticks and labels
            textAlign(CENTER, TOP);
            for (let x = xMin; x <= xMax; x += xStep) {
                const px = map(x, xMin, xMax, margin, width - margin);
                line(px, height - margin - 5, px, height - margin + 5);
                noStroke();
                fill(0);
                text(x.toString(), px, height - margin + 8);
                stroke(100);
            }

            // Draw y-axis ticks and labels
            textAlign(RIGHT, CENTER);
            for (let y = yMin; y <= yMax; y += yStep) {
                const py = map(y, yMin, yMax, height - margin, margin);
                line(margin - 5, py, margin + 5, py);
                noStroke();
                fill(0);
                text(y.toString(), margin - 8, py);
                stroke(100);
            }

            // Draw axis labels
            noStroke();
            fill(0);
            textAlign(CENTER, BOTTOM);
            text('pK_a', width / 2, height - 10);

            push();
            translate(15, height / 2);
            rotate(-PI / 2);
            textAlign(CENTER, CENTER);
            text('pH', 0, 0);
            pop();
        }

        function drawStrongAcidLine(logCa, xMin, xMax, yMin, yMax) {
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            const pH = -logCa;

            if (pH >= yMin && pH <= yMax) {
                stroke(128, 0, 0);
                strokeWeight(2);
                line(margin, map(pH, yMin, yMax, height - margin, margin), 
                     width - margin, map(pH, yMin, yMax, height - margin, margin));
            }
        }

        function drawWeakAcidLine(logCa, xMin, xMax, yMin, yMax) {
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            stroke(0, 128, 0);
            strokeWeight(2);

            const x1 = margin;
            const x2 = width - margin;
            const y1 = map(0.5 * (xMin - logCa), yMin, yMax, height - margin, margin);
            const y2 = map(0.5 * (xMax - logCa), yMin, yMax, height - margin, margin);

            // Only draw the line within the plot bounds
            let validY1 = y1;
            let validY2 = y2;

            if (y1 < margin) {
                const t = (margin - y1) / (y2 - y1);
                validY1 = margin;
                validX1 = x1 + t * (x2 - x1);
            } else if (y1 > height - margin) {
                const t = (height - margin - y1) / (y2 - y1);
                validY1 = height - margin;
                validX1 = x1 + t * (x2 - x1);
            }

            if (y2 < margin) {
                const t = (margin - y1) / (y2 - y1);
                validY2 = margin;
                validX2 = x1 + t * (x2 - x1);
            } else if (y2 > height - margin) {
                const t = (height - margin - y1) / (y2 - y1);
                validY2 = height - margin;
                validX2 = x1 + t * (x2 - x1);
            }

            line(x1, y1, x2, y2);
        }

        function drawExactCurve(logCa, xMin, xMax, yMin, yMax) {
            const margin = 50;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            const Ca = Math.pow(10, logCa);

            stroke(0, 0, 255);
            strokeWeight(3);
            noFill();

            beginShape();
            for (let px = margin; px <= width - margin; px += 2) {
                const pKaVal = map(px, margin, width - margin, xMin, xMax);
                const pH = calculateExactpH(Ca, pKaVal);

                if (pH >= yMin && pH <= yMax) {
                    vertex(px, map(pH, yMin, yMax, height - margin, margin));
                }
            }
            endShape();
        }

        function calculateExactpH(Ca, pKa) {
            const Ka = Math.pow(10, -pKa);

            // Solve quadratic equation: [H+]^2 + Ka*[H+] - Ka*Ca = 0
            const a = 1;
            const b = Ka;
            const c = -Ka * Ca;

            const discriminant = b * b - 4 * a * c;
            const H_plus = (-b + Math.sqrt(discriminant)) / (2 * a);

            return -Math.log10(H_plus);
        }

        function drawIndicatorPoint(logCa, pKa, xMin, xMax, yMin, yMax) {
            const margin = 50;
            const Ca = Math.pow(10, logCa);

            const px = map(pKa, xMin, xMax, margin, width - margin);
            const pH = calculateExactpH(Ca, pKa);

            if (pH >= yMin && pH <= yMax) {
                const py = map(pH, yMin, yMax, height - margin, margin);

                // Draw orange point with dark outline
                fill(255, 165, 0);
                stroke(0);
                strokeWeight(2);
                ellipse(px, py, 12, 12);
            }
        }
