    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // Grid and algorithm state
        const GRID_SIZE = 50;
        let grid = [];
        let obstacles = [];
        let startNode = { x: 7, y: 42 };
        let endNode = { x: 42, y: 7 };
        let history = [];
        let currentFrame = 0;

        // Algorithm configuration
        let algorithm = 'dijkstra';
        let obstacleLayout = '1-square';

        // Joystick state
        let joystickStart = { x: 0.15, y: 0.85 };
        let joystickEnd = { x: 0.85, y: 0.15 };
        let isDraggingStart = false;
        let isDraggingEnd = false;

        // Priority Queue implementation (Min-Heap)
        class PriorityQueue {
            constructor() {
                this.values = [];
            }

            enqueue(val, priority) {
                this.values.push({ val, priority });
                this.sort();
            }

            dequeue() {
                return this.values.shift();
            }

            sort() {
                this.values.sort((a, b) => a.priority - b.priority);
            }

            isEmpty() {
                return this.values.length === 0;
            }

            contains(val) {
                return this.values.some(item => item.val.x === val.x && item.val.y === val.y);
            }
        }

        // Initialize grid
        function initializeGrid() {
            grid = [];
            for (let y = 0; y < GRID_SIZE; y++) {
                grid[y] = [];
                for (let x = 0; x < GRID_SIZE; x++) {
                    grid[y][x] = {
                        x: x,
                        y: y,
                        isObstacle: false,
                        distance: Infinity,
                        previous: null,
                        f: 0,
                        g: 0,
                        h: 0
                    };
                }
            }

            // Set obstacles based on selected layout
            obstacles = [];
            switch (obstacleLayout) {
                case '1-square':
                    obstacles.push({ x: 25, y: 25 });
                    break;
                case '2-squares':
                    obstacles.push({ x: 20, y: 20 });
                    obstacles.push({ x: 30, y: 30 });
                    break;
                case 'l-shaped-wall':
                    for (let i = 20; i <= 30; i++) {
                        obstacles.push({ x: i, y: 25 });
                        if (i > 25) obstacles.push({ x: 25, y: i });
                    }
                    break;
            }

            obstacles.forEach(obs => {
                grid[obs.y][obs.x].isObstacle = true;
            });
        }

        // Get neighbors (8-directional movement)
        function getNeighbors(node) {
            const neighbors = [];
            const directions = [
                { dx: -1, dy: 0, cost: 1 },
                { dx: 1, dy: 0, cost: 1 },
                { dx: 0, dy: -1, cost: 1 },
                { dx: 0, dy: 1, cost: 1 },
                { dx: -1, dy: -1, cost: Math.sqrt(2) },
                { dx: 1, dy: -1, cost: Math.sqrt(2) },
                { dx: -1, dy: 1, cost: Math.sqrt(2) },
                { dx: 1, dy: 1, cost: Math.sqrt(2) }
            ];

            for (const dir of directions) {
                const x = node.x + dir.dx;
                const y = node.y + dir.dy;

                if (x >= 0 && x < GRID_SIZE && y >= 0 && y < GRID_SIZE && !grid[y][x].isObstacle) {
                    neighbors.push({ node: grid[y][x], cost: dir.cost });
                }
            }

            return neighbors;
        }

        // Manhattan distance heuristic for A*
        function heuristic(a, b) {
            return Math.abs(a.x - b.x) + Math.abs(a.y - b.y);
        }

        // Reconstruct path
        function reconstructPath(node) {
            const path = [];
            let current = node;
            while (current) {
                path.unshift(current);
                current = current.previous;
            }
            return path;
        }

        // Dijkstra's algorithm
        function dijkstra() {
            initializeGrid();

            const start = grid[startNode.y][startNode.x];
            const end = grid[endNode.y][endNode.x];
            const openSet = new PriorityQueue();
            const closedSet = new Set();
            history = [];

            start.distance = 0;
            openSet.enqueue(start, 0);

            while (!openSet.isEmpty()) {
                const current = openSet.dequeue().val;
                closedSet.add(`${current.x},${current.y}`);

                // Save frame
                history.push({
                    openSet: openSet.values.map(item => item.val),
                    closedSet: Array.from(closedSet).map(key => {
                        const [x, y] = key.split(',').map(Number);
                        return grid[y][x];
                    }),
                    current: current,
                    path: null
                });

                if (current === end) {
                    const path = reconstructPath(current);
                    history[history.length - 1].path = path;
                    break;
                }

                const neighbors = getNeighbors(current);
                for (const { node: neighbor, cost } of neighbors) {
                    if (closedSet.has(`${neighbor.x},${neighbor.y}`)) continue;

                    const alt = current.distance + cost;
                    if (alt < neighbor.distance) {
                        neighbor.distance = alt;
                        neighbor.previous = current;

                        if (!openSet.contains(neighbor)) {
                            openSet.enqueue(neighbor, alt);
                        }
                    }
                }
            }

            return history;
        }

        // A* algorithm
        function aStar() {
            initializeGrid();

            const start = grid[startNode.y][startNode.x];
            const end = grid[endNode.y][endNode.x];
            const openSet = new PriorityQueue();
            const closedSet = new Set();
            history = [];

            start.g = 0;
            start.h = heuristic(start, end);
            start.f = start.h;
            openSet.enqueue(start, start.f);

            while (!openSet.isEmpty()) {
                const current = openSet.dequeue().val;
                closedSet.add(`${current.x},${current.y}`);

                // Save frame
                history.push({
                    openSet: openSet.values.map(item => item.val),
                    closedSet: Array.from(closedSet).map(key => {
                        const [x, y] = key.split(',').map(Number);
                        return grid[y][x];
                    }),
                    current: current,
                    path: null
                });

                if (current === end) {
                    const path = reconstructPath(current);
                    history[history.length - 1].path = path;
                    break;
                }

                const neighbors = getNeighbors(current);
                for (const { node: neighbor, cost } of neighbors) {
                    if (closedSet.has(`${neighbor.x},${neighbor.y}`)) continue;

                    const tentativeG = current.g + cost;

                    if (!openSet.contains(neighbor)) {
                        neighbor.g = tentativeG;
                        neighbor.h = heuristic(neighbor, end);
                        neighbor.f = neighbor.g + neighbor.h;
                        neighbor.previous = current;
                        openSet.enqueue(neighbor, neighbor.f);
                    } else if (tentativeG < neighbor.g) {
                        neighbor.g = tentativeG;
                        neighbor.f = neighbor.g + neighbor.h;
                        neighbor.previous = current;
                    }
                }
            }

            return history;
        }

        // Run selected algorithm
        function runAlgorithm() {
            if (algorithm === 'dijkstra') {
                dijkstra();
            } else {
                aStar();
            }

            // Update slider
            const slider = document.getElementById('slider-step');
            slider.max = history.length;
            slider.value = 1;
            document.getElementById('label-step').textContent = '+ 1';
            currentFrame = 0;
        }

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');

            // Initialize
            runAlgorithm();
        }

        function draw() {
            background(255);

            const cellSize = width / GRID_SIZE;

            if (history.length > 0 && currentFrame < history.length) {
                const frame = history[currentFrame];

                // Draw grid cells
                for (let y = 0; y < GRID_SIZE; y++) {
                    for (let x = 0; x < GRID_SIZE; x++) {
                        const cell = grid[y][x];
                        let fillColor = color(255);

                        if (cell.isObstacle) {
                            fillColor = color(0);
                        } else if (frame.closedSet.some(node => node.x === x && node.y === y)) {
                            fillColor = color(0, 0, 205);
                        } else if (frame.openSet.some(node => node.x === x && node.y === y)) {
                            fillColor = color(189, 224, 254);
                        }

                        fill(fillColor);
                        noStroke();
                        rect(x * cellSize, y * cellSize, cellSize, cellSize);
                    }
                }

                // Draw start and end points
                fill(color(52, 168, 83));
                noStroke();
                rect(startNode.x * cellSize, startNode.y * cellSize, cellSize, cellSize);
                rect(endNode.x * cellSize, endNode.y * cellSize, cellSize, cellSize);

                // Draw path if available
                if (frame.path && frame.path.length > 0) {
                    stroke(color(255, 0, 0));
                    strokeWeight(3);
                    noFill();
                    beginShape();
                    for (const node of frame.path) {
                        const centerX = node.x * cellSize + cellSize / 2;
                        const centerY = node.y * cellSize + cellSize / 2;
                        vertex(centerX, centerY);
                    }
                    endShape();
                }

                // Draw current node
                if (frame.current) {
                    stroke(255, 165, 0);
                    strokeWeight(4);
                    noFill();
                    rect(
                        frame.current.x * cellSize + 2, 
                        frame.current.y * cellSize + 2, 
                        cellSize - 4, 
                        cellSize - 4
                    );
                }
            }
        }

        // Joystick functionality
        function setupJoystick(joystickId, handleId, initialValue) {
            const joystick = document.getElementById(joystickId);
            const handle = document.getElementById(handleId);

            // Set initial position
            handle.style.left = `${initialValue.x * 100}%`;
            handle.style.top = `${initialValue.y * 100}%`;

            let isDragging = false;

            function onMouseDown(e) {
                isDragging = true;
                document.addEventListener('mousemove', onMouseMove);
                document.addEventListener('mouseup', onMouseUp);
                e.preventDefault();
            }

            function onMouseMove(e) {
                if (!isDragging) return;

                const rect = joystick.getBoundingClientRect();
                let x = (e.clientX - rect.left) / rect.width;
                let y = (e.clientY - rect.top) / rect.height;

                // Constrain to joystick area
                x = Math.max(0, Math.min(1, x));
                y = Math.max(0, Math.min(1, y));

                handle.style.left = `${x * 100}%`;
                handle.style.top = `${y * 100}%`;

                // Update grid coordinates
                if (joystickId === 'joystick-start') {
                    startNode.x = Math.floor(x * (GRID_SIZE - 1));
                    startNode.y = Math.floor(y * (GRID_SIZE - 1));
                    joystickStart.x = x;
                    joystickStart.y = y;
                } else {
                    endNode.x = Math.floor(x * (GRID_SIZE - 1));
                    endNode.y = Math.floor(y * (GRID_SIZE - 1));
                    joystickEnd.x = x;
                    joystickEnd.y = y;
                }
            }

            function onMouseUp() {
                isDragging = false;
                document.removeEventListener('mousemove', onMouseMove);
                document.removeEventListener('mouseup', onMouseUp);

                // Trigger algorithm re-run
                runAlgorithm();
            }

            handle.addEventListener('mousedown', onMouseDown);

            return { onStart: onMouseDown };
        }

        // Initialize joysticks
        const startJoystick = setupJoystick('joystick-start', 'handle-start', joystickStart);
        const endJoystick = setupJoystick('joystick-end', 'handle-end', joystickEnd);

        // Event listeners
        document.getElementById('radio-dijkstra').addEventListener('change', (e) => {
            if (e.target.checked) {
                algorithm = 'dijkstra';
                runAlgorithm();
            }
        });

        document.getElementById('radio-a-star').addEventListener('change', (e) => {
            if (e.target.checked) {
                algorithm = 'a-star';
                runAlgorithm();
            }
        });

        document.getElementById('slider-step').addEventListener('input', (e) => {
            currentFrame = parseInt(e.target.value) - 1;
            document.getElementById('label-step').textContent = `+ ${e.target.value}`;
        });

        document.getElementById('select-obstacles').addEventListener('change', (e) => {
            obstacleLayout = e.target.value;
            runAlgorithm();
        });
    </script>
