</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-source-charge">source charge</label>
                <input type="range" id="slider-source-charge" min="-5" max="5" step="0.1" value="2.0">
                <span id="label-source-charge"></span>
            </div>
            <div class="slider-group">
                <label for="slider-test-charge">charge</label>
                <input type="range" id="slider-test-charge" min="-0.2" max="0.2" step="0.0005" value="-0.0845">
                <span id="label-test-charge"></span>
            </div>
        </div>

        <div id="main-content">
            <div id="canvas-container"></div>
            <div id="info-panel">
                <p id="display-E-mag"></p>
                <p id="display-Ex"></p>
                <p id="display-Ey"></p>
                <p id="display-x-pos"></p>
                <p id="display-y-pos"></p>
                <p id="display-force"></p>
            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
    <script>
        // Global variables
        let Q = 2.0; // Source charge
        let q = -0.0845; // Test charge
        let x = 2.0; // Test charge x position
        let y = 3.0; // Test charge y position
        let isDragging = false;
        let k = 1; // Coulomb constant

        // p5.js setup
        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            // Initialize slider labels
            document.getElementById('label-source-charge').textContent = Q.toFixed(1);
            document.getElementById('label-test-charge').textContent = q.toFixed(4);

            // Add slider event listeners
            document.getElementById('slider-source-charge').addEventListener('input', function(e) {
                Q = parseFloat(e.target.value);
                document.getElementById('label-source-charge').textContent = Q.toFixed(1);
                updateDisplay();
            });

            document.getElementById('slider-test-charge').addEventListener('input', function(e) {
                q = parseFloat(e.target.value);
                document.getElementById('label-test-charge').textContent = q.toFixed(4);
                updateDisplay();
            });

            // Initial display update
            updateDisplay();
        }

        // p5.js draw loop
        function draw() {
            background(255);

            // Setup coordinate system
            push();
            translate(width / 2, height / 2);
            scale(40, -40); // 40 pixels per unit, invert y-axis

            // Draw grid
            stroke(230);
            strokeWeight(1/40);
            for (let i = -7; i <= 7; i++) {
                if (i % 2 === 0) {
                    strokeWeight(2/40);
                } else {
                    strokeWeight(1/40);
                }
                line(i, -7, i, 7);
                line(-7, i, 7, i);
            }

            // Draw axes
            stroke(0);
            strokeWeight(3/40);
            line(-7, 0, 7, 0);
            line(0, -7, 0, 7);

            // Draw axis labels
            push();
            scale(1, -1); // Temporarily fix y-axis for text
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(12/40);
            for (let i = -6; i <= 6; i += 2) {
                if (i !== 0) {
                    text(i, i, -0.5);
                    text(i, -0.5, i);
                }
            }
            pop();

            // Draw source charge at origin
            fill(0, 0, 255);
            noStroke();
            circle(0, 0, 0.3);

            // Calculate electric field and force
            let r = Math.sqrt(x * x + y * y);
            let Ex = k * Q * x / Math.pow(r, 3);
            let Ey = k * Q * y / Math.pow(r, 3);
            let E = Math.sqrt(Ex * Ex + Ey * Ey);
            let Fx = q * Ex;
            let Fy = q * Ey;
            let F = Math.sqrt(Fx * Fx + Fy * Fy);

            // Draw test charge locator
            fill(0);
            noStroke();
            circle(x, y, 0.2);

            // Draw electric field vector (red)
            if (E > 0) {
                drawArrow(createVector(x, y), createVector(Ex, Ey).mult(400), color(255, 0, 0));
            }

            // Draw electric force vector (blue)
            if (F > 0 && q !== 0) {
                drawArrow(createVector(x, y), createVector(Fx, Fy).mult(400), color(0, 0, 255));
            }

            pop();
        }

        // Mouse pressed event
        function mousePressed() {
            let canvasX = mouseX - width / 2;
            let canvasY = -(mouseY - height / 2);
            canvasX /= 40;
            canvasY /= 40;

            let d = dist(canvasX, canvasY, x, y);
            if (d < 0.3) {
                isDragging = true;
            }
        }

        // Mouse dragged event
        function mouseDragged() {
            if (isDragging) {
                x = (mouseX - width / 2) / 40;
                y = -(mouseY - height / 2) / 40;

                // Clamp to canvas bounds
                x = constrain(x, -7, 7);
                y = constrain(y, -7, 7);

                updateDisplay();
            }
        }

        // Mouse released event
        function mouseReleased() {
            isDragging = false;
        }

        // Draw arrow function
        function drawArrow(base, vector, color) {
            push();
            stroke(color);
            strokeWeight(3/40);
            fill(color);

            let end = p5.Vector.add(base, vector);
            line(base.x, base.y, end.x, end.y);

            // Arrow head
            let arrowSize = 0.1;
            push();
            translate(end.x, end.y);
            rotate(vector.heading());
            triangle(0, 0, -arrowSize, -arrowSize/2, -arrowSize, arrowSize/2);
            pop();

            pop();
        }

        // Update display panel
        function updateDisplay() {
            let r = Math.sqrt(x * x + y * y);
            let Ex = k * Q * x / Math.pow(r, 3);
            let Ey = k * Q * y / Math.pow(r, 3);
            let E = Math.sqrt(Ex * Ex + Ey * Ey);
            let Fx = q * Ex;
            let Fy = q * Ey;
            let F = Math.sqrt(Fx * Fx + Fy * Fy);

            document.getElementById('display-E-mag').innerHTML = `|E| = ${E.toFixed(3)} N/C`;
            document.getElementById('display-Ex').innerHTML = `E<sub>x</sub> component = ${Ex.toFixed(3)} N/C`;
            document.getElementById('display-Ey').innerHTML = `E<sub>y</sub> component = ${Ey.toFixed(3)} N/C`;
            document.getElementById('display-x-pos').innerHTML = `x position = ${x.toFixed(2)} m`;
            document.getElementById('display-y-pos').innerHTML = `y position = ${y.toFixed(2)} m`;
            document.getElementById('display-force').innerHTML = `Electric force = ${F.toFixed(3)} N`;
        }
    </script>
</body>
</html>
