</head>
<body>
    <h1>Fourier Transform of Radially Symmetric Potential Functions</h1>

    <div id="controls-container">
        <div id="function-selector">
            <label><input type="checkbox" id="checkbox-hertz" checked> Hertz</label>
            <label><input type="checkbox" id="checkbox-overlap" checked> Overlap</label>
            <label><input type="checkbox" id="checkbox-gaussian" checked> Gaussian</label>
        </div>

        <div id="scale-selector">
            <label>axes</label>
            <button id="btn-linear-linear" class="active">linear-linear</button>
            <button id="btn-log-linear">log-linear</button>
            <button id="btn-log-log">log-log</button>
        </div>

        <div id="sliders-container">
            <div id="slider-r-control" class="slider-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control" class="slider-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>

    <p id="hover-info">(Hover over the curves to see math labels.)</p>

    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        // Initialize MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['$', '$'], ['\\(', '\\)']],
                displayMath: [['$$', '$$'], ['\\[', '\\]']]
            }
        };

        // Function definitions
        function hertzPotential(r) {
            return r <= 1 ? Math.pow(1 - r * r, 2.5) : 0;
        }

        function overlapPotential(r) {
            return r <= 1 ? Math.pow(1 - r, 2) : 0;
        }

        function gaussianPotential(r) {
            return Math.exp(-Math.PI * r * r);
        }

        function hertzTransform(k) {
            if (k === 0) return 15 * Math.PI / 2;
            return 15 * Math.PI * ((3 - k * k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5);
        }

        function overlapTransform(k) {
            if (k === 0) return 8 * Math.PI / 3;
            return 8 * Math.PI * (2 - k * k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4);
        }

        function gaussianTransform(k) {
            return (1 / Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI));
        }

        // Update function
        function updatePlots() {
            // Get control states
            const hertzVisible = document.getElementById('checkbox-hertz').checked;
            const overlapVisible = document.getElementById('checkbox-overlap').checked;
            const gaussianVisible = document.getElementById('checkbox-gaussian').checked;

            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);

            // Update slider value displays
            document.getElementById('span-r-value').textContent = rMax.toFixed(1);
            document.getElementById('span-k-value').textContent = kMax.toFixed(2);

            // Generate data
            const numPoints = 200;
            const rValues = Array.from({length: numPoints}, (_, i) => i * rMax / (numPoints - 1));
            const kValues = Array.from({length: numPoints}, (_, i) => i * kMax / (numPoints - 1));

            // Left plot data (f(r))
            const leftTraces = [];

            if (hertzVisible) {
                const hertzData = rValues.map(r => hertzPotential(r));
                leftTraces.push({
                    x: rValues,
                    y: hertzData,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#ff0000', width: 2},
                    name: 'Hertz',
                    hovertemplate: 'Hertz<br>$f_H(r) = (1 - r^2)^{5/2}, r \\le 1$<extra></extra>'
                });
            }

            if (overlapVisible) {
                const overlapData = rValues.map(r => overlapPotential(r));
                leftTraces.push({
                    x: rValues,
                    y: overlapData,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#000000', width: 2},
                    name: 'Overlap',
                    hovertemplate: 'Overlap<br>$f_O(r) = (1 - r)^2, r \\le 1$<extra></extra>'
                });
            }

            if (gaussianVisible) {
                const gaussianData = rValues.map(r => gaussianPotential(r));
                leftTraces.push({
                    x: rValues,
                    y: gaussianData,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000ff', width: 2},
                    name: 'Gaussian',
                    hovertemplate: 'Gaussian<br>$f_G(r) = e^{-\\pi r^2}$<extra></extra>'
                });
            }

            // Right plot data (F(k))
            const rightTraces = [];
            const activeScale = document.querySelector('#scale-selector button.active').id;
            const yaxisType = activeScale === 'btn-linear-linear' ? 'linear' : 'log';

            if (hertzVisible) {
                const hertzTransformData = kValues.map(k => hertzTransform(k));
                rightTraces.push({
                    x: kValues,
                    y: hertzTransformData,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#ff0000', width: 2},
                    name: 'Hertz',
                    hovertemplate: 'Hertz<br>$F_H(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}$<extra></extra>'
                });
            }

            if (overlapVisible) {
                const overlapTransformData = kValues.map(k => overlapTransform(k));
                rightTraces.push({
                    x: kValues,
                    y: overlapTransformData,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#000000', width: 2},
                    name: 'Overlap',
                    hovertemplate: 'Overlap<br>$F_O(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}$<extra></extra>'
                });
            }

            if (gaussianVisible) {
                const gaussianTransformData = kValues.map(k => gaussianTransform(k));
                rightTraces.push({
                    x: kValues,
                    y: gaussianTransformData,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000ff', width: 2},
                    name: 'Gaussian',
                    hovertemplate: 'Gaussian<br>$F_G(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}$<extra></extra>'
                });
            }

            // Layouts
            const leftLayout = {
                title: {text: 'function', font: {color: '#999999'}},
                xaxis: {title: 'r', range: [0, rMax]},
                yaxis: {title: 'f(r)'},
                margin: {l: 50, r: 50, t: 50, b: 50},
                hovermode: 'closest'
            };

            const rightLayout = {
                title: {text: '3D Fourier transform (real part)', font: {color: '#999999'}},
                xaxis: {title: 'k', range: [0, kMax], type: activeScale === 'btn-log-log' || activeScale === 'btn-log-linear' ? 'log' : 'linear'},
                yaxis: {title: 'F(k)', type: yaxisType},
                margin: {l: 50, r: 50, t: 50, b: 50},
                hovermode: 'closest'
            };

            // Update plots
            Plotly.react('plot-left', leftTraces, leftLayout, {responsive: true});
            Plotly.react('plot-right', rightTraces, rightLayout, {responsive: true});

            // Render MathJax
            setTimeout(() => {
                MathJax.typesetPromise();
            }, 100);
        }

        // Event listeners
        document.getElementById('checkbox-hertz').addEventListener('change', updatePlots);
        document.getElementById('checkbox-overlap').addEventListener('change', updatePlots);
        document.getElementById('checkbox-gaussian').addEventListener('change', updatePlots);

        document.getElementById('slider-r').addEventListener('input', updatePlots);
        document.getElementById('slider-k').addEventListener('input', updatePlots);

        // Scale button event listeners
        document.getElementById('btn-linear-linear').addEventListener('click', function() {
            document.querySelectorAll('#scale-selector button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updatePlots();
        });

        document.getElementById('btn-log-linear').addEventListener('click', function() {
            document.querySelectorAll('#scale-selector button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updatePlots();
        });

        document.getElementById('btn-log-log').addEventListener('click', function() {
            document.querySelectorAll('#scale-selector button').forEach(btn => btn.classList.remove('active'));
            this.classList.add('active');
            updatePlots();
        });

        // Initialize on load
        window.addEventListener('load', updatePlots);
    </script>
</body>
</html>
