            </div>

            <div class="control-group">
                <h4>type</h4>
                <div id="type-switch" class="radio-group">
                    <input type="radio" id="type-emissions" name="type" value="emissions" checked>
                    <label for="type-emissions">emissions</label>
                    <input type="radio" id="type-costs" name="type" value="costs">
                    <label for="type-costs">costs</label>
                </div>
            </div>

            <div class="control-group">
                <h4>emission parameters</h4>
                <div class="slider-group">
                    <label for="slider-annual-emissions">annual CO₂ emissions per person (tons/year)</label>
                    <div class="slider-container">
                        <input type="range" id="slider-annual-emissions" min="1" max="25" value="14" step="0.1">
                        <span id="value-annual-emissions" class="value-display">14</span>
                    </div>
                </div>
                <div class="slider-group">
                    <label for="slider-reduction-time">reduction time until 50% emissions (years)</label>
                    <div class="slider-container">
                        <input type="range" id="slider-reduction-time" min="5" max="50" value="20" step="1">
                        <span id="value-reduction-time" class="value-display">20</span>
                    </div>
                </div>
                <div class="slider-group">
                    <label for="slider-annual-budget">annual CO₂ budget per person (tons/year)</label>
                    <div class="slider-container">
                        <input type="range" id="slider-annual-budget" min="0" max="5" value="1.5" step="0.1">
                        <span id="value-annual-budget" class="value-display">1.5</span>
                    </div>
                </div>
                <div class="slider-group">
                    <label for="slider-start-year">start of action</label>
                    <div class="slider-container">
                        <input type="range" id="slider-start-year" min="2020" max="2049" value="2020" step="1">
                        <span id="value-start-year" class="value-display">2020</span>
                    </div>
                </div>
            </div>

            <div class="control-group">
                <h4>compensation parameters</h4>
                <div class="slider-group">
                    <label for="slider-compensation-cost">compensation costs per ton of CO₂ ($/ton)</label>
                    <div class="slider-container">
                        <input type="range" id="slider-compensation-cost" min="5" max="100" value="15" step="1">
                        <span id="value-compensation-cost" class="value-display">15</span>
                    </div>
                </div>
                <div class="slider-group">
                    <label for="slider-fractional-compensation">fractional compensation (%)</label>
                    <div class="slider-container">
                        <input type="range" id="slider-fractional-compensation" min="0" max="100" value="50" step="1">
                        <span id="value-fractional-compensation" class="value-display">50</span>
                    </div>
                </div>
                <div class="slider-group">
                    <label for="slider-constant-compensation">constant compensation (%)</label>
                    <div class="slider-container">
                        <input type="range" id="slider-constant-compensation" min="0" max="100" value="25" step="1">
                        <span id="value-constant-compensation" class="value-display">25</span>
                    </div>
                </div>
            </div>
        </div>

        <div id="vis-panel">
            <div id="climate-neutral-buttons">
                <button id="btn-neutral-now">now</button>
                <button id="btn-neutral-2030">2030</button>
                <button id="btn-neutral-2040" class="active">2040</button>
                <button id="btn-neutral-2050">2050</button>
                <button id="btn-neutral-never">never</button>
            </div>
            <div id="main-plot-container"></div>
            <div id="budget-plot-container"></div>
        </div>
    </div>

    <script>
        // Global variables
        let mainSketch, budgetSketch;
        let neutral_year = 2040;

        // Get all control elements
        const controls = {
            scale_pop: document.getElementById('scale-population'),
            scale_ind: document.getElementById('scale-individual'),
            slider_pop: document.getElementById('slider-population'),
            value_pop: document.getElementById('value-population'),
            type_emissions: document.getElementById('type-emissions'),
            type_costs: document.getElementById('type-costs'),
            slider_annual_emissions: document.getElementById('slider-annual-emissions'),
            value_annual_emissions: document.getElementById('value-annual-emissions'),
            slider_reduction_time: document.getElementById('slider-reduction-time'),
            value_reduction_time: document.getElementById('value-reduction-time'),
            slider_annual_budget: document.getElementById('slider-annual-budget'),
            value_annual_budget: document.getElementById('value-annual-budget'),
            slider_start_year: document.getElementById('slider-start-year'),
            value_start_year: document.getElementById('value-start-year'),
            slider_compensation_cost: document.getElementById('slider-compensation-cost'),
            value_compensation_cost: document.getElementById('value-compensation-cost'),
            slider_fractional_compensation: document.getElementById('slider-fractional-compensation'),
            value_fractional_compensation: document.getElementById('value-fractional-compensation'),
            slider_constant_compensation: document.getElementById('slider-constant-compensation'),
            value_constant_compensation: document.getElementById('value-constant-compensation')
        };

        // Climate neutral buttons
        const neutralButtons = {
            now: document.getElementById('btn-neutral-now'),
            2030: document.getElementById('btn-neutral-2030'),
            2040: document.getElementById('btn-neutral-2040'),
            2050: document.getElementById('btn-neutral-2050'),
            never: document.getElementById('btn-neutral-never')
        };

        // Get input values
        function getInputs() {
            return {
                scale: controls.scale_pop.checked ? 'population' : 'individual',
                type: controls.type_emissions.checked ? 'emissions' : 'costs',
                population: parseFloat(controls.slider_pop.value),
                annual_emissions: parseFloat(controls.slider_annual_emissions.value),
                reduction_time: parseFloat(controls.slider_reduction_time.value),
                annual_budget: parseFloat(controls.slider_annual_budget.value),
                start_year: parseInt(controls.slider_start_year.value),
                compensation_cost: parseFloat(controls.slider_compensation_cost.value),
                fractional_compensation: parseFloat(controls.slider_fractional_compensation.value),
                constant_compensation: parseFloat(controls.slider_constant_compensation.value),
                neutral_year: neutral_year
            };
        }

        // Calculation functions
        function realEmissionsPerson(t, inputs) {
            if (t < inputs.start_year) {
                return inputs.annual_emissions;
            } else {
                return inputs.annual_emissions * Math.pow(0.5, (t - inputs.start_year) / inputs.reduction_time);
            }
        }

        function compensatedEmissionsPerson(t, inputs) {
            const real = realEmissionsPerson(t, inputs);
            const fractional = real * (inputs.fractional_compensation / 100);
            const constant = inputs.annual_emissions * (inputs.constant_compensation / 100);
            return fractional + constant;
        }

        function effectiveEmissionsPerson(t, inputs) {
            return realEmissionsPerson(t, inputs) - compensatedEmissionsPerson(t, inputs);
        }

        function cumulativeEffective(T, inputs) {
            let sum = 0;
            for (let t = 2020; t <= T; t++) {
                sum += effectiveEmissionsPerson(t, inputs);
            }
            return sum;
        }

        function annualCosts(t, inputs) {
            return compensatedEmissionsPerson(t, inputs) * inputs.compensation_cost;
        }

        function totalCosts(inputs) {
            let sum = 0;
            for (let t = 2020; t <= 2050; t++) {
                sum += annualCosts(t, inputs);
            }
            return sum;
        }

        // Main plot sketch
        const mainPlotSketch = (p) => {
            let data = [];
            let canvas;

            p.setup = () => {
                const container = document.getElementById('main-plot-container');
                canvas = p.createCanvas(container.offsetWidth - 40, container.offsetHeight - 40);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);
                const inputs = getInputs();
                data = [];

                // Calculate data for all years
                for (let t = 2020; t <= 2050; t++) {
                    const real = realEmissionsPerson(t, inputs);
                    const compensated = compensatedEmissionsPerson(t, inputs);
                    const effective = effectiveEmissionsPerson(t, inputs);

                    let multiplier = 1;
                    if (inputs.scale === 'population') {
                        multiplier = inputs.population * 1000 / 1000000; // Convert to million tons
                    }

                    data.push({
                        year: t,
                        real_emissions: real * multiplier,
                        compensated: compensated * multiplier,
                        effective: effective * multiplier,
                        annual_cost: annualCosts(t, inputs)
                    });
                }

                // Draw based on type
                if (inputs.type === 'emissions') {
                    drawEmissionsPlot(p, inputs, data);
                } else {
                    drawCostsPlot(p, inputs, data);
                }
            };

            p.windowResized = () => {
                const container = document.getElementById('main-plot-container');
                p.resizeCanvas(container.offsetWidth - 40, container.offsetHeight - 40);
                p.redraw();
            };
        };

        function drawEmissionsPlot(p, inputs, data) {
            const padding = 60;
            const plotWidth = p.width - 2 * padding;
            const plotHeight = p.height - 2 * padding;

            // Find max value for scaling
            let maxValue = 0;
            data.forEach(d => {
                maxValue = Math.max(maxValue, Math.abs(d.real_emissions), Math.abs(d.compensated), Math.abs(d.effective));
            });

            // Draw axes
            p.stroke(0);
            p.strokeWeight(2);
            p.line(padding, padding, padding, p.height - padding);
            p.line(padding, p.height - padding, p.width - padding, p.height - padding);

            // X-axis labels
            p.textAlign(p.CENTER, p.TOP);
            p.textSize(12);
            p.fill(0);
            for (let year = 2020; year <= 2050; year += 5) {
                const x = padding + (year - 2020) * plotWidth / 30;
                p.line(x, p.height - padding, x, p.height - padding + 5);
                p.text(year.toString(), x, p.height - padding + 10);
            }

            // Y-axis labels
            p.textAlign(p.RIGHT, p.CENTER);
            const ySteps = 5;
            for (let i = 0; i <= ySteps; i++) {
                const y = p.height - padding - (i * plotHeight / ySteps);
                const value = (i * maxValue / ySteps).toFixed(1);
                p.text(value, padding - 10, y);
                p.stroke(200);
                p.strokeWeight(1);
                p.line(padding, y, p.width - padding, y);
            }

            // Y-axis title
            p.push();
            p.translate(20, p.height / 2);
            p.rotate(-p.PI / 2);
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(14);
            const yLabel = inputs.scale === 'population' ? 
                'annual city emission of CO₂ (mil tons)' : 
                'annual CO₂ emissions per person (tons)';
            p.text(yLabel, 0, 0);
            p.pop();

            // Draw curves and areas
            p.noFill();
            p.strokeWeight(3);

            // Draw compensated area (blue)
            p.fill(100, 149, 237, 100);
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.real_emissions / maxValue) * plotHeight;
                p.vertex(x, y);
            });
            for (let i = data.length - 1; i >= 0; i--) {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (data[i].effective / maxValue) * plotHeight;
                p.vertex(x, y);
            }
            p.endShape(p.CLOSE);

            // Draw positive effective area (red)
            p.fill(237, 100, 100, 100);
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.effective / maxValue) * plotHeight;
                if (d.effective > 0) {
                    p.vertex(x, y);
                }
            });
            for (let i = data.length - 1; i >= 0; i--) {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (data[i].effective / maxValue) * plotHeight;
                if (data[i].effective > 0) {
                    p.vertex(x, p.height - padding);
                    p.vertex(x, y);
                }
            }
            p.endShape(p.CLOSE);

            // Draw negative effective area (green)
            p.fill(100, 237, 100, 100);
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.effective / maxValue) * plotHeight;
                if (d.effective < 0) {
                    p.vertex(x, y);
                }
            });
            for (let i = data.length - 1; i >= 0; i--) {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (data[i].effective / maxValue) * plotHeight;
                if (data[i].effective < 0) {
                    p.vertex(x, p.height - padding);
                    p.vertex(x, y);
                }
            }
            p.endShape(p.CLOSE);

            // Draw real emissions line (red)
            p.stroke(237, 100, 100);
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.real_emissions / maxValue) * plotHeight;
                p.vertex(x, y);
            });
            p.endShape();

            // Draw effective emissions line (black)
            p.stroke(0);
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.effective / maxValue) * plotHeight;
                p.vertex(x, y);
            });
            p.endShape();

            // Draw climate neutral vertical line
            if (inputs.neutral_year !== 'never') {
                const year = parseInt(inputs.neutral_year);
                if (year >= 2020 && year <= 2050) {
                    const x = padding + (year - 2020) * plotWidth / 30;
                    p.stroke(100);
                    p.strokeWeight(2);
                    p.setLineDash([5, 5]);
                    p.line(x, padding, x, p.height - padding);
                    p.setLineDash([]);
                    p.fill(100);
                    p.textAlign(p.LEFT, p.BOTTOM);
                    p.text(`climate neutral ${year}`, x + 5, padding - 5);
                }
            }

            // Draw horizontal lines for cumulative emissions
            if (inputs.neutral_year !== 'never') {
                const year = parseInt(inputs.neutral_year);
                const cumul = cumulativeEffective(year, inputs);
                const multiplier = inputs.scale === 'population' ? inputs.population * 1000 : 1;
                const yValue = cumul * multiplier;

                if (inputs.scale === 'population') {
                    yValue = yValue / 1000000; // Convert to million tons
                }

                const y = p.height - padding - (yValue / maxValue) * plotHeight;
                p.stroke(100);
                p.strokeWeight(2);
                p.setLineDash([5, 5]);
                p.line(padding, y, p.width - padding, y);
                p.setLineDash([]);
                p.fill(100);
                p.textAlign(p.LEFT, p.CENTER);
                p.text(`CO₂ emissions until ${year} = ${yValue.toFixed(1)} tons CO₂`, p.width - padding + 10, y);
            }

            // Draw cumulative until 2050
            const cumul2050 = cumulativeEffective(2050, inputs);
            const multiplier2050 = inputs.scale === 'population' ? inputs.population * 1000 : 1;
            let yValue2050 = cumul2050 * multiplier2050;

            if (inputs.scale === 'population') {
                yValue2050 = yValue2050 / 1000000;
            }

            const y2050 = p.height - padding - (yValue2050 / maxValue) * plotHeight;
            p.stroke(100);
            p.strokeWeight(2);
            p.setLineDash([5, 5]);
            p.line(padding, y2050, p.width - padding, y2050);
            p.setLineDash([]);
            p.fill(100);
            p.textAlign(p.LEFT, p.CENTER);
            p.text(`CO₂ emissions until 2050 = ${yValue2050.toFixed(1)} tons CO₂`, p.width - padding + 10, y2050);
        }

        function drawCostsPlot(p, inputs, data) {
            const padding = 60;
            const plotWidth = p.width - 2 * padding;
            const plotHeight = p.height - 2 * padding;

            // Find max cost
            let maxCost = 0;
            data.forEach(d => {
                maxCost = Math.max(maxCost, d.annual_cost);
            });

            // Draw axes
            p.stroke(0);
            p.strokeWeight(2);
            p.line(padding, padding, padding, p.height - padding);
            p.line(padding, p.height - padding, p.width - padding, p.height - padding);

            // X-axis labels
            p.textAlign(p.CENTER, p.TOP);
            p.textSize(12);
            p.fill(0);
            for (let year = 2020; year <= 2050; year += 5) {
                const x = padding + (year - 2020) * plotWidth / 30;
                p.line(x, p.height - padding, x, p.height - padding + 5);
                p.text(year.toString(), x, p.height - padding + 10);
            }

            // Y-axis labels
            p.textAlign(p.RIGHT, p.CENTER);
            const ySteps = 5;
            for (let i = 0; i <= ySteps; i++) {
                const y = p.height - padding - (i * plotHeight / ySteps);
                const value = (i * maxCost / ySteps).toFixed(0);
                p.text(`$${value}`, padding - 10, y);
                p.stroke(200);
                p.strokeWeight(1);
                p.line(padding, y, p.width - padding, y);
            }

            // Y-axis title
            p.push();
            p.translate(20, p.height / 2);
            p.rotate(-p.PI / 2);
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(14);
            p.text('annual personal compensation costs ($)', 0, 0);
            p.pop();

            // Draw costs area
            p.fill(100, 149, 237, 100);
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.annual_cost / maxCost) * plotHeight;
                p.vertex(x, y);
            });
            p.vertex(p.width - padding, p.height - padding);
            p.vertex(padding, p.height - padding);
            p.endShape(p.CLOSE);

            // Draw costs line
            p.stroke(100, 149, 237);
            p.strokeWeight(3);
            p.noFill();
            p.beginShape();
            data.forEach((d, i) => {
                const x = padding + i * plotWidth / 30;
                const y = p.height - padding - (d.annual_cost / maxCost) * plotHeight;
                p.vertex(x, y);
            });
            p.endShape();

            // Draw average cost line
            const total = totalCosts(inputs);
            const avgAnnual = total / 31;
            const yAvg = p.height - padding - (avgAnnual / maxCost) * plotHeight;
            p.stroke(100);
            p.strokeWeight(2);
            p.setLineDash([5, 5]);
            p.line(padding, yAvg, p.width - padding, yAvg);
            p.setLineDash([]);
            p.fill(100);
            p.textAlign(p.LEFT, p.CENTER);
            p.text(`annual personal compensation costs (average) = $${avgAnnual.toFixed(0)} per year`, 
                   p.width - padding + 10, yAvg);

            // Draw total costs label
            p.textAlign(p.CENTER, p.TOP);
            p.text(`total personal compensation costs (until 2050) = $${total.toFixed(0)}`, 
                   p.width / 2, padding + 10);
        }

        // Budget plot sketch
        const budgetPlotSketch = (p) => {
            p.setup = () => {
                const container = document.getElementById('budget-plot-container');
                p.createCanvas(container.offsetWidth, container.offsetHeight);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);
                const inputs = getInputs();
                const years = [2020, 2030, 2040, 2050];
                const circleSpacing = p.width / 5;

                years.forEach((year, index) => {
                    const x = circleSpacing * (index + 1);
                    const y = p.height / 2;

                    // Calculate budget value
                    const totalBudget = 30 * inputs.annual_budget;
                    const cumulative = cumulativeEffective(year, inputs);
                    const budgetValue = totalBudget - cumulative;
                    const effective = effectiveEmissionsPerson(year, inputs);

                    // Calculate radius
                    const maxRadius = 60;
                    const C = maxRadius / Math.sqrt(30 * 5); // Normalization constant
                    const radius = C * Math.sqrt(Math.abs(budgetValue));

                    // Draw circle
                    p.strokeWeight(3);
                    p.noFill();
                    if (effective <= 0) {
                        p.fill(100, 237, 100, 100);
                        p.noStroke();
                    } else {
                        p.stroke(100, 237, 100);
                        p.setLineDash([5, 5]);
                    }

                    p.ellipse(x, y, radius * 2, radius * 2);
                    p.setLineDash([]);

                    // Draw text
                    p.fill(0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(12);
                    p.text(year.toString(), x, y - radius - 15);
                    p.textSize(11);
                    p.text(`${budgetValue.toFixed(1)} tons CO₂`, x, y);

                    // Draw title
                    const title = inputs.scale === 'population' ? 
                        'population climate budget' : 
                        'individual climate budget';
                    if (index === 0) {
                        p.textAlign(p.LEFT, p.TOP);
                        p.textSize(14);
                        p.fill(100);
                        p.text(title, 10, 10);
                    }
                });
            };

            p.windowResized = () => {
                const container = document.getElementById('budget-plot-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                p.redraw();
            };
        };

        // Update function
        function update() {
            if (mainSketch) mainSketch.redraw();
            if (budgetSketch) budgetSketch.redraw();
        }

        // Event listeners
        function setupEventListeners() {
            // Scale radios
            controls.scale_pop.addEventListener('change', () => {
                controls.slider_pop.disabled = true;
                update();
            });
            controls.scale_ind.addEventListener('change', () => {
                controls.slider_pop.disabled = false;
                update();
            });

            // Type radios
            controls.type_emissions.addEventListener('change', update);
            controls.type_costs.addEventListener('change', update);

            // Sliders
            const sliders = [
                controls.slider_pop,
                controls.slider_annual_emissions,
                controls.slider_reduction_time,
                controls.slider_annual_budget,
                controls.slider_start_year,
                controls.slider_compensation_cost,
                controls.slider_fractional_compensation,
                controls.slider_constant_compensation
            ];

            const valueDisplays = [
                controls.value_pop,
                controls.value_annual_emissions,
                controls.value_reduction_time,
                controls.value_annual_budget,
                controls.value_start_year,
                controls.value_compensation_cost,
                controls.value_fractional_compensation,
                controls.value_constant_compensation
            ];

            sliders.forEach((slider, index) => {
                slider.addEventListener('input', () => {
                    valueDisplays[index].textContent = slider.value;
                    update();
                });
            });

            // Climate neutral buttons
            neutralButtons.now.addEventListener('click', () => {
                setNeutralYear('now');
            });
            neutralButtons[2030].addEventListener('click', () => {
                setNeutralYear(2030);
            });
            neutralButtons[2040].addEventListener('click', () => {
                setNeutralYear(2040);
            });
            neutralButtons[2050].addEventListener('click', () => {
                setNeutralYear(2050);
            });
            neutralButtons.never.addEventListener('click', () => {
                setNeutralYear('never');
            });
        }

        function setNeutralYear(year) {
            neutral_year = year;

            // Update button states
            Object.values(neutralButtons).forEach(button => {
                button.classList.remove('active');
            });

            if (year === 'now') {
                neutralButtons.now.classList.add('active');
                neutral_year = 2020;
            } else if (year === 'never') {
                neutralButtons.never.classList.add('active');
            } else {
                neutralButtons[year].classList.add('active');
            }

            update();
        }

        // Initialize
        document.addEventListener('DOMContentLoaded', () => {
            mainSketch = new p5(mainPlotSketch, 'main-plot-container');
            budgetSketch = new p5(budgetPlotSketch, 'budget-plot-container');
            setupEventListeners();
            update();
        });
    </script>
</body>
</html>
