</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="solid-selector">
                <button id="btn-solid-1" class="solid-btn active">1</button>
                <button id="btn-solid-2" class="solid-btn">2</button>
                <button id="btn-solid-3" class="solid-btn">3</button>
            </div>
            <div id="fold-slider-container">
                <label for="slider-fold">fold-unfold</label>
                <input type="range" id="slider-fold" min="0" max="1" step="0.01" value="0">
            </div>
            <button id="btn-reset-view">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Three.js setup
        let scene, camera, renderer, controls;
        let currentSolid = null;
        let currentSolidNumber = 1;

        // Initialize Three.js scene
        function initScene() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            const aspect = window.innerWidth / (window.innerHeight - 80);
            camera = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight - 80);
            renderer.shadowMap.enabled = true;
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;
            controls.screenSpacePanning = false;
            controls.minDistance = 2;
            controls.maxDistance = 20;
            controls.maxPolarAngle = Math.PI / 2;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 10, 5);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            const aspect = window.innerWidth / (window.innerHeight - 80);
            camera.aspect = aspect;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight - 80);
        }

        // Solid definitions
        const solidDefinitions = {
            1: {
                name: "Hexahedron",
                faces: [
                    // Left side triangles
                    { vertices: [[0, 1], [-1, 0], [0, -1]], color: 0xE47A55, pivotEdge: { start: [0, 1], end: [-1, 0] } },
                    { vertices: [[-1, 0], [-2, 1], [-1, 2]], color: 0xA4C6E3, pivotEdge: { start: [-1, 0], end: [-2, 1] } },
                    { vertices: [[-2, 1], [-3, 0], [-2, -1]], color: 0xF4E0E1, pivotEdge: { start: [-2, 1], end: [-3, 0] } },
                    // Right side triangles
                    { vertices: [[0, 1], [1, 0], [0, -1]], color: 0xF3C76E, pivotEdge: { start: [0, 1], end: [1, 0] } },
                    { vertices: [[1, 0], [2, 1], [1, 2]], color: 0xE47A55, pivotEdge: { start: [1, 0], end: [2, 1] } },
                    { vertices: [[2, 1], [3, 0], [2, -1]], color: 0xA4C6E3, pivotEdge: { start: [2, 1], end: [3, 0] } }
                ],
                foldAngle: Math.PI * 0.7
            },
            2: {
                name: "Octahedron",
                faces: [
                    // Central square split into triangles
                    { vertices: [[0, 0], [2, 0], [1, 1]], color: 0xE47A55, pivotEdge: { start: [0, 0], end: [2, 0] } },
                    { vertices: [[2, 0], [0, 0], [1, 1]], color: 0xA4C6E3, pivotEdge: { start: [2, 0], end: [0, 0] } },
                    // Side triangles
                    { vertices: [[0, 0], [-1, 1], [-1, -1]], color: 0xF4E0E1, pivotEdge: { start: [0, 0], end: [-1, 1] } },
                    { vertices: [[2, 0], [3, 1], [3, -1]], color: 0xF3C76E, pivotEdge: { start: [2, 0], end: [3, 1] } },
                    { vertices: [[1, 1], [0, 2], [2, 2]], color: 0xE47A55, pivotEdge: { start: [1, 1], end: [0, 2] } },
                    { vertices: [[0, 0], [-1, -1], [1, -1]], color: 0xA4C6E3, pivotEdge: { start: [0, 0], end: [-1, -1] } },
                    { vertices: [[2, 0], [3, -1], [1, -1]], color: 0xF4E0E1, pivotEdge: { start: [2, 0], end: [3, -1] } },
                    { vertices: [[1, 1], [2, 2], [0, 2]], color: 0xF3C76E, pivotEdge: { start: [1, 1], end: [2, 2] } }
                ],
                foldAngle: Math.PI * 0.8
            },
            3: {
                name: "Tetrahedron",
                faces: [
                    // Base triangle
                    { vertices: [[0, 0], [2, 0], [1, 1.73]], color: 0xE47A55, pivotEdge: { start: [0, 0], end: [2, 0] } },
                    // Attached triangles
                    { vertices: [[0, 0], [-1, 0.87], [0, 1.73]], color: 0xA4C6E3, pivotEdge: { start: [0, 0], end: [-1, 0.87] } },
                    { vertices: [[2, 0], [3, 0.87], [2, 1.73]], color: 0xF4E0E1, pivotEdge: { start: [2, 0], end: [3, 0.87] } },
                    { vertices: [[1, 1.73], [0, 3.46], [2, 3.46]], color: 0xF3C76E, pivotEdge: { start: [1, 1.73], end: [0, 3.46] } }
                ],
                foldAngle: Math.PI * 0.9
            }
        };

        // Create solid geometry
        function createSolid(solidNumber) {
            const definition = solidDefinitions[solidNumber];
            const group = new THREE.Group();
            const faces = [];

            // Base face
            const baseFace = createFace(definition.faces[0]);
            group.add(baseFace);
            faces.push({ mesh: baseFace, pivot: null });

            // Other faces with pivots
            for (let i = 1; i < definition.faces.length; i++) {
                const face = definition.faces[i];
                const mesh = createFace(face);

                // Create pivot
                const pivot = new THREE.Object3D();

                // Position pivot at the hinge edge
                const edge = face.pivotEdge;
                const edgeCenter = [
                    (edge.start[0] + edge.end[0]) / 2,
                    (edge.start[1] + edge.end[1]) / 2
                ];
                pivot.position.set(edgeCenter[0], edgeCenter[1], 0);

                // Calculate rotation axis (perpendicular to the edge in 2D)
                const edgeVec = new THREE.Vector3(edge.end[0] - edge.start[0], edge.end[1] - edge.start[1], 0);
                const axis = new THREE.Vector3(0, 0, 1).cross(edgeVec).normalize();
                pivot.up.copy(axis);

                // Add mesh to pivot
                mesh.position.sub(pivot.position);
                pivot.add(mesh);

                // Find parent face (the one sharing the edge)
                let parent = null;
                for (let j = 0; j < i; j++) {
                    if (sharesEdge(face, definition.faces[j])) {
                        parent = faces[j];
                        break;
                    }
                }

                if (parent) {
                    if (parent.pivot) {
                        parent.pivot.add(pivot);
                    } else {
                        parent.mesh.add(pivot);
                    }
                } else {
                    group.add(pivot);
                }

                faces.push({ mesh, pivot });
            }

            return { group, faces, definition };
        }

        function createFace(faceDef) {
            const vertices = faceDef.vertices.map(v => new THREE.Vector3(v[0], v[1], 0));
            const geometry = new THREE.BufferGeometry().setFromPoints(vertices);

            // Set indices for triangles
            const indices = [0, 1, 2];
            geometry.setIndex(indices);
            geometry.computeVertexNormals();

            const unfoldedColor = new THREE.Color(0xF5E0E0);
            const foldedColor = new THREE.Color(faceDef.color);

            const material = new THREE.MeshStandardMaterial({
                color: unfoldedColor,
                side: THREE.DoubleSide,
                flatShading: true
            });

            const mesh = new THREE.Mesh(geometry, material);
            mesh.userData = { unfoldedColor, foldedColor };

            return mesh;
        }

        function sharesEdge(face1, face2) {
            // Simple edge sharing check (for demo purposes)
            const edge1 = face1.pivotEdge;
            for (let i = 0; i < face2.vertices.length - 1; i++) {
                const v1 = face2.vertices[i];
                const v2 = face2.vertices[i + 1];

                if ((v1[0] === edge1.start[0] && v1[1] === edge1.start[1] && 
                     v2[0] === edge1.end[0] && v2[1] === edge1.end[1]) ||
                    (v1[0] === edge1.end[0] && v1[1] === edge1.end[1] && 
                     v2[0] === edge1.start[0] && v2[1] === edge1.start[1])) {
                    return true;
                }
            }
            return false;
        }

        // Update folding
        function updateFolding(t) {
            if (!currentSolid) return;

            currentSolid.faces.forEach((face, index) => {
                if (index === 0) return; // Skip base face

                const pivot = face.pivot;
                if (pivot) {
                    // Rotate pivot
                    pivot.rotation.z = -t * currentSolid.definition.foldAngle;
                }

                // Interpolate color
                const { unfoldedColor, foldedColor } = face.mesh.userData;
                face.mesh.material.color.lerpColors(unfoldedColor, foldedColor, t);
            });
        }

        // Event listeners
        document.getElementById('btn-solid-1').addEventListener('click', function() {
            switchSolid(1);
        });

        document.getElementById('btn-solid-2').addEventListener('click', function() {
            switchSolid(2);
        });

        document.getElementById('btn-solid-3').addEventListener('click', function() {
            switchSolid(3);
        });

        document.getElementById('slider-fold').addEventListener('input', function(e) {
            updateFolding(parseFloat(e.target.value));
        });

        document.getElementById('btn-reset-view').addEventListener('click', function() {
            // Reset camera position
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);
            controls.reset();
        });

        function switchSolid(solidNumber) {
            // Update button states
            document.querySelectorAll('.solid-btn').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-solid-${solidNumber}`).classList.add('active');

            // Remove current solid
            if (currentSolid) {
                scene.remove(currentSolid.group);
            }

            // Create new solid
            currentSolid = createSolid(solidNumber);
            scene.add(currentSolid.group);
            currentSolidNumber = solidNumber;

            // Apply current slider value
            const sliderValue = parseFloat(document.getElementById('slider-fold').value);
            updateFolding(sliderValue);

            // Reset view
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);
            controls.reset();
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        initScene();
        switchSolid(1);
        animate();
    </script>
</body>
</html>
