<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>relative volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global variables
        let sliderAC, sliderBC;
        let valueAC, valueBC;
        let btnReset;
        let canvas;
        let alpha13, alpha23;

        // P5.js sketch variables
        let triangleSize = 400;
        let padding = 50;
        let vertexA, vertexB, vertexC;

        function setup() {
            // Initialize p5.js canvas
            canvas = createCanvas(600, 550);
            canvas.parent('ternary-plot-canvas');

            // Set triangle vertices
            vertexA = createVector(width / 2, padding);
            vertexB = createVector(padding, height - padding);
            vertexC = createVector(width - padding, height - padding);

            // Get DOM elements
            sliderAC = document.getElementById('slider-ac');
            sliderBC = document.getElementById('slider-bc');
            valueAC = document.getElementById('value-ac');
            valueBC = document.getElementById('value-bc');
            btnReset = document.getElementById('btn-reset');

            // Set initial alpha values
            alpha13 = parseFloat(sliderAC.value);
            alpha23 = parseFloat(sliderBC.value);

            // Add event listeners
            sliderAC.addEventListener('input', handleSliderACChange);
            sliderBC.addEventListener('input', handleSliderBCChange);
            btnReset.addEventListener('click', handleResetClick);

            // Initial draw
            drawTernaryPlot();
        }

        function draw() {
            // Static canvas, no animation loop needed
        }

        function handleSliderACChange() {
            alpha13 = parseFloat(sliderAC.value);
            valueAC.textContent = alpha13.toFixed(2);
            drawTernaryPlot();
        }

        function handleSliderBCChange() {
            alpha23 = parseFloat(sliderBC.value);
            valueBC.textContent = alpha23.toFixed(2);
            drawTernaryPlot();
        }

        function handleResetClick() {
            // Reset sliders
            sliderAC.value = 0.6;
            sliderBC.value = 3.93;

            // Update values
            alpha13 = 0.6;
            alpha23 = 3.93;
            valueAC.textContent = '0.60';
            valueBC.textContent = '3.93';

            // Redraw
            drawTernaryPlot();
        }

        function drawTernaryPlot() {
            // Clear canvas
            background(255);

            // Draw grid of triangles
            let N = 10;

            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (i + j >= N) continue;

                    // Calculate barycentric coordinates for small triangle center
                    let xA = (i / N);
                    let xB = (j / N);
                    let xC = 1 - xA - xB;

                    // Shift to triangle center
                    let centerX = xA + 0.5 / N;
                    let centerY = xB + 0.5 / N;
                    let centerZ = 1 - centerX - centerY;

                    // Calculate function F at center
                    let F = calculateF(centerX, centerY, centerZ, alpha13, alpha23);

                    // Normalize F to [0, 1]
                    let v = F / 2.0;

                    // Get color from gradient
                    let col = getColorFromGradient(v);

                    // Get triangle vertices in cartesian coordinates
                    let v1 = baryToCartesian(i / N, j / N);
                    let v2 = baryToCartesian((i + 1) / N, j / N);
                    let v3 = baryToCartesian(i / N, (j + 1) / N);

                    // Draw triangle
                    fill(col);
                    stroke(0);
                    strokeWeight(1);
                    triangle(v1.x, v1.y, v2.x, v2.y, v3.x, v3.y);
                }
            }

            // Draw triangle outline
            noFill();
            stroke(0);
            strokeWeight(2);
            triangle(vertexA.x, vertexA.y, vertexB.x, vertexB.y, vertexC.x, vertexC.y);

            // Draw labels and ticks
            drawLabels();
        }

        function calculateF(x1, x2, x3, alpha13, alpha23) {
            // Calculate vapor phase mole fractions
            let alpha1 = alpha13;
            let alpha2 = alpha23;
            let alpha3 = 1.0;

            let D = alpha1 * x1 + alpha2 * x2 + alpha3 * x3;
            let y1 = (alpha1 * x1) / D;
            let y2 = (alpha2 * x2) / D;
            let y3 = (alpha3 * x3) / D;

            // Calculate F = sum((xi - yi)^2)
            let F = Math.pow(x1 - y1, 2) + Math.pow(x2 - y2, 2) + Math.pow(x3 - y3, 2);

            return F;
        }

        function baryToCartesian(a, b) {
            let x = vertexA.x * a + vertexB.x * b + vertexC.x * (1 - a - b);
            let y = vertexA.y * a + vertexB.y * b + vertexC.y * (1 - a - b);
            return createVector(x, y);
        }

        function getColorFromGradient(v) {
            v = constrain(v, 0, 1);

            // Color stops
            let colors = [
                { pos: 0.0, r: 44, g: 123, b: 182 },   // #2c7bb6
                { pos: 0.25, r: 171, g: 217, b: 233 }, // #abd9e9
                { pos: 0.5, r: 255, g: 255, b: 191 },  // #ffffbf
                { pos: 0.75, r: 253, g: 174, b: 97 },  // #fdae61
                { pos: 1.0, r: 215, g: 25, b: 28 }     // #d7191c
            ];

            // Find the two surrounding colors
            let c1, c2;
            for (let i = 0; i < colors.length - 1; i++) {
                if (v >= colors[i].pos && v <= colors[i + 1].pos) {
                    c1 = colors[i];
                    c2 = colors[i + 1];
                    break;
                }
            }

            // Interpolate
            let t = (v - c1.pos) / (c2.pos - c1.pos);
            let r = Math.round(c1.r + t * (c2.r - c1.r));
            let g = Math.round(c1.g + t * (c2.g - c1.g));
            let b = Math.round(c1.b + t * (c2.b - c1.b));

            return color(r, g, b);
        }

        function drawLabels() {
            // Draw vertex labels
            push();
            textAlign(CENTER, CENTER);
            textSize(20);
            fill(0);
            noStroke();
            text('A', vertexA.x, vertexA.y - 15);
            text('B', vertexB.x - 15, vertexB.y + 15);
            text('C', vertexC.x + 15, vertexC.y + 15);
            pop();

            // Draw axis ticks
            drawAxisTicks();
        }

        function drawAxisTicks() {
            push();
            textAlign(CENTER, CENTER);
            textSize(12);
            fill(0);
            noStroke();

            let Nticks = 11; // 0.0 to 1.0 in steps of 0.1

            // A-B side (left side)
            for (let i = 0; i < Nticks; i++) {
                let t = i / (Nticks - 1);
                let v = baryToCartesian(1 - t, t);
                if (i % 2 === 0) { // Show every other tick for clarity
                    text((1 - t).toFixed(1), v.x - 20, v.y);
                }
                stroke(0);
                strokeWeight(1);
                line(v.x - 5, v.y, v.x, v.y);
            }

            // B-C side (bottom)
            for (let i = 0; i < Nticks; i++) {
                let t = i / (Nticks - 1);
                let v = baryToCartesian(0, 1 - t);
                let x = vertexB.x + t * (vertexC.x - vertexB.x);
                let y = vertexB.y + t * (vertexC.y - vertexB.y);
                if (i % 2 === 0) {
                    text((1 - t).toFixed(1), x, y + 15);
                }
                stroke(0);
                strokeWeight(1);
                line(x, y, x, y + 5);
            }

            // C-A side (right side)
            for (let i = 0; i < Nticks; i++) {
                let t = i / (Nticks - 1);
                let v = baryToCartesian(t, 0);
                let x = vertexC.x + t * (vertexA.x - vertexC.x);
                let y = vertexC.y + t * (vertexA.y - vertexC.y);
                if (i % 2 === 0) {
                    text(t.toFixed(1), x + 20, y);
                }
                stroke(0);
                strokeWeight(1);
                line(x, y, x + 5, y);
            }

            pop();
        }
    </script>
</body>
</html>
