<body>
    <h1>Band Structure of a Quantum Wire with Rashba and Zeeman Interactions</h1>

    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-wso">Rashba spin-orbit interaction strength (wso)</label>
                <input type="range" id="slider-wso" min="0" max="2" step="0.1" value="0">
                <span id="value-wso" class="value-display">0.0</span>
            </div>

            <hr>

            <h2>magnetic field</h2>

            <div class="control-group">
                <label for="slider-n">subband (n)</label>
                <input type="range" id="slider-n" min="0" max="10" step="1" value="0">
                <span id="value-n" class="value-display">0</span>
            </div>

            <h3>strengths</h3>

            <div class="control-group">
                <label for="slider-bx">x direction (BX)</label>
                <input type="range" id="slider-bx" min="0" max="2" step="0.1" value="0">
                <span id="value-bx" class="value-display">0.0</span>
            </div>

            <div class="control-group">
                <label for="slider-by">y direction (BY)</label>
                <input type="range" id="slider-by" min="0" max="2" step="0.1" value="0">
                <span id="value-by" class="value-display">0.0</span>
            </div>

            <div class="control-group">
                <label for="slider-bz">z direction (BZ)</label>
                <input type="range" id="slider-bz" min="0" max="2" step="0.1" value="0">
                <span id="value-bz" class="value-display">0.0</span>
            </div>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        // Get all slider and value elements
        const sliders = {
            wso: document.getElementById('slider-wso'),
            n: document.getElementById('slider-n'),
            bx: document.getElementById('slider-bx'),
            by: document.getElementById('slider-by'),
            bz: document.getElementById('slider-bz')
        };

        const values = {
            wso: document.getElementById('value-wso'),
            n: document.getElementById('value-n'),
            bx: document.getElementById('value-bx'),
            by: document.getElementById('value-by'),
            bz: document.getElementById('value-bz')
        };

        // Add event listeners to all sliders
        Object.keys(sliders).forEach(key => {
            sliders[key].addEventListener('input', function() {
                // Update the displayed value
                if (key === 'n') {
                    values[key].textContent = this.value;
                } else {
                    values[key].textContent = parseFloat(this.value).toFixed(1);
                }
                // Update the plot
                updatePlot();
            });
        });

        function updatePlot() {
            // Get current slider values
            const n = parseInt(sliders.n.value);
            const wso = parseFloat(sliders.wso.value);
            const bx = parseFloat(sliders.bx.value);
            const by = parseFloat(sliders.by.value);
            const bz = parseFloat(sliders.bz.value);

            // Generate k values
            const kValues = [];
            for (let k = -5; k <= 5; k += 0.1) {
                kValues.push(k);
            }

            // Calculate energy bands
            const ePlus = [];
            const eMinus = [];

            kValues.forEach(k => {
                const baseEnergy = (2 * n + 1) + k * k;
                const sqrtTerm = Math.sqrt(Math.pow(bz + wso * k, 2) + bx * bx + by * by);

                ePlus.push(baseEnergy + sqrtTerm);
                eMinus.push(baseEnergy - sqrtTerm);
            });

            // Prepare traces
            const traces = [];

            // Check if we need one or two traces
            const allZero = wso === 0 && bx === 0 && by === 0 && bz === 0;

            if (allZero) {
                // Single trace for degenerate state
                traces.push({
                    x: kValues,
                    y: ePlus,
                    mode: 'lines',
                    line: {
                        color: 'red',
                        dash: 'solid',
                        width: 2
                    },
                    name: 'E'
                });
            } else {
                // Two traces for split bands
                traces.push({
                    x: kValues,
                    y: ePlus,
                    mode: 'lines',
                    line: {
                        color: 'red',
                        dash: 'solid',
                        width: 2
                    },
                    name: 'E+'
                });

                traces.push({
                    x: kValues,
                    y: eMinus,
                    mode: 'lines',
                    line: {
                        color: 'red',
                        dash: 'dash',
                        width: 2
                    },
                    name: 'E-'
                });
            }

            // Generate dynamic title
            const titleParts = [`n = ${n}`];
            if (bx !== 0) titleParts.push(`BX = ${bx.toFixed(1)}`);
            if (by !== 0) titleParts.push(`BY = ${by.toFixed(1)}`);
            if (bz !== 0) titleParts.push(`BZ = ${bz.toFixed(1)}`);
            if (wso !== 0) titleParts.push(`wso = ${wso.toFixed(1)}`);

            const title = titleParts.join(', ');

            // Layout configuration
            const layout = {
                title: {
                    text: title,
                    font: {
                        size: 16
                    }
                },
                xaxis: {
                    title: 'normalized k length (lwky)',
                    range: [-5, 5],
                    zeroline: true,
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: '2 E / &#x210F;&omega;',
                    range: [0, 40],
                    zeroline: true,
                    gridcolor: '#e0e0e0'
                },
                margin: {
                    l: 60,
                    r: 20,
                    t: 50,
                    b: 50
                },
                showlegend: !allZero
            };

            // Update or create the plot
            Plotly.react('plot-container', traces, layout);
        }

        // Initialize the plot on page load
        updatePlot();
    </script>
</body>
</html>
