        <div id="plots-container">
            <div class="plot-wrapper">
                <div id="plot-canvas-1"></div>
            </div>
            <div class="plot-wrapper">
                <div id="plot-canvas-2"></div>
            </div>
            <div class="plot-wrapper">
                <div id="plot-canvas-3"></div>
            </div>
            <div class="plot-wrapper">
                <div id="plot-canvas-4"></div>
            </div>
            <div class="plot-wrapper">
                <div id="plot-canvas-5"></div>
            </div>
            <div class="plot-wrapper">
                <div id="plot-canvas-6"></div>
            </div>
        </div>
    </div>

    <script>
        // Global variables for data
        let P = [1, 0, 0]; // Precipitation values
        let U = [1, 5, 3, 1]; // Unit Hydrograph values
        let C1 = [], C2 = [], C3 = []; // Component hydrographs
        let Q = []; // Composite hydrograph

        // Get all sliders and value displays
        const sliders = {
            precip1: document.getElementById('slider-precip-1'),
            precip2: document.getElementById('slider-precip-2'),
            precip3: document.getElementById('slider-precip-3'),
            uh1: document.getElementById('slider-uh-1'),
            uh2: document.getElementById('slider-uh-2'),
            uh3: document.getElementById('slider-uh-3'),
            uh4: document.getElementById('slider-uh-4')
        };

        const valueDisplays = {
            precip1: document.getElementById('value-precip-1'),
            precip2: document.getElementById('value-precip-2'),
            precip3: document.getElementById('value-precip-3'),
            uh1: document.getElementById('value-uh-1'),
            uh2: document.getElementById('value-uh-2'),
            uh3: document.getElementById('value-uh-3'),
            uh4: document.getElementById('value-uh-4')
        };

        // Toggle button functionality
        const toggleButton = document.getElementById('toggle-controls');
        const slidersArea = document.getElementById('sliders-area');
        let controlsVisible = true;

        toggleButton.addEventListener('click', () => {
            controlsVisible = !controlsVisible;
            slidersArea.style.display = controlsVisible ? 'flex' : 'none';
            toggleButton.textContent = controlsVisible ? '+' : '-';
        });

        // Add event listeners to all sliders
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', update);
        });

        // Update function reads values and recalculates
        function update() {
            // Update value displays
            valueDisplays.precip1.textContent = sliders.precip1.value;
            valueDisplays.precip2.textContent = sliders.precip2.value;
            valueDisplays.precip3.textContent = sliders.precip3.value;
            valueDisplays.uh1.textContent = sliders.uh1.value;
            valueDisplays.uh2.textContent = sliders.uh2.value;
            valueDisplays.uh3.textContent = sliders.uh3.value;
            valueDisplays.uh4.textContent = sliders.uh4.value;

            // Read slider values
            P = [parseInt(sliders.precip1.value), 
                 parseInt(sliders.precip2.value), 
                 parseInt(sliders.precip3.value)];
            U = [parseInt(sliders.uh1.value), 
                 parseInt(sliders.uh2.value), 
                 parseInt(sliders.uh3.value), 
                 parseInt(sliders.uh4.value)];

            // Calculate component hydrographs
            C1 = U.map(u => P[0] * u);
            C2 = U.map(u => P[1] * u);
            C3 = U.map(u => P[2] * u);

            // Calculate composite hydrograph using convolution
            Q = [
                P[0] * U[0],
                P[0] * U[1] + P[1] * U[0],
                P[0] * U[2] + P[1] * U[1] + P[2] * U[0],
                P[0] * U[3] + P[1] * U[2] + P[2] * U[1],
                P[1] * U[3] + P[2] * U[2],
                P[2] * U[3]
            ];
        }

        // Draw plot function
        function drawPlot(p5Instance, data, xLabel, yLabel, xMax, yMax, timeOffset = 0) {
            const padding = 40;
            const plotWidth = p5Instance.width - 2 * padding;
            const plotHeight = p5Instance.height - 2 * padding;

            // Clear canvas
            p5Instance.background(255);

            // Draw axes
            p5Instance.stroke(0);
            p5Instance.strokeWeight(2);
            p5Instance.line(padding, padding, padding, p5Instance.height - padding); // Y-axis
            p5Instance.line(padding, p5Instance.height - padding, p5Instance.width - padding, p5Instance.height - padding); // X-axis

            // Draw step chart
            if (data.length > 0) {
                p5Instance.fill('#d4e6f1');
                p5Instance.stroke('#2e86c1');
                p5Instance.strokeWeight(2);

                p5Instance.beginShape();
                p5Instance.vertex(padding, p5Instance.height - padding);

                for (let i = 0; i < data.length; i++) {
                    const x = padding + (i + timeOffset) * (plotWidth / xMax);
                    const y = p5Instance.height - padding - (data[i] / yMax) * plotHeight;
                    p5Instance.vertex(x, y);
                    p5Instance.vertex(x + (plotWidth / xMax), y);
                }

                p5Instance.vertex(padding + (data.length + timeOffset) * (plotWidth / xMax), p5Instance.height - padding);
                p5Instance.endShape(p5Instance.CLOSE);

                // Draw ticks and labels for X-axis
                p5Instance.stroke(0);
                p5Instance.strokeWeight(1);
                p5Instance.textAlign(p5Instance.CENTER, p5Instance.TOP);
                for (let i = 0; i <= xMax; i++) {
                    const x = padding + (i * plotWidth / xMax);
                    p5Instance.line(x, p5Instance.height - padding, x, p5Instance.height - padding + 5);
                    if (i % 2 === 0 || xMax <= 5) {
                        p5Instance.text(i.toString(), x, p5Instance.height - padding + 10);
                    }
                }

                // Draw ticks and labels for Y-axis
                p5Instance.textAlign(p5Instance.RIGHT, p5Instance.CENTER);
                for (let i = 0; i <= yMax; i += Math.ceil(yMax / 5)) {
                    const y = p5Instance.height - padding - (i / yMax) * plotHeight;
                    p5Instance.line(padding - 5, y, padding, y);
                    p5Instance.text(i.toString(), padding - 10, y);
                }

                // Draw axis labels
                p5Instance.textAlign(p5Instance.CENTER, p5Instance.BOTTOM);
                p5Instance.text(xLabel, p5Instance.width / 2, p5Instance.height - 5);
                p5Instance.push();
                p5Instance.translate(10, p5Instance.height / 2);
                p5Instance.rotate(-p5Instance.HALF_PI);
                p5Instance.text(yLabel, 0, 0);
                p5Instance.pop();
            }
        }

        // p5.js sketch for Plot 1
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(300, 250);
                canvas.parent('plot-canvas-1');
                update();
            };

            p.draw = () => {
                drawPlot(p, P, 't (hr)', 'P (in)', 5, 10);
            };
        });

        // p5.js sketch for Plot 2
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(300, 250);
                canvas.parent('plot-canvas-2');
            };

            p.draw = () => {
                drawPlot(p, U, 't (hr)', 'Q (cfs/in)', 5, 10);
            };
        });

        // p5.js sketch for Plot 3
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(300, 250);
                canvas.parent('plot-canvas-3');
            };

            p.draw = () => {
                drawPlot(p, C1, 't (hr)', 'Q (cfs)', 8, 100, 0);
            };
        });

        // p5.js sketch for Plot 4
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(300, 250);
                canvas.parent('plot-canvas-4');
            };

            p.draw = () => {
                drawPlot(p, C2, 't (hr)', 'Q (cfs)', 8, 100, 1);
            };
        });

        // p5.js sketch for Plot 5
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(300, 250);
                canvas.parent('plot-canvas-5');
            };

            p.draw = () => {
                drawPlot(p, C3, 't (hr)', 'Q (cfs)', 8, 100, 2);
            };
        });

        // p5.js sketch for Plot 6
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(300, 250);
                canvas.parent('plot-canvas-6');
            };

            p.draw = () => {
                drawPlot(p, Q, 't (hr)', 'Q (cfs)', 8, 100, 0);
            };
        });
    </script>
</body>
</html>
