    <script>
        // Global variables
        let matrixA = {a11: -5, a12: -3, a21: -1, a22: -4};
        let matrixB = {b11: -5, b12: -3, b21: -1, b22: -4};
        let nashVertices = [];

        // Setup event listeners
        function setupEventListeners() {
            const sliders = ['a11', 'a12', 'a21', 'a22', 'b11', 'b12', 'b21', 'b22'];

            sliders.forEach(id => {
                const slider = document.getElementById(`slider-${id}`);
                const valueDisplay = document.getElementById(`value-${id}`);
                const minusBtn = document.getElementById(`btn-minus-${id}`);
                const plusBtn = document.getElementById(`btn-plus-${id}`);

                slider.addEventListener('input', function() {
                    valueDisplay.textContent = this.value;
                    updateMatrix();
                });

                minusBtn.addEventListener('click', function() {
                    slider.value = parseInt(slider.value) - 1;
                    valueDisplay.textContent = slider.value;
                    updateMatrix();
                });

                plusBtn.addEventListener('click', function() {
                    slider.value = parseInt(slider.value) + 1;
                    valueDisplay.textContent = slider.value;
                    updateMatrix();
                });
            });
        }

        // Update matrix values and refresh display
        function updateMatrix() {
            matrixA = {
                a11: parseInt(document.getElementById('slider-a11').value),
                a12: parseInt(document.getElementById('slider-a12').value),
                a21: parseInt(document.getElementById('slider-a21').value),
                a22: parseInt(document.getElementById('slider-a22').value)
            };

            matrixB = {
                b11: parseInt(document.getElementById('slider-b11').value),
                b12: parseInt(document.getElementById('slider-b12').value),
                b21: parseInt(document.getElementById('slider-b21').value),
                b22: parseInt(document.getElementById('slider-b22').value)
            };

            updatePayoffTable();
            if (window.p5Instance) {
                window.p5Instance.redraw();
            }
        }

        // Update payoff table display
        function updatePayoffTable() {
            document.getElementById('cell-a11-b11').textContent = `${matrixA.a11}, ${matrixB.b11}`;
            document.getElementById('cell-a12-b12').textContent = `${matrixA.a12}, ${matrixB.b12}`;
            document.getElementById('cell-a21-b21').textContent = `${matrixA.a21}, ${matrixB.b21}`;
            document.getElementById('cell-a22-b22').textContent = `${matrixA.a22}, ${matrixB.b22}`;
        }

        // Convert decimal to fraction
        function toFraction(decimal, tolerance = 1e-6) {
            if (Math.abs(decimal) < tolerance) return '0';

            let numerator = 1;
            let denominator = 1;
            let minDiff = Infinity;

            for (let d = 1; d <= 20; d++) {
                let n = Math.round(decimal * d);
                let diff = Math.abs(decimal - n / d);

                if (diff < minDiff && diff < tolerance) {
                    minDiff = diff;
                    numerator = n;
                    denominator = d;
                }
            }

            if (denominator === 1) return numerator.toString();
            if (numerator === denominator) return '1';
            if (numerator === -denominator) return '-1';
            if (numerator === 0) return '0';

            return `${numerator}/${denominator}`;
        }

        // Calculate best response for Player 1
        function getPlayer1BestResponse() {
            const den1 = matrixA.a11 - matrixA.a12 - matrixA.a21 + matrixA.a22;
            let segments = [];

            if (Math.abs(den1) < 1e-6) {
                // Dominant strategy case
                if (matrixA.a12 - matrixA.a22 > 0) {
                    segments.push([{x: 1, y: 0}, {x: 1, y: 1}]);
                } else {
                    segments.push([{x: 0, y: 0}, {x: 0, y: 1}]);
                }
            } else {
                const y1_indiff = (matrixA.a22 - matrixA.a12) / den1;

                if (y1_indiff > 0 && y1_indiff < 1) {
                    segments.push([{x: 0, y: y1_indiff}, {x: 1, y: y1_indiff}]);

                    if (den1 > 0) {
                        segments.push([{x: 0, y: 0}, {x: 0, y: y1_indiff}]);
                        segments.push([{x: 1, y: y1_indiff}, {x: 1, y: 1}]);
                    } else {
                        segments.push([{x: 0, y: y1_indiff}, {x: 0, y: 1}]);
                        segments.push([{x: 1, y: 0}, {x: 1, y: y1_indiff}]);
                    }
                } else {
                    // Determine dominant strategy
                    let y1_test = 0.5;
                    let expected = matrixA.a11 * y1_test + matrixA.a12 * (1 - y1_test);
                    let actual = matrixA.a21 * y1_test + matrixA.a22 * (1 - y1_test);

                    if (expected > actual) {
                        segments.push([{x: 1, y: 0}, {x: 1, y: 1}]);
                    } else {
                        segments.push([{x: 0, y: 0}, {x: 0, y: 1}]);
                    }
                }
            }

            return segments;
        }

        // Calculate best response for Player 2
        function getPlayer2BestResponse() {
            const den2 = matrixB.b11 - matrixB.b12 - matrixB.b21 + matrixB.b22;
            let segments = [];

            if (Math.abs(den2) < 1e-6) {
                // Dominant strategy case
                if (matrixB.b21 - matrixB.b22 > 0) {
                    segments.push([{x: 0, y: 1}, {x: 1, y: 1}]);
                } else {
                    segments.push([{x: 0, y: 0}, {x: 1, y: 0}]);
                }
            } else {
                const x1_indiff = (matrixB.b22 - matrixB.b21) / den2;

                if (x1_indiff > 0 && x1_indiff < 1) {
                    segments.push([{x: x1_indiff, y: 0}, {x: x1_indiff, y: 1}]);

                    if (den2 > 0) {
                        segments.push([{x: 0, y: 0}, {x: x1_indiff, y: 0}]);
                        segments.push([{x: x1_indiff, y: 1}, {x: 1, y: 1}]);
                    } else {
                        segments.push([{x: x1_indiff, y: 0}, {x: 1, y: 0}]);
                        segments.push([{x: 0, y: 1}, {x: x1_indiff, y: 1}]);
                    }
                } else {
                    // Determine dominant strategy
                    let x1_test = 0.5;
                    let expected = x1_test * matrixB.b11 + (1 - x1_test) * matrixB.b21;
                    let actual = x1_test * matrixB.b12 + (1 - x1_test) * matrixB.b22;

                    if (expected > actual) {
                        segments.push([{x: 0, y: 1}, {x: 1, y: 1}]);
                    } else {
                        segments.push([{x: 0, y: 0}, {x: 1, y: 0}]);
                    }
                }
            }

            return segments;
        }

        // Find intersection points between two sets of segments
        function findIntersections(segments1, segments2) {
            let intersections = [];

            segments1.forEach(seg1 => {
                segments2.forEach(seg2 => {
                    // Check horizontal vs vertical intersection
                    let horizontal, vertical;

                    if (Math.abs(seg1[0].y - seg1[1].y) < 1e-6 && Math.abs(seg2[0].x - seg2[1].x) < 1e-6) {
                        horizontal = seg1;
                        vertical = seg2;
                    } else if (Math.abs(seg2[0].y - seg2[1].y) < 1e-6 && Math.abs(seg1[0].x - seg1[1].x) < 1e-6) {
                        horizontal = seg2;
                        vertical = seg1;
                    } else {
                        return; // No axis-aligned intersection
                    }

                    let y = horizontal[0].y;
                    let x = vertical[0].x;

                    // Check if intersection point lies within both segments
                    let xInRange = Math.min(vertical[0].x, vertical[1].x) - 1e-6 <= x && x <= Math.max(vertical[0].x, vertical[1].x) + 1e-6;
                    let yInRange = Math.min(horizontal[0].y, horizontal[1].y) - 1e-6 <= y && y <= Math.max(horizontal[0].y, horizontal[1].y) + 1e-6;

                    if (xInRange && yInRange) {
                        intersections.push({x: x, y: y});
                    }
                });
            });

            return intersections;
        }

        // Update MathJax display
        function updateEquilibriaDisplay() {
            const display = document.getElementById('equilibria-display');

            if (nashVertices.length === 0) {
                display.innerHTML = '\\( \\emptyset \\)';
            } else {
                const verticesStr = nashVertices.map(v => 
                    `(${toFraction(v.x)}, ${toFraction(v.y)})`
                ).join(', ');

                display.innerHTML = `\\( \\{ ${verticesStr} \\} \\)`;
            }

            if (window.MathJax) {
                MathJax.typesetPromise([display]).catch(function (err) {
                    console.log('MathJax typeset failed: ' + err.message);
                });
            }
        }

        // p5.js sketch
        const sketch = function(p) {
            let canvasSize = 400;
            let margin = 40;
            let plotSize = canvasSize - 2 * margin;

            p.setup = function() {
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('canvas-container');
            };

            p.draw = function() {
                p.background(255);

                // Setup coordinate system
                p.translate(margin, canvasSize - margin);
                p.scale(1, -1);

                // Draw grid and axes
                p.stroke(200);
                p.strokeWeight(1);

                // Grid lines
                for (let i = 0; i <= 5; i++) {
                    let pos = i / 5 * plotSize;
                    p.line(pos, 0, pos, plotSize);
                    p.line(0, pos, plotSize, pos);
                }

                // Axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(0, 0, plotSize, 0);
                p.line(0, 0, 0, plotSize);

                // Labels
                p.push();
                p.scale(1, -1);
                p.fill(0);
                p.noStroke();
                p.textAlign('center');
                p.text('x₁', plotSize / 2, -plotSize - 15);
                p.textAlign('left');
                p.text('y₁', 10, plotSize / 2);

                // Tick labels
                p.textSize(10);
                for (let i = 0; i <= 5; i++) {
                    let val = i / 5;
                    let pos = val * plotSize;
                    p.text(val.toFixed(1), pos, 15);
                    p.text(val.toFixed(1), -25, -pos + 3);
                }
                p.pop();

                // Transform to game coordinates
                p.push();
                p.translate(-0.1 * plotSize, -0.1 * plotSize);
                p.scale(plotSize / 1.2, plotSize / 1.2);

                // Get best response segments
                let player1Segments = getPlayer1BestResponse();
                let player2Segments = getPlayer2BestResponse();

                // Draw Player 1 best response (blue)
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                player1Segments.forEach(seg => {
                    p.line(seg[0].x, seg[0].y, seg[1].x, seg[1].y);
                });

                // Draw Player 2 best response (green)
                p.stroke(0, 200, 0);
                player2Segments.forEach(seg => {
                    p.line(seg[0].x, seg[0].y, seg[1].x, seg[1].y);
                });

                // Find and draw Nash equilibria (red)
                nashVertices = findIntersections(player1Segments, player2Segments);

                p.stroke(255, 0, 0);
                p.strokeWeight(4);
                if (nashVertices.length > 0) {
                    p.point(nashVertices[0].x, nashVertices[0].y);

                    // Draw red circle around Nash equilibrium
                    p.noFill();
                    p.strokeWeight(2);
                    p.circle(nashVertices[0].x, nashVertices[0].y, 0.05);
                }

                p.pop();

                // Update equilibria display
                updateEquilibriaDisplay();
            };
        };

        // Initialize everything
        document.addEventListener('DOMContentLoaded', function() {
            setupEventListeners();
            updateMatrix();
            window.p5Instance = new p5(sketch);
        });
    </script>
