            [106, 90, 205],  // Slate Blue
            [255, 165, 0],   // Orange
            [147, 112, 219], // Medium Purple
            [255, 20, 147],  // Deep Pink
            [0, 191, 255],   // Deep Sky Blue
            [255, 215, 0],   // Gold
            [50, 205, 50],   // Lime Green
