        function drawAxes() {
            push();
            stroke(220);
            strokeWeight(1);

            // Grid lines
            for (let x = -6; x <= 6; x++) {
                if (x !== 0) {
                    let px = mapX(x);
                    line(px, 0, px, height);

                    // X-axis labels
                    push();
                    fill(100);
                    noStroke();
                    textAlign(CENTER, CENTER);
                    text(x, px, mapY(-0.3));
