            for (let y = -5; y <= 5; y++) {
                if (y !== 0) {
                    let py = mapY(y);
                    line(0, py, width, py);

                    // Y-axis labels
                    push();
                    fill(100);
                    noStroke();
                    textAlign(RIGHT, CENTER);
                    text(y, mapX(-0.3), py);
