            noFill();

            beginShape();
            for (let px = 0; px <= width; px += 1) {
                let x = unmapX(px);
                let y = cubicCoeffs.a * x * x * x + cubicCoeffs.b * x * x + cubicCoeffs.c * x + cubicCoeffs.d;
                let py = mapY(y);
                if (py >= 0 && py <= height) {
                    vertex(px, py);
