        function drawQuadraticCurve() {
            let t = parseFloat(sliderT.value);
            let coeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);

            push();
            stroke('#f57c00');
            strokeWeight(3);
            noFill();

            beginShape();
            for (let px = 0; px <= width; px += 1) {
                let x = unmapX(px);
