                beginShape();
                // Start at bottom
                vertex(0, mapY(-5));
                vertex(width, mapY(-5));

                // Add curve points
                for (let px = width; px >= 0; px -= 2) {
                    let x = unmapX(px);
                    let y = coeffs.p * x * x + coeffs.q * x + coeffs.r;
                    vertex(px, mapY(y));
                }

                // End at bottom
                vertex(0, mapY(-5));
                endShape(CLOSE);
                pop();
            });

            // Draw the selected quadratic on top
            drawQuadraticCurve();
        }

        function drawLocators() {
            locators.forEach((loc, i) => {
                push();
                let px = mapX(loc.x);
                let py = mapY(loc.y);

                // Draw circle
                fill(255);
                stroke(0);
                strokeWeight(2);
                circle(px, py, 16);

                // Draw cross
                strokeWeight(2);
                line(px - 6, py, px + 6, py);
                line(px, py - 6, px, py + 6);
                pop();
            });
        }

        function calculateCubicCoefficients() {
            // Solve system: a*x³ + b*x² + c*x + d = y for 4 points
            let points = locators;

            // Build matrix A and vector B for Ax = B
            let A = [];
            let B = [];

            for (let i = 0; i < 4; i++) {
                let x = points[i].x;
                A.push([x*x*x, x*x, x, 1]);
                B.push(points[i].y);
            }

            // Solve using Gaussian elimination
            let coeffs = gaussianElimination(A, B);

            if (coeffs) {
                cubicCoeffs.a = coeffs[0];
                cubicCoeffs.b = coeffs[1];
                cubicCoeffs.c = coeffs[2];
                cubicCoeffs.d = coeffs[3];
            }
        }

        function gaussianElimination(A, B) {
            let n = A.length;

            // Forward elimination
            for (let i = 0; i < n; i++) {
                // Find pivot
                let maxRow = i;
                for (let k = i + 1; k < n; k++) {
                    if (Math.abs(A[k][i]) > Math.abs(A[maxRow][i])) {
                        maxRow = k;
                    }
                }

                // Swap rows
                [A[i], A[maxRow]] = [A[maxRow], A[i]];
                [B[i], B[maxRow]] = [B[maxRow], B[i]];

                // Make pivot 1
                let pivot = A[i][i];
                if (Math.abs(pivot) < 1e-10) return null; // Singular matrix

                for (let j = i; j < n; j++) {
                    A[i][j] /= pivot;
                }
                B[i] /= pivot;

                // Eliminate column
                for (let k = i + 1; k < n; k++) {
                    let factor = A[k][i];
                    for (let j = i; j < n; j++) {
                        A[k][j] -= factor * A[i][j];
                    }
                    B[k] -= factor * B[i];
                }
            }

            // Back substitution
            let x = new Array(n);
            for (let i = n - 1; i >= 0; i--) {
                x[i] = B[i];
                for (let j = i + 1; j < n; j++) {
                    x[i] -= A[i][j] * x[j];
                }
            }

            return x;
        }

        function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
            let p = 3 * cubicCoeffs.a * t + cubicCoeffs.b;
            let q = -3 * cubicCoeffs.a * t * t + cubicCoeffs.c;
            let r = cubicCoeffs.a * t * t * t + cubicCoeffs.d;
            return { p, q, r };
        }

        function mapX(x) {
            return width / 2 + x * scale;
        }

        function mapY(y) {
            return height / 2 - y * scale;
        }

        function unmapX(px) {
            return (px - width / 2) / scale;
        }

        function unmapY(py) {
            return (height / 2 - py) / scale;
        }

        function mousePressed() {
            for (let i = 0; i < locators.length; i++) {
                let px = mapX(locators[i].x);
                let py = mapY(locators[i].y);
                let d = dist(mouseX, mouseY, px, py);

                if (d < 10) {
                    selectedLocator = i;
                    return;
                }
            }
            selectedLocator = null;
        }

        function mouseDragged() {
            if (selectedLocator !== null) {
                locators[selectedLocator].x = unmapX(mouseX);
                locators[selectedLocator].y = unmapY(mouseY);
                calculateCubicCoefficients();
            }
        }

        function mouseReleased() {
            selectedLocator = null;
        }

        // Event listeners
        sliderT.addEventListener('input', function() {
            inputT.value = this.value;
        });

        inputT.addEventListener('input', function() {
            sliderT.value = this.value;
        });

        checkboxFamily.addEventListener('change', function() {
            // Redraw handled automatically
        });

        btnReset.addEventListener('click', function() {
            // Reset controls
            sliderT.value = 0.5;
            inputT.value = 0.5;
            checkboxFamily.checked = false;

            // Reset locators
            locators = [
                { x: -4, y: 1 },
                { x: -2, y: 1.8 },
                { x: 1, y: -1 },
                { x: 4, y: -1 }
            ];

            // Recalculate
            calculateCubicCoefficients();
        });
    </script>
</body>
</html>
