                <label for="slider-T">temperature T</label>
                <input type="range" id="slider-T" min="-273" max="727" value="270" step="1">
                <span class="value-display" id="T-display">270°C</span>
            </div>
            <div class="control-group">
                <label>temperature units</label>
                <div class="radio-group">
                    <input type="radio" id="radio-kelvin" name="temp-units" value="K">
                    <label for="radio-kelvin">Kelvin</label>
                    <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
                    <label for="radio-celsius">Celsius</label>
                </div>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
            <div id="piston-container"></div>
        </div>
