    <script>
        // Global variables
        let v0 = 211; // Initial volume at T0
        let T0 = 273.15; // Reference temperature in Kelvin
        let currentUnit = 'C'; // Current temperature unit
        let plotData = [];
        let pistonSketch;

        // Elements
        const sliderV0 = document.getElementById('slider-v0');
        const sliderT = document.getElementById('slider-T');
        const radioKelvin = document.getElementById('radio-kelvin');
        const radioCelsius = document.getElementById('radio-celsius');
        const v0Display = document.getElementById('v0-display');
        const tDisplay = document.getElementById('T-display');

        // Initialize
        function init() {
            setupEventListeners();
            createPlot();
            createPistonAnimation();
            updateVisualization();
        }

        // Setup event listeners
        function setupEventListeners() {
            sliderV0.addEventListener('input', handleV0Change);
            sliderT.addEventListener('input', handleTChange);
            radioKelvin.addEventListener('change', handleUnitChange);
            radioCelsius.addEventListener('change', handleUnitChange);
        }

        // Handle initial volume change
        function handleV0Change(e) {
            v0 = parseInt(e.target.value);
            v0Display.textContent = v0;
            updateVisualization();
        }

        // Handle temperature change
        function handleTChange(e) {
            updateVisualization();
        }

        // Handle unit change
        function handleUnitChange(e) {
            const newUnit = e.target.value;
            const currentT = parseInt(sliderT.value);
            let newT;

            if (newUnit === 'K' && currentUnit === 'C') {
                newT = currentT + 273.15;
            } else if (newUnit === 'C' && currentUnit === 'K') {
                newT = currentT - 273.15;
            } else {
                return;
            }

            currentUnit = newUnit;

            // Update slider attributes
            if (currentUnit === 'C') {
                sliderT.min = -273;
                sliderT.max = 727;
            } else {
                sliderT.min = 0;
                sliderT.max = 1000;
            }

            sliderT.value = Math.round(newT);
            updateVisualization();
        }

        // Get current temperature in Kelvin
        function getCurrentTempKelvin() {
            const currentT = parseInt(sliderT.value);
            return currentUnit === 'K' ? currentT : currentT + 273.15;
        }

        // Calculate constants and current values
        function calculateValues() {
            const k = v0 / T0; // Proportionality constant
            const TK = getCurrentTempKelvin();
            const V_current = k * TK; // Current volume
            const TC = currentUnit === 'C' ? parseInt(sliderT.value) : TK - 273.15;

            return { k, TK, TC, V_current };
        }

        // Get temperature display value
        function getDisplayTemp() {
            const temp = parseInt(sliderT.value);
            return currentUnit === 'K' ? `${temp}K` : `${temp}°C`;
        }

        // Update visualization
        function updateVisualization() {
            const { k, TK, TC, V_current } = calculateValues();

            // Update displays
            tDisplay.textContent = getDisplayTemp();

            // Update plot
            updatePlot(TC, V_current);

            // Update piston
            if (pistonSketch) {
                pistonSketch.updatePiston(V_current, TK);
            }
        }

        // Create Plotly plot
        function createPlot() {
            const xMin = currentUnit === 'C' ? -273 : 0;
            const xMax = currentUnit === 'C' ? 727 : 1000;

            // Generate temperature array
            const tempValues = [];
            for (let t = xMin; t <= xMax; t += 10) {
                tempValues.push(t);
            }

            // Calculate volume for each temperature
            const { k } = calculateValues();
            const volumeValues = tempValues.map(t => {
                const TK = currentUnit === 'C' ? t + 273.15 : t;
                return k * TK;
            });

            // Create traces
            const lineTrace = {
                x: tempValues,
                y: volumeValues,
                type: 'scatter',
                mode: 'lines',
                name: "Charles's Law",
                line: {
                    color: tempValues.map(t => {
                        const TK = currentUnit === 'C' ? t + 273.15 : t;
                        return TK / 1000; // Normalized temperature for color
                    }),
                    colorscale: [[0, 'purple'], [0.5, 'yellow'], [1, 'red']],
                    width: 3
                }
            };

            const markerTrace = {
                x: [currentUnit === 'C' ? (TK - 273.15) : TK],
                y: [V_current],
                type: 'scatter',
                mode: 'markers',
                name: 'Current State',
                marker: {
                    size: 12,
                    color: TK / 1000,
                    colorscale: [[0, 'purple'], [0.5, 'yellow'], [1, 'red']],
                    line: { width: 2, color: 'white' }
                }
            };

            const layout = {
                title: 'Volume vs Temperature (Charles\'s Law)',
                xaxis: {
                    title: `Temperature (${currentUnit === 'C' ? '°C' : 'K'})`,
                    range: [xMin, xMax]
                },
                yaxis: {
                    title: 'Volume',
                    range: [0, 2000]
                },
                margin: { t: 50, r: 20, b: 50, l: 60 },
                plot_bgcolor: 'rgba(0,0,0,0)',
                paper_bgcolor: 'white'
            };

            Plotly.newPlot('plot-container', [lineTrace, markerTrace], layout, {responsive: true});

            // Store plot data for updates
            plotData = [lineTrace, markerTrace];
        }

        // Update plot
        function updatePlot(TC, V_current) {
            const { k } = calculateValues();
            const xMin = currentUnit === 'C' ? -273 : 0;
            const xMax = currentUnit === 'C' ? 727 : 1000;

            // Generate temperature array
            const tempValues = [];
            for (let t = xMin; t <= xMax; t += 10) {
                tempValues.push(t);
            }

            // Calculate volume for each temperature
            const volumeValues = tempValues.map(t => {
                const TK = currentUnit === 'C' ? t + 273.15 : t;
                return k * TK;
            });

            // Update traces
            plotData[0].x = tempValues;
            plotData[0].y = volumeValues;
            plotData[0].line.color = tempValues.map(t => {
                const TK = currentUnit === 'C' ? t + 273.15 : t;
                return TK / 1000;
            });

            plotData[1].x = [currentUnit === 'C' ? TC : TC + 273.15];
            plotData[1].y = [V_current];
            plotData[1].marker.color = (currentUnit === 'C' ? TC + 273.15 : TC) / 1000;

            // Update layout
            const layout = {
                title: 'Volume vs Temperature (Charles\'s Law)',
                xaxis: {
                    title: `Temperature (${currentUnit === 'C' ? '°C' : 'K'})`,
                    range: [xMin, xMax]
                },
                yaxis: {
                    title: 'Volume',
                    range: [0, 2000]
                }
            };

            Plotly.react('plot-container', plotData, layout);
        }

        // Create p5.js piston animation
        function createPistonAnimation() {
            pistonSketch = function(p) {
                let currentVolume = 211;
                let currentTemp = 270 + 273.15;
                let minHeight = 50;
                let maxHeight = 300;
                let cylinderWidth = 120;

                p.setup = function() {
                    p.createCanvas(300, 400);
                    p.noLoop();
                };

                p.updatePiston = function(volume, tempK) {
                    currentVolume = volume;
                    currentTemp = tempK;
                    p.draw();
                };

                p.draw = function() {
                    p.background(255);

                    // Calculate piston height (inverse relationship)
                    let normalizedVolume = (currentVolume - 100) / (1000 - 100);
                    let pistonY = p.lerp(maxHeight, minHeight, normalizedVolume);

                    // Draw cylinder walls
                    p.stroke(50, 100, 200);
                    p.strokeWeight(8);
                    p.line(100, 50, 100, 350);
                    p.line(220, 50, 220, 350);

                    // Draw cylinder base
                    p.strokeWeight(4);
                    p.line(100, 350, 220, 350);

                    // Calculate gas color based on temperature
                    let colorLow = p.color(128, 0, 128); // Purple at 0K
                    let colorMid = p.color(255, 255, 0); // Yellow at 500K
                    let colorHigh = p.color(255, 0, 0); // Red at 1000K

                    let gasColor;
                    if (currentTemp <= 500) {
                        gasColor = p.lerpColor(colorLow, colorMid, currentTemp / 500);
                    } else {
                        gasColor = p.lerpColor(colorMid, colorHigh, (currentTemp - 500) / 500);
                    }

                    // Draw gas
                    p.noStroke();
                    p.fill(gasColor);
                    p.rect(104, pistonY, 112, 350 - pistonY);

                    // Draw piston
                    p.stroke(100);
                    p.strokeWeight(2);
                    p.fill(150);

                    // Piston plate
                    p.rect(90, pistonY - 10, 140, 20);

                    // Piston rod
                    p.rect(155, 50, 10, pistonY - 60);

                    // Draw pressure arrows
                    p.stroke(255, 150, 0);
                    p.strokeWeight(3);

                    // Arrow 1
                    p.line(110, pistonY - 30, 110, pistonY - 15);
                    p.line(105, pistonY - 20, 110, pistonY - 15);
                    p.line(115, pistonY - 20, 110, pistonY - 15);

                    // Arrow 2
                    p.line(190, pistonY - 30, 190, pistonY - 15);
                    p.line(185, pistonY - 20, 190, pistonY - 15);
                    p.line(195, pistonY - 20, 190, pistonY - 15);

                    // Arrow 3
                    p.line(150, pistonY - 30, 150, pistonY - 15);
                    p.line(145, pistonY - 20, 150, pistonY - 15);
                    p.line(155, pistonY - 20, 150, pistonY - 15);
                };
            };

            new p5(pistonSketch, 'piston-container');
        }

        // Initialize the demo
        init();
    </script>
</body>
