                angleC = parseFloat(e.target.value);
                updateVisualization();
            });

            // Radio button event listeners
            const radios = document.querySelectorAll('input[name="vertex-select"]');
            radios.forEach(radio => {
                radio.addEventListener('change', function(e) {
                    selectedVertex = e.target.value;
                    updateVisualization();
                });
            });
        }

        function draw() {
            background(255);

            // Draw background sectors
            drawBackgroundSectors();

            // Draw main circle
            fill('#aaccde');
            stroke('#666666');
            strokeWeight(2);
            circle(centerX, centerY, radius * 2);

            // Draw center point
            fill('#666666');
            noStroke();
            circle(centerX, centerY, 6);

            // Draw triangle
            drawTriangle();

            // Draw vertex labels
            drawVertexLabels();

            // Draw angle highlights if a vertex is selected
            if (selectedVertex !== 'None') {
                drawAngleHighlight();
            }
        }

        function drawBackgroundSectors() {
            noStroke();

            // Calculate vertex coordinates
            let posA = getVertexPosition(angleA);
            let posB = getVertexPosition(angleB);
            let posC = getVertexPosition(angleC);

            // Calculate arc angles
            let arcAB = (angleB - angleA + 360) % 360;
            let arcBC = (angleC - angleB + 360) % 360;
            let arcCA = (angleA - angleC + 360) % 360;

            // Convert to radians for p5.js
            let a1 = radians(angleA);
            let a2 = radians(angleB);
            let a3 = radians(angleC);

            // Draw sectors
            // Sector AB (green)
            fill('#e0f0e0');
            arc(centerX, centerY, radius * 2, radius * 2, a1, a2, PIE);

            // Sector BC (yellow)
            fill('#f5f5d5');
            arc(centerX, centerY, radius * 2, radius * 2, a2, a3, PIE);

            // Sector CA (blue)
            fill('#e0e8f8');
            arc(centerX, centerY, radius * 2, radius * 2, a3, a1, PIE);
        }

        function drawTriangle() {
            let posA = getVertexPosition(angleA);
            let posB = getVertexPosition(angleB);
            let posC = getVertexPosition(angleC);

            fill('rgba(173, 216, 230, 0.5)');
            stroke('#666666');
            strokeWeight(2);
            beginShape();
            vertex(posA.x, posA.y);
            vertex(posB.x, posB.y);
            vertex(posC.x, posC.y);
            endShape(CLOSE);
        }

        function drawVertexLabels() {
            let posA = getVertexPosition(angleA);
            let posB = getVertexPosition(angleB);
            let posC = getVertexPosition(angleC);

            fill('#00008B');
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(20);
            textStyle(BOLD);

            // Draw labels slightly outside the circle
            let labelRadius = radius + 25;

            text('A', centerX + labelRadius * cos(radians(angleA)), 
                     centerY + labelRadius * sin(radians(angleA)));
            text('B', centerX + labelRadius * cos(radians(angleB)), 
                     centerY + labelRadius * sin(radians(angleB)));
            text('C', centerX + labelRadius * cos(radians(angleC)), 
                     centerY + labelRadius * sin(radians(angleC)));
        }

        function drawAngleHighlight() {
            strokeWeight(3);
            textAlign(CENTER, CENTER);
            textSize(16);

            if (selectedVertex === 'A') {
                drawCentralAngleHighlight(angleB, angleC, '#008000');
                drawVertexAngleHighlight(angleA, angleB, angleC, '#FF0000');
            } else if (selectedVertex === 'B') {
                drawCentralAngleHighlight(angleC, angleA, '#008000');
                drawVertexAngleHighlight(angleB, angleC, angleA, '#FF0000');
            } else if (selectedVertex === 'C') {
                drawCentralAngleHighlight(angleA, angleB, '#008000');
                drawVertexAngleHighlight(angleC, angleA, angleB, '#FF0000');
            }
        }

        function drawCentralAngleHighlight(startAngle, endAngle, color) {
            stroke(color);
            fill(color);

            let arcLength = (endAngle - startAngle + 360) % 360;
            let startRad = radians(startAngle);
            let endRad = radians(endAngle);

            // Draw arc
            noFill();
            arc(centerX, centerY, radius * 0.4, radius * 0.4, startRad, endRad);

            // Draw arrow
            let arrowAngle = endRad - 0.2;
            let arrowRadius = radius * 0.4;
            let arrowX = centerX + arrowRadius * cos(arrowAngle);
            let arrowY = centerY + arrowRadius * sin(arrowAngle);
            push();
            translate(arrowX, arrowY);
            rotate(arrowAngle - PI/2);
            triangle(-5, -10, 5, -10, 0, 0);
            pop();

            // Draw label
            let labelAngle = (startRad + endRad) / 2;
            let labelRadius = radius * 0.5;
            let labelX = centerX + labelRadius * cos(labelAngle);
            let labelY = centerY + labelRadius * sin(labelAngle);
            noStroke();
            text(`θ = ${arcLength.toFixed(1)}°`, labelX, labelY);

            // Draw small center arc
            strokeWeight(2);
            arc(centerX, centerY, 30, 30, startRad, endRad);
        }

        function drawVertexAngleHighlight(vertexAngle, arcStart, arcEnd, color) {
            stroke(color);
            fill(color);

            let pos = getVertexPosition(vertexAngle);
            let angle1 = radians(arcStart + 180);
            let angle2 = radians(arcEnd + 180);

            // Draw arc at vertex
            noFill();
            arc(pos.x, pos.y, 40, 40, angle1, angle2);

            // Draw label
            let labelAngle = (angle1 + angle2) / 2;
            let labelX = pos.x + 25 * cos(labelAngle);
            let labelY = pos.y + 25 * sin(labelAngle);
            noStroke();
            let arcLength = (arcEnd - arcStart + 360) % 360;
            text(`θ/2 = ${(arcLength / 2).toFixed(1)}°`, labelX, labelY);
        }

        function getVertexPosition(angle) {
            return {
                x: centerX + radius * cos(radians(angle)),
                y: centerY + radius * sin(radians(angle))
            };
        }

        function mousePressed() {
            // Check if clicking on a vertex
            let posA = getVertexPosition(angleA);
            let posB = getVertexPosition(angleB);
            let posC = getVertexPosition(angleC);

            if (dist(mouseX, mouseY, posA.x, posA.y) < 15) {
                draggingVertex = 'A';
            } else if (dist(mouseX, mouseY, posB.x, posB.y) < 15) {
                draggingVertex = 'B';
            } else if (dist(mouseX, mouseY, posC.x, posC.y) < 15) {
                draggingVertex = 'C';
            }
        }

        function mouseDragged() {
            if (draggingVertex) {
                let angle = degrees(atan2(mouseY - centerY, mouseX - centerX));
                if (angle < 0) angle += 360;

                if (draggingVertex === 'A') {
                    angleA = angle;
                    document.getElementById('slider-a').value = angle;
                } else if (draggingVertex === 'B') {
                    angleB = angle;
                    document.getElementById('slider-b').value = angle;
                } else if (draggingVertex === 'C') {
                    angleC = angle;
                    document.getElementById('slider-c').value = angle;
                }

                updateVisualization();
            }
        }

        function mouseReleased() {
            draggingVertex = null;
        }

        function updateVisualization() {
            // Calculate arc lengths
            let arcAB = (angleB - angleA + 360) % 360;
            let arcBC = (angleC - angleB + 360) % 360;
            let arcCA = (angleA - angleC + 360) % 360;

            // Calculate vertex angles
            let vertexA_angle = arcBC / 2;
            let vertexB_angle = arcCA / 2;
            let vertexC_angle = arcAB / 2;

            // Update info panel based on selected vertex
            if (selectedVertex === 'None') {
                // Show all values
                document.getElementById('arc-label-1').textContent = 'Arc AB';
                document.getElementById('arc-value-1').textContent = arcAB.toFixed(1) + '°';
                document.getElementById('vertex-label-1').textContent = 'Vertex C';
                document.getElementById('vertex-value-1').textContent = vertexC_angle.toFixed(1) + '°';

                document.getElementById('arc-label-2').textContent = 'Arc BC';
                document.getElementById('arc-value-2').textContent = arcBC.toFixed(1) + '°';
                document.getElementById('vertex-label-2').textContent = 'Vertex A';
                document.getElementById('vertex-value-2').textContent = vertexA_angle.toFixed(1) + '°';

                document.getElementById('arc-label-3').textContent = 'Arc CA';
                document.getElementById('arc-value-3').textContent = arcCA.toFixed(1) + '°';
                document.getElementById('vertex-label-3').textContent = 'Vertex B';
                document.getElementById('vertex-value-3').textContent = vertexB_angle.toFixed(1) + '°';

                // Update totals
                document.getElementById('arc-total').textContent = '360.0°';
                document.getElementById('vertex-total').textContent = '180.0°';
            } else {
                // Show only the selected pair
                if (selectedVertex === 'A') {
                    document.getElementById('arc-label-1').textContent = 'Arc BC';
                    document.getElementById('arc-value-1').textContent = arcBC.toFixed(1) + '°';
                    document.getElementById('vertex-label-1').textContent = 'Vertex A';
                    document.getElementById('vertex-value-1').textContent = vertexA_angle.toFixed(1) + '°';
                } else if (selectedVertex === 'B') {
                    document.getElementById('arc-label-1').textContent = 'Arc CA';
                    document.getElementById('arc-value-1').textContent = arcCA.toFixed(1) + '°';
                    document.getElementById('vertex-label-1').textContent = 'Vertex B';
                    document.getElementById('vertex-value-1').textContent = vertexB_angle.toFixed(1) + '°';
                } else if (selectedVertex === 'C') {
                    document.getElementById('arc-label-1').textContent = 'Arc AB';
                    document.getElementById('arc-value-1').textContent = arcAB.toFixed(1) + '°';
                    document.getElementById('vertex-label-1').textContent = 'Vertex C';
                    document.getElementById('vertex-value-1').textContent = vertexC_angle.toFixed(1) + '°';
                }

                // Clear other rows
                document.getElementById('arc-label-2').textContent = '';
                document.getElementById('arc-value-2').textContent = '';
                document.getElementById('vertex-label-2').textContent = '';
                document.getElementById('vertex-value-2').textContent = '';

                document.getElementById('arc-label-3').textContent = '';
                document.getElementById('arc-value-3').textContent = '';
                document.getElementById('vertex-label-3').textContent = '';
                document.getElementById('vertex-value-3').textContent = '';

                // Hide totals
                document.getElementById('arc-total').textContent = '?';
                document.getElementById('vertex-total').textContent = '?';
            }
        }
    </script>
</body>
</html>
