            </div>

            <div class="control-group">
                <label class="control-label">β (Prater Number)</label>
                <div class="slider-container">
                    <button id="btn-dec-beta">−button>
                    <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
                    <button id="btn-inc-beta">+</button>
                </div>
                <div class="value-display" id="value-beta">0.20div>
            </div>

            <div class="control-group">
                <label class="control-label">γ (Arrhenius Number)</label>
                <div class="slider-container">
                    <button id="btn-dec-gamma">−button>
                    <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
                    <button id="btn-inc-gamma">+</button>
                </div>
                <div class="value-display" id="value-gamma">20div>
            </div>

            <div class="control-group">
                <label class="control-label">ξ (Position)</label>
                <div class="slider-container">
                    <button id="btn-dec-xi">−button>
                    <input type="range" id="slider-xi" min="0.001" max="1.0" step="0.001" value="0.4">
                    <button id="btn-inc-xi">+</button>
                </div>
                <div class="value-display" id="value-xi">0.400div>
            </div>
        </div>

        <div id="visualization-panel">
            <div class="plot-container">
                <div id="plot-concentration"></div>
            </div>
            <div class="plot-container">
                <div id="plot-temperature"></div>
            </div>
        </div>
    </div>

    <script>
        // Simulation parameters
        const N = 100; // Number of spatial grid points
        const dx = 1.0 / (N - 1); // Spatial step size
        const xi = Array.from({length: N}, (_, i) => i * dx); // Spatial grid

        // Current simulation state
        let c = new Array(N);
        let theta = new Array(N);
        let currentTime = 0;
        let simulationInProgress = false;

        // Get DOM elements
        const tauSlider = document.getElementById('slider-tau');
        const phiSlider = document.getElementById('slider-phi');
        const betaSlider = document.getElementById('slider-beta');
        const gammaSlider = document.getElementById('slider-gamma');
        const xiSlider = document.getElementById('slider-xi');

        // Value displays
        const tauValue = document.getElementById('value-tau');
        const phiValue = document.getElementById('value-phi');
        const betaValue = document.getElementById('value-beta');
        const gammaValue = document.getElementById('value-gamma');
        const xiValue = document.getElementById('value-xi');

        // Buttons
        const btnDecTau = document.getElementById('btn-dec-tau');
        const btnIncTau = document.getElementById('btn-inc-tau');
        const btnDecPhi = document.getElementById('btn-dec-phi');
        const btnIncPhi = document.getElementById('btn-inc-phi');
        const btnDecBeta = document.getElementById('btn-dec-beta');
        const btnIncBeta = document.getElementById('btn-inc-beta');
        const btnDecGamma = document.getElementById('btn-dec-gamma');
        const btnIncGamma = document.getElementById('btn-inc-gamma');
        const btnDecXi = document.getElementById('btn-dec-xi');
        const btnIncXi = document.getElementById('btn-inc-xi');

        // Initialize conditions
        function initializeConditions() {
            for (let i = 0; i < N; i++) {
                c[i] = i === N-1 ? 1.0 : 0.0; // c=1 at surface, 0 elsewhere
                theta[i] = 1.0; // θ=1 everywhere
            }
            currentTime = 0;
        }

        // Compute reaction rate
        function reactionRate(c_val, theta_val, gamma) {
            return c_val * Math.exp(gamma * (1 - 1 / theta_val));
        }

        // Compute spatial second derivative using central differences
        function d2dx2(f, i) {
            if (i === 0 || i === N-1) return 0; // Boundary conditions
            return (f[i+1] - 2*f[i] + f[i-1]) / (dx * dx);
        }

        // RK4 integration step
        function rk4Step(c, theta, dt, phi, beta, gamma) {
            const k1_c = new Array(N);
            const k1_theta = new Array(N);
            const k2_c = new Array(N);
            const k2_theta = new Array(N);
            const k3_c = new Array(N);
            const k3_theta = new Array(N);
            const k4_c = new Array(N);
            const k4_theta = new Array(N);

            // Temporary arrays
            const c_temp = new Array(N);
            const theta_temp = new Array(N);

            // K1
            for (let i = 0; i < N; i++) {
                const rate = reactionRate(c[i], theta[i], gamma);
                k1_c[i] = d2dx2(c, i) - phi * phi * rate;
                k1_theta[i] = d2dx2(theta, i) + beta * phi * phi * rate;
            }

            // K2
            for (let i = 0; i < N; i++) {
                c_temp[i] = c[i] + 0.5 * dt * k1_c[i];
                theta_temp[i] = theta[i] + 0.5 * dt * k1_theta[i];
            }
            for (let i = 0; i < N; i++) {
                const rate = reactionRate(c_temp[i], theta_temp[i], gamma);
                k2_c[i] = d2dx2(c_temp, i) - phi * phi * rate;
                k2_theta[i] = d2dx2(theta_temp, i) + beta * phi * phi * rate;
            }

            // K3
            for (let i = 0; i < N; i++) {
                c_temp[i] = c[i] + 0.5 * dt * k2_c[i];
                theta_temp[i] = theta[i] + 0.5 * dt * k2_theta[i];
            }
            for (let i = 0; i < N; i++) {
                const rate = reactionRate(c_temp[i], theta_temp[i], gamma);
                k3_c[i] = d2dx2(c_temp, i) - phi * phi * rate;
                k3_theta[i] = d2dx2(theta_temp, i) + beta * phi * phi * rate;
            }

            // K4
            for (let i = 0; i < N; i++) {
                c_temp[i] = c[i] + dt * k3_c[i];
                theta_temp[i] = theta[i] + dt * k3_theta[i];
            }
            for (let i = 0; i < N; i++) {
                const rate = reactionRate(c_temp[i], theta_temp[i], gamma);
                k4_c[i] = d2dx2(c_temp, i) - phi * phi * rate;
                k4_theta[i] = d2dx2(theta_temp, i) + beta * phi * phi * rate;
            }

            // Update solution
            for (let i = 0; i < N; i++) {
                c[i] += dt * (k1_c[i] + 2*k2_c[i] + 2*k3_c[i] + k4_c[i]) / 6;
                theta[i] += dt * (k1_theta[i] + 2*k2_theta[i] + 2*k3_theta[i] + k4_theta[i]) / 6;
            }

            // Apply boundary conditions
            theta[N-1] = 1.0;
            c[N-1] = 1.0;
        }

        // Run simulation
        function runSimulation() {
            const tau = parseFloat(tauSlider.value);
            const phi = parseFloat(phiSlider.value);
            const beta = parseFloat(betaSlider.value);
            const gamma = parseFloat(gammaSlider.value);

            initializeConditions();

            const dt = 0.0001; // Time step
            const maxSteps = Math.ceil(tau / dt);
            const stepsPerFrame = 100; // Update plots every N steps

            let step = 0;

            function simulateStep() {
                if (step < maxSteps) {
                    for (let i = 0; i < stepsPerFrame && step < maxSteps; i++) {
                        rk4Step(c, theta, dt, phi, beta, gamma);
                        currentTime += dt;
                        step++;
                    }

                    updatePlots();
                    requestAnimationFrame(simulateStep);
                } else {
                    simulationInProgress = false;
                }
            }

            simulationInProgress = true;
            simulateStep();
        }

        // Interpolate value at given xi
        function interpolate(x, y, xi_target) {
            const idx = Math.floor(xi_target / dx);
            if (idx >= N-1) return y[N-1];
            const alpha = (xi_target - xi[idx]) / dx;
            return y[idx] * (1 - alpha) + y[idx + 1] * alpha;
        }

        // Update plots
        function updatePlots() {
            const tau = parseFloat(tauSlider.value);
            const xi_val = parseFloat(xiSlider.value);
            const c_val = interpolate(xi, c, xi_val);
            const theta_val = interpolate(xi, theta, xi_val);

            // Concentration plot data
            const concLayout = {
                yaxis: { title: 'c', range: [0, 1.1], autorange: false },
                xaxis: { title: 'ξ', range: [0, 1.0] },
                margin: { l: 50, r: 150, t: 20, b: 40 },
                showlegend: false,
                annotations: [{
                    xref: 'paper',
                    yref: 'paper',
                    x: 0.98,
                    y: 0.5,
                    xanchor: 'right',
                    yanchor: 'middle',
                    text: `τ = ${tau.toFixed(2)}\nξ = ${xi_val.toFixed(3)}\nc = ${c_val.toFixed(5)}`,
                    showarrow: false,
                    font: { size: 12 },
                    bgcolor: 'rgba(255,255,255,0.8)',
                    bordercolor: '#ddd',
                    borderwidth: 1
                }]
            };

            const concData = [
                { x: xi, y: c, type: 'scatter', mode: 'lines', line: { color: 'blue', width: 2 } },
                { 
                    x: [xi_val, xi_val, 0], 
                    y: [0, c_val, c_val], 
                    type: 'scatter', 
                    mode: 'lines', 
                    line: { color: '#333333', width: 2, dash: 'dot' } 
                }
            ];

            // Temperature plot data
            const thetaMin = Math.min(...theta);
            const thetaMax = Math.max(...theta);
            const thetaRange = thetaMax - thetaMin;
            const thetaYmin = thetaMin - 0.1 * thetaRange;

            const tempLayout = {
                yaxis: { title: 'θ', autorange: true },
                xaxis: { title: 'ξ', range: [0, 1.0] },
                margin: { l: 50, r: 150, t: 20, b: 40 },
                showlegend: false,
                annotations: [{
                    xref: 'paper',
                    yref: 'paper',
                    x: 0.98,
                    y: 0.5,
                    xanchor: 'right',
                    yanchor: 'middle',
                    text: `τ = ${tau.toFixed(2)}\nξ = ${xi_val.toFixed(3)}\nθ = ${theta_val.toFixed(5)}`,
                    showarrow: false,
                    font: { size: 12 },
                    bgcolor: 'rgba(255,255,255,0.8)',
                    bordercolor: '#ddd',
                    borderwidth: 1
                }]
            };

            const tempData = [
                { x: xi, y: theta, type: 'scatter', mode: 'lines', line: { color: 'red', width: 2 } },
                { 
                    x: [xi_val, xi_val, 0], 
                    y: [thetaYmin, theta_val, theta_val], 
                    type: 'scatter', 
                    mode: 'lines', 
                    line: { color: '#333333', width: 2, dash: 'dot' } 
                }
            ];

            Plotly.react('plot-concentration', concData, concLayout, { responsive: true });
            Plotly.react('plot-temperature', tempData, tempLayout, { responsive: true });
        }

        // Update markers only (when xi changes)
        function updateMarkersOnly() {
            const tau = parseFloat(tauSlider.value);
            const xi_val = parseFloat(xiSlider.value);
            const c_val = interpolate(xi, c, xi_val);
            const theta_val = interpolate(xi, theta, xi_val);

            // Get current plot ranges
            const concPlot = document.getElementById('plot-concentration');
            const tempPlot = document.getElementById('plot-temperature');
            const concBounds = concPlot.getBoundingClientRect();
            const tempBounds = tempPlot.getBoundingClientRect();

            // Update marker traces
            const thetaMin = Math.min(...theta);
            const thetaMax = Math.max(...theta);
            const thetaRange = thetaMax - thetaMin;
            const thetaYmin = thetaMin - 0.1 * thetaRange;

            const markerDataConc = { 
                x: [[xi_val, xi_val, 0]], 
                y: [[0, c_val, c_val]] 
            };
            const markerDataTemp = { 
                x: [[xi_val, xi_val, 0]], 
                y: [[thetaYmin, theta_val, theta_val]] 
            };

            Plotly.restyle('plot-concentration', markerDataConc, [1]);
            Plotly.restyle('plot-temperature', markerDataTemp, [1]);

            // Update annotations
            const concAnnotation = { text: `τ = ${tau.toFixed(2)}\nξ = ${xi_val.toFixed(3)}\nc = ${c_val.toFixed(5)}` };
            const tempAnnotation = { text: `τ = ${tau.toFixed(2)}\nξ = ${xi_val.toFixed(3)}\nθ = ${theta_val.toFixed(5)}` };

            Plotly.relayout('plot-concentration', { 'annotations[0].text': concAnnotation.text });
            Plotly.relayout('plot-temperature', { 'annotations[0].text': tempAnnotation.text });
        }

        // Event listeners for sliders
        tauSlider.addEventListener('input', function() {
            tauValue.textContent = parseFloat(this.value).toFixed(2);
            if (!simulationInProgress) {
                runSimulation();
            }
        });

        phiSlider.addEventListener('input', function() {
            phiValue.textContent = parseFloat(this.value).toFixed(1);
            if (!simulationInProgress) {
                runSimulation();
            }
        });

        betaSlider.addEventListener('input', function() {
            betaValue.textContent = parseFloat(this.value).toFixed(2);
            if (!simulationInProgress) {
                runSimulation();
            }
        });

        gammaSlider.addEventListener('input', function() {
            gammaValue.textContent = parseInt(this.value);
            if (!simulationInProgress) {
                runSimulation();
            }
        });

        xiSlider.addEventListener('input', function() {
            xiValue.textContent = parseFloat(this.value).toFixed(3);
            updateMarkersOnly();
        });

        // Event listeners for buttons
        btnDecTau.addEventListener('click', () => {
            tauSlider.value = Math.max(0.01, parseFloat(tauSlider.value) - 0.01);
            tauSlider.dispatchEvent(new Event('input'));
        });

        btnIncTau.addEventListener('click', () => {
            tauSlider.value = Math.min(5, parseFloat(tauSlider.value) + 0.01);
            tauSlider.dispatchEvent(new Event('input'));
        });

        btnDecPhi.addEventListener('click', () => {
            phiSlider.value = Math.max(0.1, parseFloat(phiSlider.value) - 0.1);
            phiSlider.dispatchEvent(new Event('input'));
        });

        btnIncPhi.addEventListener('click', () => {
            phiSlider.value = Math.min(10, parseFloat(phiSlider.value) + 0.1);
            phiSlider.dispatchEvent(new Event('input'));
        });

        btnDecBeta.addEventListener('click', () => {
            betaSlider.value = Math.max(-0.5, parseFloat(betaSlider.value) - 0.01);
            betaSlider.dispatchEvent(new Event('input'));
        });

        btnIncBeta.addEventListener('click', () => {
            betaSlider.value = Math.min(0.5, parseFloat(betaSlider.value) + 0.01);
            betaSlider.dispatchEvent(new Event('input'));
        });

        btnDecGamma.addEventListener('click', () => {
            gammaSlider.value = Math.max(0, parseInt(gammaSlider.value) - 1);
            gammaSlider.dispatchEvent(new Event('input'));
        });

        btnIncGamma.addEventListener('click', () => {
            gammaSlider.value = Math.min(40, parseInt(gammaSlider.value) + 1);
            gammaSlider.dispatchEvent(new Event('input'));
        });

        btnDecXi.addEventListener('click', () => {
            xiSlider.value = Math.max(0.001, parseFloat(xiSlider.value) - 0.001);
            xiSlider.dispatchEvent(new Event('input'));
        });

        btnIncXi.addEventListener('click', () => {
            xiSlider.value = Math.min(1.0, parseFloat(xiSlider.value) + 0.001);
            xiSlider.dispatchEvent(new Event('input'));
        });

        // Initialize plots on page load
        initializeConditions();
        runSimulation();
    </script>
