    <script>
        // Static data
        const staticAges = [30, 35, 40, 45, 50, 55];
        const staticVolumes = [289, 445, 565, 704, 833, 924];

        // Get slider and value elements
        const sliders = {
            bmax: document.getElementById('slider-bmax'),
            b0: document.getElementById('slider-b0'),
            r: document.getElementById('slider-r'),
            beta: document.getElementById('slider-beta')
        };

        const valueDisplays = {
            bmax: document.getElementById('value-bmax'),
            b0: document.getElementById('value-b0'),
            r: document.getElementById('value-r'),
            beta: document.getElementById('value-beta')
        };

        const resultU = document.getElementById('result-u');
        const resultP = document.getElementById('result-p');

        // Initialize plot
        function initPlot() {
            const data = [
                {
                    x: staticAges,
                    y: staticVolumes,
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'blue',
                        size: 8
                    },
                    name: 'Data Points'
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'orange',
                        width: 2
                    },
                    name: 'Richards Curve'
                },
                {
                    x: [],
                    y: [],
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'blue',
                        width: 2
                    },
                    name: '4/5 Law Curve'
                }
            ];

            const layout = {
                title: 'forest growth curve',
                xaxis: {
                    title: 'stand age (years)',
                    range: [15, 80],
                    gridcolor: 'lightgrey',
                    zeroline: false,
                    gridwidth: 1,
                    griddash: 'dot'
                },
                yaxis: {
                    title: {
                        text: 'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
                        standoff: 20
                    },
                    range: [0, 1600],
                    gridcolor: 'lightgrey',
                    zeroline: false,
                    gridwidth: 1,
                    griddash: 'dot'
                },
                plot_bgcolor: '#FFFFF0',
                showlegend: false,
                margin: {
                    l: 80,
                    r: 20,
                    t: 50,
                    b: 60
                }
            };

            Plotly.newPlot('plot-container', data, layout, {responsive: true});
        }

        // Update function
        function update() {
            // Get current slider values
            const b_max = parseFloat(sliders.bmax.value);
            const b_0 = parseFloat(sliders.b0.value);
            const r = parseFloat(sliders.r.value);
            const beta = parseFloat(sliders.beta.value);

            // Update value displays
            valueDisplays.bmax.textContent = b_max;
            valueDisplays.b0.textContent = b_0;
            valueDisplays.r.textContent = r.toFixed(3);
            valueDisplays.beta.textContent = beta.toFixed(3);

            // Generate t values for plotting
            const tValues = [];
            for (let t = 15; t <= 80; t += 0.5) {
                tValues.push(t);
            }

            // Calculate Richards curve
            const M = Math.pow(b_max / b_0, beta) - 1;
            const richardsValues = tValues.map(t => {
                return b_max / Math.pow(1 + M * Math.exp(-r * t), 1 / beta);
            });

            // Calculate inflection point properties
            const t_i = Math.log(M * beta) / r;
            const V_i = b_max * Math.pow((beta + 1) / (2 * beta), 1 / beta);
            const V_prime_i = (r / beta) * V_i * (1 - Math.pow(V_i / b_max, beta));

            // Calculate u and p
            let u = NaN;
            let p = NaN;
            if (V_prime_i > 0) {
                u = t_i - (4/5) * V_i / V_prime_i;
                if (t_i - u > 0) {
                    p = V_i / Math.pow(t_i - u, 4/5);
                }
            }

            // Calculate 4/5 law curve
            const law45Values = tValues.map(t => {
                if (t > u && !isNaN(u) && !isNaN(p)) {
                    return p * Math.pow(t - u, 4/5);
                }
                return null;
            });

            // Update results display
            resultU.textContent = `rejuvenation bias u = ${isNaN(u) ? 'N/A' : u.toFixed(4)}`;
            resultP.textContent = `site productivity p = ${isNaN(p) ? 'N/A' : p.toFixed(4)}`;

            // Update plot
            Plotly.react('plot-container', [
                {
                    x: staticAges,
                    y: staticVolumes,
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'blue',
                        size: 8
                    },
                    name: 'Data Points'
                },
                {
                    x: tValues,
                    y: richardsValues,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'orange',
                        width: 2
                    },
                    name: 'Richards Curve'
                },
                {
                    x: tValues,
                    y: law45Values,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: 'blue',
                        width: 2
                    },
                    name: '4/5 Law Curve'
                }
            ], {
                title: 'forest growth curve',
                xaxis: {
                    title: 'stand age (years)',
                    range: [15, 80],
                    gridcolor: 'lightgrey',
                    zeroline: false,
                    gridwidth: 1,
                    griddash: 'dot'
                },
                yaxis: {
                    title: {
                        text: 'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
                        standoff: 20
                    },
                    range: [0, 1600],
                    gridcolor: 'lightgrey',
                    zeroline: false,
                    gridwidth: 1,
                    griddash: 'dot'
                },
                plot_bgcolor: '#FFFFF0',
                showlegend: false,
                margin: {
                    l: 80,
                    r: 20,
                    t: 50,
                    b: 60
                }
            });
        }

        // Add event listeners to sliders
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', update);
        });

        // Initialize on page load
        window.addEventListener('load', () => {
            initPlot();
            update();
        });

        // Configure MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['$$', '$$'], ['\\[', '\\]']]
            }
        };
    </script>
</body>
