            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(50, 50, 50);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            // Create main group for the pine cone
            coneGroup = new THREE.Group();
            scene.add(coneGroup);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            const canvas = document.getElementById('main-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        // Create polygon geometry
        function createPolygonGeometry(sides, radius) {
            const shape = new THREE.Shape();
            const angleStep = (Math.PI * 2) / sides;

            for (let i = 0; i < sides; i++) {
                const angle = i * angleStep - Math.PI / 2;
                const x = Math.cos(angle) * radius;
                const y = Math.sin(angle) * radius;

                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }

            shape.closePath();
            return new THREE.ShapeGeometry(shape);
        }

        // Main redraw function
        function redraw() {
            // Clear existing cone
            while (coneGroup.children.length > 0) {
                coneGroup.remove(coneGroup.children[0]);
            }

            // Get control values
            const basePolygon = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            const numUnits = parseInt(document.getElementById('slider-units').value);
            const angularAdvance = parseFloat(document.getElementById('slider-angle').value);
            const conicalFactor = parseFloat(document.getElementById('slider-conical').value);
            const rotateX = parseFloat(document.getElementById('slider-rotate-x').value);
            const rotateZ = parseFloat(document.getElementById('slider-rotate-z').value);

            // Convert angular advance to radians
            const angularAdvanceRad = angularAdvance * (Math.PI / 180);

            // Create base geometry
            const geometry = createPolygonGeometry(basePolygon, 10);

            // Loop through units
            for (let i = 0; i < numUnits; i++) {
                // Calculate spiral parameters
                const angle = i * angularAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);

                // Calculate position
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conicalFactor * radius;

                // Create material with HSL color
                const material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color(),
                    roughness: 0.5,
                    metalness: 0.1
                });
                material.color.setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6);

                // Create mesh
                const mesh = new THREE.Mesh(geometry, material);
                mesh.castShadow = true;
                mesh.receiveShadow = true;

                // Apply transformations
                mesh.position.set(x, y, z);
                mesh.scale.setScalar(scale);
                mesh.rotation.z = angle;

                // Add to group
                coneGroup.add(mesh);
            }

            // Apply master rotations
            coneGroup.rotation.x = rotateX * (Math.PI / 180);
            coneGroup.rotation.z = rotateZ * (Math.PI / 180);

            // Render
            renderer.render(scene, camera);
        }

        // Update value displays
        function updateValueDisplays() {
            document.getElementById('value-units').textContent = 
                document.getElementById('slider-units').value;
            document.getElementById('value-angle').textContent = 
                parseFloat(document.getElementById('slider-angle').value).toFixed(1);
            document.getElementById('value-conical').textContent = 
                parseFloat(document.getElementById('slider-conical').value).toFixed(2);
            document.getElementById('value-rotate-x').textContent = 
                document.getElementById('slider-rotate-x').value;
            document.getElementById('value-rotate-z').textContent = 
                document.getElementById('slider-rotate-z').value;
        }

        // Add event listeners
        function addEventListeners() {
            // Radio buttons
            document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
                radio.addEventListener('change', redraw);
            });

            // Sliders
            document.getElementById('slider-units').addEventListener('input', () => {
                updateValueDisplays();
                redraw();
            });

            document.getElementById('slider-angle').addEventListener('input', () => {
                updateValueDisplays();
                redraw();
            });

            document.getElementById('slider-conical').addEventListener('input', () => {
                updateValueDisplays();
                redraw();
            });

            document.getElementById('slider-rotate-x').addEventListener('input', () => {
                updateValueDisplays();
                redraw();
            });

            document.getElementById('slider-rotate-z').addEventListener('input', () => {
                updateValueDisplays();
                redraw();
            });

            // Plus buttons
            document.getElementById('btn-units-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-units');
                slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
                updateValueDisplays();
                redraw();
            });

            document.getElementById('btn-angle-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-angle');
                slider.value = Math.min(parseFloat(slider.value) + parseFloat(slider.step), slider.max);
                updateValueDisplays();
                redraw();
            });

            document.getElementById('btn-conical-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-conical');
                slider.value = Math.min(parseFloat(slider.value) + parseFloat(slider.step), slider.max);
                updateValueDisplays();
                redraw();
            });

            document.getElementById('btn-rotate-x-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-rotate-x');
                slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
                updateValueDisplays();
                redraw();
            });

            document.getElementById('btn-rotate-z-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-rotate-z');
                slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
                updateValueDisplays();
                redraw();
            });
        }

        // Initialize everything
        window.addEventListener('DOMContentLoaded', () => {
            initScene();
            addEventListeners();
            updateValueDisplays();
            redraw();
        });
    </script>
</body>
</html>
