    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="lattice-controls" class="control-group">
                <label>lattice</label>
                <div class="button-group">
                    <button id="btn-sc" class="active">SC</button>
                    <button id="btn-bcc">BCC</button>
                    <button id="btn-fcc">FCC</button>
                </div>
            </div>

            <div id="miller-controls" class="control-group">
                <label>Miller indices</label>
                <label for="input-h">h</label>
                <input type="number" id="input-h" value="1" min="-10" max="10" step="1">
                <label for="input-k">k</label>
                <input type="number" id="input-k" value="1" min="-10" max="10" step="1">
                <label for="input-l">l</label>
                <input type="number" id="input-l" value="1" min="-10" max="10" step="1">
            </div>

            <div id="opacity-controls" class="control-group">
                <label for="slider-opacity">plane opacity</label>
                <input type="range" id="slider-opacity" value="0.8" min="0" max="1" step="0.01">
                <span id="opacity-value">0.8</span>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/controls/OrbitControls.min.js"></script>

    <script>
        // Scene setup
        let scene, camera, renderer, controls;
        let atomsGroup, planesGroup;
        let currentLattice = 'SC';

        // Initialize Three.js scene
        function initScene() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            camera = new THREE.PerspectiveCamera(
                75,
                document.getElementById('canvas-container').offsetWidth / 
                document.getElementById('canvas-container').offsetHeight,
                0.1,
                1000
            );
            camera.position.set(2, 2, 2);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Groups
            atomsGroup = new THREE.Group();
            planesGroup = new THREE.Group();
            scene.add(atomsGroup);
            scene.add(planesGroup);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight1.position.set(5, 5, 5);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.3);
            directionalLight2.position.set(-5, -5, -5);
            scene.add(directionalLight2);

            // Add coordinate axes
            addAxes();

            // Add unit cell wireframe
            addUnitCell();

            // Initial scene update
            updateScene();

            // Start animation loop
            animate();
        }

        // Add coordinate axes
        function addAxes() {
            const axisLength = 0.7;

            const xAxis = new THREE.ArrowHelper(
                new THREE.Vector3(1, 0, 0),
                new THREE.Vector3(0, 0, 0),
                axisLength,
                0xff0000,
                axisLength * 0.3,
                axisLength * 0.1
            );

            const yAxis = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, 0, 0),
                axisLength,
                0x00ff00,
                axisLength * 0.3,
                axisLength * 0.1
            );

            const zAxis = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 1),
                new THREE.Vector3(0, 0, 0),
                axisLength,
                0x0000ff,
                axisLength * 0.3,
                axisLength * 0.1
            );

            scene.add(xAxis);
            scene.add(yAxis);
            scene.add(zAxis);
        }

        // Add unit cell wireframe
        function addUnitCell() {
            const geometry = new THREE.BoxGeometry(1, 1, 1);
            const edges = new THREE.EdgesGeometry(geometry);
            const line = new THREE.LineSegments(
                edges,
                new THREE.LineBasicMaterial({ color: 0x666666 })
            );
            scene.add(line);
        }

        // Calculate GCD
        function gcd(a, b, c) {
            return gcdTwo(gcdTwo(Math.abs(a), Math.abs(b)), Math.abs(c));
        }

        function gcdTwo(a, b) {
            return b === 0 ? a : gcdTwo(b, a % b);
        }

        // Update the entire scene
        function updateScene() {
            // Clear existing atoms and planes
            while (atomsGroup.children.length > 0) {
                atomsGroup.remove(atomsGroup.children[0]);
            }
            while (planesGroup.children.length > 0) {
                planesGroup.remove(planesGroup.children[0]);
            }

            // Draw atoms based on lattice type
            drawAtoms();

            // Draw planes
            drawPlanes();
        }

        // Draw atoms based on lattice type
        function drawAtoms() {
            const atomGeometry = new THREE.SphereGeometry(0.08, 16, 16);
            const atomMaterial = new THREE.MeshPhongMaterial({
                color: 0x2a4d69,
                shininess: 100,
                specular: 0x4080ff
            });

            switch (currentLattice) {
                case 'SC':
                    // 8 atoms at corners
                    for (let x of [-0.5, 0.5]) {
                        for (let y of [-0.5, 0.5]) {
                            for (let z of [-0.5, 0.5]) {
                                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                                atom.position.set(x, y, z);
                                atomsGroup.add(atom);
                            }
                        }
                    }
                    break;

                case 'BCC':
                    // Corner atoms
                    for (let x of [-0.5, 0.5]) {
                        for (let y of [-0.5, 0.5]) {
                            for (let z of [-0.5, 0.5]) {
                                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                                atom.position.set(x, y, z);
                                atomsGroup.add(atom);
                            }
                        }
                    }
                    // Center atom
                    const centerAtom = new THREE.Mesh(atomGeometry, atomMaterial);
                    centerAtom.position.set(0, 0, 0);
                    atomsGroup.add(centerAtom);
                    break;

                case 'FCC':
                    // Corner atoms
                    for (let x of [-0.5, 0.5]) {
                        for (let y of [-0.5, 0.5]) {
                            for (let z of [-0.5, 0.5]) {
                                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                                atom.position.set(x, y, z);
                                atomsGroup.add(atom);
                            }
                        }
                    }
                    // Face center atoms
                    const facePositions = [
                        [-0.5, 0, 0], [0.5, 0, 0],
                        [0, -0.5, 0], [0, 0.5, 0],
                        [0, 0, -0.5], [0, 0, 0.5]
                    ];
                    for (let pos of facePositions) {
                        const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                        atom.position.set(...pos);
                        atomsGroup.add(atom);
                    }
                    break;
            }
        }

        // Draw crystallographic planes
        function drawPlanes() {
            const h = parseInt(document.getElementById('input-h').value);
            const k = parseInt(document.getElementById('input-k').value);
            const l = parseInt(document.getElementById('input-l').value);

            // If all indices are zero, don't draw planes
            if (h === 0 && k === 0 && l === 0) return;

            // Simplify indices
            const g = gcd(h, k, l);
            const hp = h / g;
            const kp = k / g;
            const lp = l / g;

            // Calculate min and max values of h'x + k'y + l'z at cube vertices
            let min = Infinity;
            let max = -Infinity;

            for (let x of [-0.5, 0.5]) {
                for (let y of [-0.5, 0.5]) {
                    for (let z of [-0.5, 0.5]) {
                        const value = hp * x + kp * y + lp * z;
                        min = Math.min(min, value);
                        max = Math.max(max, value);
                    }
                }
            }

            // Create planes for each valid n
            const nMin = Math.floor(min);
            const nMax = Math.ceil(max);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);

            for (let n = nMin; n <= nMax; n++) {
                createPlane(hp, kp, lp, n, opacity);
            }
        }

        // Create a single plane
        function createPlane(h, k, l, n, opacity) {
            const vertices = [];

            // Cube vertices
            const cubeVertices = [
                [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5],
                [0.5, 0.5, -0.5], [-0.5, 0.5, -0.5],
                [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5],
                [0.5, 0.5, 0.5], [-0.5, 0.5, 0.5]
            ];

            // Cube edges
            const edges = [
                [0, 1], [1, 2], [2, 3], [3, 0],
                [4, 5], [5, 6], [6, 7], [7, 4],
                [0, 4], [1, 5], [2, 6], [3, 7]
            ];

            // Find intersection points
            for (let edge of edges) {
                const v1 = cubeVertices[edge[0]];
                const v2 = cubeVertices[edge[1]];

                const f1 = h * v1[0] + k * v1[1] + l * v1[2] - n;
                const f2 = h * v2[0] + k * v2[1] + l * v2[2] - n;

                if (f1 * f2 < 0) {
                    const t = -f1 / (f2 - f1);
                    const point = [
                        v1[0] + t * (v2[0] - v1[0]),
                        v1[1] + t * (v2[1] - v1[1]),
                        v1[2] + t * (v2[2] - v1[2])
                    ];
                    vertices.push(point);
                }
            }

            // Create polygon if we have enough vertices
            if (vertices.length >= 3) {
                // Sort vertices angularly around centroid
                const centroid = vertices.reduce((acc, v) => {
                    return [acc[0] + v[0], acc[1] + v[1], acc[2] + v[2]];
                }, [0, 0, 0]).map(v => v / vertices.length);

                vertices.sort((a, b) => {
                    const vecA = [a[0] - centroid[0], a[1] - centroid[1], a[2] - centroid[2]];
                    const vecB = [b[0] - centroid[0], b[1] - centroid[1], b[2] - centroid[2]];

                    const cross = [
                        vecA[1] * vecB[2] - vecA[2] * vecB[1],
                        vecA[2] * vecB[0] - vecA[0] * vecB[2],
                        vecA[0] * vecB[1] - vecA[1] * vecB[0]
                    ];
                    const dot = cross[0] * h + cross[1] * k + cross[2] * l;
                    return dot > 0 ? 1 : -1;
                });

                // Create shape
                const shape = new THREE.Shape();
                shape.moveTo(vertices[0][0], vertices[0][1]);
                for (let i = 1; i < vertices.length; i++) {
                    shape.lineTo(vertices[i][0], vertices[i][1]);
                }

                const geometry = new THREE.ShapeGeometry(shape);
                const material = new THREE.MeshBasicMaterial({
                    color: 0x0080ff,
                    transparent: true,
                    opacity: opacity,
                    side: THREE.DoubleSide
                });

                const mesh = new THREE.Mesh(geometry, material);

                // Position mesh in 3D space
                const normal = new THREE.Vector3(h, k, l).normalize();
                const quaternion = new THREE.Quaternion();
                quaternion.setFromUnitVectors(new THREE.Vector3(0, 0, 1), normal);
                mesh.quaternion.copy(quaternion);
                mesh.position.set(centroid[0], centroid[1], centroid[2]);

                planesGroup.add(mesh);
            }
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('btn-sc').addEventListener('click', function() {
            currentLattice = 'SC';
            document.querySelectorAll('.button-group button').forEach(btn => {
                btn.classList.remove('active');
            });
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('btn-bcc').addEventListener('click', function() {
            currentLattice = 'BCC';
            document.querySelectorAll('.button-group button').forEach(btn => {
                btn.classList.remove('active');
            });
            this.classList.add('active');
            updateScene();
        });

        document.getElementById('btn-fcc').addEventListener('click', function() {
            currentLattice = 'FCC';
            document.querySelectorAll('.button-group button').forEach(btn => {
                btn.classList.remove('active');
            });
            this.classList.add('active');
            updateScene();
        });

        ['input-h', 'input-k', 'input-l'].forEach(id => {
            document.getElementById(id).addEventListener('change', updateScene);
        });

        document.getElementById('slider-opacity').addEventListener('input', function() {
            const opacity = parseFloat(this.value);
            document.getElementById('opacity-value').textContent = opacity.toFixed(2);

            // Update existing planes
            planesGroup.children.forEach(plane => {
                if (plane.material) {
                    plane.material.opacity = opacity;
                }
            });
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            camera.aspect = document.getElementById('canvas-container').offsetWidth / 
                           document.getElementById('canvas-container').offsetHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
        });

        // Initialize the scene when page loads
        window.addEventListener('load', initScene);
    </script>
</body>
</html>
