        <div id="visualization-area">
            <div class="visualization-container">
                <div class="visualization-title">Orbit Sketch</div>
                <div id="orbit-canvas"></div>
            </div>

            <div class="visualization-container">
                <div class="visualization-title">Equation of Time Plot</div>
                <div id="plot-canvas"></div>
            </div>
        </div>
    </div>

    <script>
        // Get slider elements and value displays
        const sliderAngle = document.getElementById('slider-angle');
        const sliderLongitude = document.getElementById('slider-longitude');
        const sliderEccentricity = document.getElementById('slider-eccentricity');
        const valueAngle = document.getElementById('value-angle');
        const valueLongitude = document.getElementById('value-longitude');
        const valueEccentricity = document.getElementById('value-eccentricity');

        // Update value displays when sliders change
        sliderAngle.addEventListener('input', () => {
            valueAngle.textContent = parseFloat(sliderAngle.value).toFixed(2);
            updateVisualizations();
        });

        sliderLongitude.addEventListener('input', () => {
            valueLongitude.textContent = parseFloat(sliderLongitude.value).toFixed(2);
            updateVisualizations();
        });

        sliderEccentricity.addEventListener('input', () => {
            valueEccentricity.textContent = parseFloat(sliderEccentricity.value).toFixed(6);
            updateVisualizations();
        });

        // Parameters
        let angle, longitude, eccentricity;

        function updateParameters() {
            angle = parseFloat(sliderAngle.value);
            longitude = parseFloat(sliderLongitude.value);
            eccentricity = parseFloat(sliderEccentricity.value);
        }

        // Orbit Sketch p5.js instance
        const orbitSketch = (p) => {
            p.setup = () => {
                const canvas = p.createCanvas(500, 300);
                canvas.parent('orbit-canvas');
            };

            p.draw = () => {
                p.background(240); // #f0f0f0

                updateParameters();

                p.push();
                p.translate(p.width / 2, p.height / 2);

                // Calculate semi-major and semi-minor axes
                const a = p.width * 0.4; // Semi-major axis
                const b = a * Math.sqrt(1 - eccentricity * eccentricity); // Semi-minor axis

                // Rotate by longitude of periapsis
                p.rotate(p.radians(longitude));

                // Draw ellipse
                p.stroke(160, 82, 45); // #a0522d
                p.strokeWeight(2);
                p.noFill();
                p.ellipse(0, 0, a * 2, b * 2);

                // Draw axes
                p.stroke(0, 100);
                p.strokeWeight(1);
                p.line(-p.width/2, 0, p.width/2, 0);
                p.line(0, -p.height/2, 0, p.height/2);

                p.pop();
            };
        };

        // Equation of Time Plot p5.js instance
        const plotSketch = (p) => {
            let eotData = [];

            p.setup = () => {
                const canvas = p.createCanvas(800, 400);
                canvas.parent('plot-canvas');
                calculateEOTData();
            };

            function calculateEOTData() {
                updateParameters();

                const epsilon = p.radians(angle); // Obliquity in radians
                const omega = p.radians(longitude); // Longitude of periapsis in radians
                const e = eccentricity;

                eotData = [];
                let prevEOT = 0;

                for (let i = 0; i <= 360; i++) {
                    const M = p.radians(i); // Mean anomaly in radians

                    // Solve Kepler's equation using Newton's method
                    let E = M;
                    for (let iter = 0; iter < 10; iter++) {
                        const deltaE = (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
                        E = E - deltaE;
                    }

                    // Calculate true anomaly
                    const nu = 2 * Math.atan2(
                        Math.sqrt(1 + e) * Math.sin(E / 2),
                        Math.sqrt(1 - e) * Math.cos(E / 2)
                    );

                    // Calculate true longitude
                    const lambda = nu + omega;

                    // Calculate right ascension
                    const alpha = Math.atan2(
                        Math.cos(epsilon) * Math.sin(lambda),
                        Math.cos(lambda)
                    );

                    // Calculate Equation of Time in radians
                    let eotRad = M - alpha;

                    // Phase unwrapping
                    if (eotData.length > 0 && Math.abs(eotRad - prevEOT) > Math.PI) {
                        if (eotRad > prevEOT) {
                            eotRad -= 2 * Math.PI;
                        } else {
                            eotRad += 2 * Math.PI;
                        }
                    }

                    // Convert to minutes
                    const eotMinutes = eotRad * (720 / Math.PI);

                    eotData.push({ M: i, EOT: eotMinutes });
                    prevEOT = eotRad;
                }
            }

            p.draw = () => {
                p.background(255); // White background

                calculateEOTData();

                if (eotData.length === 0) return;

                // Find min and max EOT values for scaling
                const eotValues = eotData.map(d => d.EOT);
                const minEOT = Math.min(...eotValues);
                const maxEOT = Math.max(...eotValues);
                const eotRange = maxEOT - minEOT;
                const eotCenter = (minEOT + maxEOT) / 2;

                // Set up margins
                const margin = { top: 40, right: 40, bottom: 50, left: 60 };
                const plotWidth = p.width - margin.left - margin.right;
                const plotHeight = p.height - margin.top - margin.bottom;

                // Save drawing context
                p.push();
                p.translate(margin.left, margin.top);

                // Draw grid lines
                p.stroke(220); // Light gray
                p.strokeWeight(1);

                // Vertical grid lines (x-axis)
                for (let i = 0; i <= 6; i++) {
                    const x = (i / 6) * plotWidth;
                    p.line(x, 0, x, plotHeight);
                }

                // Horizontal grid lines (y-axis)
                for (let i = 0; i <= 10; i++) {
                    const y = (i / 10) * plotHeight;
                    p.line(0, y, plotWidth, y);
                }

                // Draw axes
                p.stroke(0); // Black
                p.strokeWeight(2);
                p.line(0, plotHeight, plotWidth, plotHeight); // X-axis
                p.line(0, 0, 0, plotHeight); // Y-axis

                // Draw axis labels
                p.fill(0);
                p.noStroke();
                p.textAlign('center');

                // X-axis labels (mean anomaly)
                for (let i = 0; i <= 6; i++) {
                    const x = (i / 6) * plotWidth;
                    p.text(i, x, plotHeight + 20);
                }
                p.text('Mean Anomaly (radians)', plotWidth / 2, plotHeight + 40);

                // Y-axis labels (minutes)
                p.textAlign('right');
                const yStep = eotRange / 5;
                for (let i = 0; i <= 5; i++) {
                    const value = minEOT + i * yStep;
                    const y = plotHeight - (i / 5) * plotHeight;
                    p.text(value.toFixed(1), -10, y + 5);
                }
                p.push();
                p.translate(-40, plotHeight / 2);
                p.rotate(-Math.PI / 2);
                p.textAlign('center');
                p.text('minutes', 0, 0);
                p.pop();

                // Draw the EOT curve
                p.stroke(255, 140, 0); // #ff8c00
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                for (let point of eotData) {
                    const x = (point.M / 360) * plotWidth;
                    const y = plotHeight - ((point.EOT - minEOT) / eotRange) * plotHeight;
                    p.vertex(x, y);
                }
                p.endShape();

                p.pop();
            };
        };

        // Initialize p5.js instances
        new p5(orbitSketch);
        new p5(plotSketch);

        function updateVisualizations() {
            // The p5.js draw functions are called continuously, so no need to manually call them
        }
    </script>
</body>
