            redrawScene();
        }

        function redrawScene() {
            // Get current values from controls
            const c = parseFloat(document.getElementById('slider-c').value);
            const h_C = parseFloat(document.getElementById('slider-h_C').value);
            const delta = parseFloat(document.getElementById('slider-delta').value);
            const plotRange = parseFloat(document.getElementById('slider-plot-range').value);
            const step = parseInt(document.querySelector('input[name="steps"]:checked').value);
            const verification = document.getElementById('check-verification').checked;

            // Update value displays
            document.getElementById('c-value').textContent = c.toFixed(1);
            document.getElementById('h_C-value').textContent = h_C.toFixed(1);
            document.getElementById('delta-value').textContent = delta.toFixed(2);
            document.getElementById('plot-range-value').textContent = plotRange.toFixed(2);

            // Clear canvas
            background('#E0E0E0');

            // Set up coordinate system
            push();
            translate(width / 2, height * 0.6);
            const currentScale = scale * plotRange;
            scale(currentScale, -currentScale);

            // Define initial points
            const F = { x: 0, y: 0 };
            const C = { x: 0, y: h_C };
            const D = { x: c / 2, y: 0 };
            const K = { x: c, y: 0 };

            // Calculate point M
            const M = calculateM(c, h_C, delta);
            const R = dist(M.x, M.y, C.x, C.y);

            // Calculate point B
            const x_B = 2 * M.x - c / 2;
            const B = { x: x_B, y: 0 };

            // Calculate point A
            const A = { x: x_B - c, y: 0 };

            // Calculate point E (for verification)
            const E = { x: c - x_B, y: 0 };

            // Draw based on step
            if (step >= 1) {
                // Draw line λ (x-axis)
                drawLine(-width / currentScale, 0, width / currentScale, 0, 1, '#000');

                // Draw points F, C, D
                drawPoint(F.x, F.y, 'F');
                drawPoint(C.x, C.y, 'C');
                drawPoint(D.x, D.y, 'D');

                // Draw line segment FC
                drawLine(F.x, F.y, C.x, C.y, 1, '#000');
            }

            if (step >= 2) {
                // Draw point K
                drawPoint(K.x, K.y, 'K');

                // Draw dashed lines
                drawDashedLine(C.x, C.y, D.x, D.y);
                drawDashedLine(D.x, D.y, K.x, K.y);

                // Draw point M
                drawPoint(M.x, M.y, 'M');

                // Draw dashed lines from M to C, D, K
                drawDashedLine(M.x, M.y, C.x, C.y);
                drawDashedLine(M.x, M.y, D.x, D.y);
                drawDashedLine(M.x, M.y, K.x, K.y);

                // Draw arc κ
                drawArc(M.x, M.y, R);
            }

            if (step >= 3) {
                // Draw point B
                drawPoint(B.x, B.y, 'B');
            }

            if (step >= 4) {
                // Draw point A
                drawPoint(A.x, A.y, 'A');
            }

            if (step >= 5) {
                // Draw triangle ABC
                drawLine(A.x, A.y, B.x, B.y, 3, '#000');
                drawLine(B.x, B.y, C.x, C.y, 3, '#000');
                drawLine(C.x, C.y, A.x, A.y, 3, '#000');
            }

            // Draw verification geometry if enabled
            if (verification && step >= 2) {
                drawPoint(E.x, E.y, 'E');
                drawDashedLine(C.x, C.y, E.x, E.y);
                drawDashedLine(K.x, K.y, B.x, B.y);
            }

            pop();
        }

        function calculateM(c, h_C, delta) {
            // Solve the system of equations to find point M
            // This is a simplified approach for demonstration
            const tan_delta = Math.tan(delta);

            // Coefficients for quadratic equation in y_M
            const a = 4 * h_C * h_C + c * c * tan_delta * tan_delta;
            const b = -4 * h_C * (c * c - 2 * h_C * h_C) * tan_delta;
            const c_val = -c * c * (c * c - 4 * h_C * h_C) + 4 * h_C * h_C * c * c;

            // Solve quadratic
            const discriminant = b * b - 4 * a * c_val;
            const y_M1 = (-b + Math.sqrt(discriminant)) / (2 * a);
            const y_M2 = (-b - Math.sqrt(discriminant)) / (2 * a);

            // Select the negative root
            const y_M = Math.min(y_M1, y_M2);

            // Calculate x_M from the perpendicular bisector equation
            const x_M = (2 * h_C * y_M - h_C * h_C + c * c / 4) / c;

            return { x: x_M, y: y_M };
        }

        function drawPoint(x, y, label) {
            push();
            fill('#000');
            noStroke();
            ellipse(x, y, 8 / scale, 8 / scale);

            // Draw label
            resetMatrix();
            translate(width / 2, height * 0.6);
            textFont('Arial', 14);
            fill('#000');
            text(label, x * scale, -y * scale + 15);
            pop();
        }

        function drawLine(x1, y1, x2, y2, weight = 1, color = '#000') {
            push();
            stroke(color);
            strokeWeight(weight / scale);
            line(x1, y1, x2, y2);
            pop();
        }

        function drawDashedLine(x1, y1, x2, y2) {
            push();
            drawingContext.setLineDash([5 / scale, 5 / scale]);
            stroke('#000');
            strokeWeight(1 / scale);
            line(x1, y1, x2, y2);
            pop();
        }

        function drawArc(x, y, r) {
            push();
            noFill();
            stroke('#000');
            strokeWeight(1 / scale);
            arc(x, y, 2 * r, 2 * r, 0, TWO_PI);
            pop();
        }

        // Event listeners
        document.getElementById('slider-c').addEventListener('input', redrawScene);
        document.getElementById('slider-h_C').addEventListener('input', redrawScene);
        document.getElementById('slider-delta').addEventListener('input', redrawScene);
        document.getElementById('slider-plot-range').addEventListener('input', redrawScene);
        document.getElementById('check-verification').addEventListener('change', redrawScene);

        document.querySelectorAll('input[name="steps"]').forEach(radio => {
            radio.addEventListener('change', redrawScene);
        });
    </script>
</body>
</html>
