<body>
    <div id="main-container">
        <h2>Gas Tube Simulation</h2>

        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
            <span id="label-z">0.</span>
        </div>

        <div id="plots-container">
            <div id="plot-intensity" class="plot-container"></div>
            <div id="plot-population" class="plot-container"></div>
            <div id="plot-coherence" class="plot-container"></div>
        </div>
    </div>

    <script>
        // Generate time array from -50 to 250 ns
        const t_values = Array.from({length: 301}, (_, i) => i - 50);

        // Data generation functions
        function generateRedIntensity(t) {
            return (t < -15 || t > 170) ? 0 : (t > 15 ? 1 : 0.5 * (1 + Math.tanh(t/4)));
        }

        function generateRedPopulation(t) {
            return (t < -15) ? 0 : 4.5 * (1 - Math.exp(-(t+15)/50));
        }

        function generateRedCoherence(t) {
            return (t < -15) ? 0 : 2.2 * (1 - Math.exp(-(t+15)/50));
        }

        function generateBlueIntensity(t, z) {
            const t_delay = z / 0.3;
            const t_prime = t - t_delay;
            const I_base = (t_prime < -15) ? 0 : (t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime/4)));
            const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
            return (t > 170 + t_delay) ? 0 : I_base * envelope;
        }

        function generateBluePopulation(t, z) {
            const t_delay = z / 0.3;
            const t_prime = t - t_delay;
            const scaling_factor_p = 1 - 0.45 * (z/32);
            const value = (t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime+15)/50));
            return (t > 170 + t_delay) ? 0 : scaling_factor_p * value;
        }

        function generateBlueCoherence(t, z) {
            const t_delay = z / 0.3;
            const t_prime = t - t_delay;
            const scaling_factor_c = 1 - 0.45 * (z/32);
            const value = (t_prime < -15) ? 0 : 2.2 * (1 - Math.exp(-(t_prime+15)/50));
            return (t > 170 + t_delay) ? 0 : scaling_factor_c * value;
        }

        // Generate initial red data
        const redIntensity = t_values.map(generateRedIntensity);
        const redPopulation = t_values.map(generateRedPopulation);
        const redCoherence = t_values.map(generateRedCoherence);

        // Initial blue data (z=0)
        let blueIntensity = [...redIntensity];
        let bluePopulation = [...redPopulation];
        let blueCoherence = [...redCoherence];

        // Common layout properties
        function getCommonLayout(yaxisConfig) {
            return {
                xaxis: {
                    title: "time (ns)",
                    range: [-50, 250],
                    zeroline: true
                },
                yaxis: yaxisConfig,
                margin: { l: 80, r: 20, t: 40, b: 40 },
                showlegend: true,
                legend: { x: 0.7, y: 0.9 },
                shapes: [
                    {
                        type: 'line',
                        x0: 0, y0: 0, x1: 0, y1: 1,
                        yref: 'paper',
                        line: { color: 'black', width: 2, dash: 'dash' }
                    },
                    {
                        type: 'line',
                        x0: 0, y0: 0, x1: 0, y1: 1,
                        yref: 'paper',
                        line: { color: 'black', width: 2, dash: 'dash' }
                    }
                ],
                annotations: []
            };
        }

        // Create plots
        const intensityData = [
            {
                x: t_values,
                y: redIntensity,
                name: "z = 0 meters",
                mode: 'lines',
                line: { color: 'red', width: 2 }
            },
            {
                x: t_values,
                y: blueIntensity,
                name: "z = 0. meters",
                mode: 'lines',
                line: { color: 'blue', width: 2, dash: 'dash' }
            }
        ];

        const populationData = [
            {
                x: t_values,
                y: redPopulation,
                name: "z = 0 meters",
                mode: 'lines',
                line: { color: 'red', width: 2 }
            },
            {
                x: t_values,
                y: bluePopulation,
                name: "z = 0. meters",
                mode: 'lines',
                line: { color: 'blue', width: 2, dash: 'dash' }
            }
        ];

        const coherenceData = [
            {
                x: t_values,
                y: redCoherence,
                name: "z = 0 meters",
                mode: 'lines',
                line: { color: 'red', width: 2 }
            },
            {
                x: t_values,
                y: blueCoherence,
                name: "z = 0. meters",
                mode: 'lines',
                line: { color: 'blue', width: 2, dash: 'dash' }
            }
        ];

        // Plot configurations
        const intensityLayout = getCommonLayout({
            title: 'intensity (arb)',
            range: [-0.1, 1.1]
        });
        intensityLayout.annotations.push({
            x: -45, y: 0.9, xref: 'x', yref: 'y',
            text: '(a)', showarrow: false
        }, {
            x: 0, y: 0.7, xref: 'x', yref: 'y',
            text: 'pulse<br>midpoint', showarrow: false,
            xanchor: 'left', yanchor: 'bottom'
        });

        const populationLayout = getCommonLayout({
            title: 'excited-state population × 10<sup>4</sup>',
            range: [-0.5, 5.5]
        });
        populationLayout.annotations.push({
            x: -45, y: 4.8, xref: 'x', yref: 'y',
            text: '(b)', showarrow: false
        });

        const coherenceLayout = getCommonLayout({
            title: 'coherence × 10<sup>2</sup>',
            range: [-0.2, 2.4]
        });
        coherenceLayout.annotations.push({
            x: -45, y: 2.1, xref: 'x', yref: 'y',
            text: '(c)', showarrow: false
        });

        // Initialize plots
        Plotly.newPlot('plot-intensity', intensityData, intensityLayout);
        Plotly.newPlot('plot-population', populationData, populationLayout);
        Plotly.newPlot('plot-coherence', coherenceData, coherenceLayout);

        // Slider event handler
        const slider = document.getElementById('slider-z');
        const label = document.getElementById('label-z');

        slider.addEventListener('input', function() {
            const z_current = parseFloat(this.value);

            // Update label
            if (Number.isInteger(z_current)) {
                label.textContent = z_current + '.';
            } else {
                label.textContent = z_current.toFixed(2);
            }

            // Calculate new blue curves
            blueIntensity = t_values.map(t => generateBlueIntensity(t, z_current));
            bluePopulation = t_values.map(t => generateBluePopulation(t, z_current));
            blueCoherence = t_values.map(t => generateBlueCoherence(t, z_current));

            // Update plot data
            const zLabel = Number.isInteger(z_current) ? 
                `z = ${z_current}. meters` : 
                `z = ${z_current.toFixed(2)} meters`;

            Plotly.restyle('plot-intensity', {
                y: [blueIntensity],
                name: [zLabel]
            }, [1]);

            Plotly.restyle('plot-population', {
                y: [bluePopulation],
                name: [zLabel]
            }, [1]);

            Plotly.restyle('plot-coherence', {
                y: [blueCoherence],
                name: [zLabel]
            }, [1]);

            // Update moving vertical line position
            const t_delay = z_current / 0.3;
            const relayoutUpdate = {
                'shapes[1].x0': t_delay,
                'shapes[1].x1': t_delay
            };

            Plotly.relayout('plot-intensity', relayoutUpdate);
            Plotly.relayout('plot-population', relayoutUpdate);
            Plotly.relayout('plot-coherence', relayoutUpdate);
        });
    </script>
</body>
</html>
