<body>
    <div id="demo-container">

        <!-- Control Panel Section -->
        <div id="control-panel">

            <!-- Heat Transfer Coefficient Slider -->
            <div>
                <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
                <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
                <span id="ua-value">0</span>
            </div>

            <!-- Reverse Reaction Pre-exponential Factor Slider -->
            <div>
                <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
                <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
                <span id="kr0-value">0.0 x 10¹²</span>
            </div>

            <!-- Feed Temperature Slider -->
            <div>
                <label for="slider-tf">feed temperature (K)</label>
                <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
                <span id="tf-value">265</span>
            </div>

            <!-- Residence Time Slider -->
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
                <span id="tau-value">400</span>
            </div>

        </div>

        <!-- Visualization Section -->
        <div id="plot-container"></div>
    </div>

    <script>
        // Constants
        const C_A0 = 10; // mmol/dm³
        const R = 1.987; // cal/mol K
        const E_f = 25000; // cal/mol
        const delta_H_rxn = -200000; // cal/mol
        const E_r = E_f - delta_H_rxn; // cal/mol
        const k_f0 = 1e15; // 1/s
        const rhoCp = 20000; // cal/(dm³ K)

        // Get slider elements
        const sliderUA = document.getElementById('slider-ua');
        const sliderKr0 = document.getElementById('slider-kr0');
        const sliderTf = document.getElementById('slider-tf');
        const sliderTau = document.getElementById('slider-tau');

        // Get value display elements
        const uaValue = document.getElementById('ua-value');
        const kr0Value = document.getElementById('kr0-value');
        const tfValue = document.getElementById('tf-value');
        const tauValue = document.getElementById('tau-value');

        // Format scientific notation
        function formatScientific(value) {
            const exponent = Math.floor(Math.log10(value));
            const mantissa = (value / Math.pow(10, exponent)).toFixed(1);
            return `${mantissa} × 10${formatSuperscript(exponent)}`;
        }

        function formatSuperscript(num) {
            const superscriptDigits = '⁰¹²³⁴⁵⁶⁷⁸⁹';
            const negative = num < 0;
            num = Math.abs(num);
            let result = '';

            if (num >= 10) {
                result += superscriptDigits[Math.floor(num / 10)];
                num = num % 10;
            }
            result += superscriptDigits[num];

            return negative ? '⁻' + result : result;
        }

        function updateGraph() {
            // Get slider values
            const UA = parseFloat(sliderUA.value);
            const kr0Slider = parseFloat(sliderKr0.value);
            const Tf = parseFloat(sliderTf.value);
            const tau = parseFloat(sliderTau.value);

            // Update display values
            uaValue.textContent = UA;
            kr0Value.textContent = `${kr0Slider.toFixed(1)} × 10¹²`;
            tfValue.textContent = Tf;
            tauValue.textContent = tau;

            // Generate temperature points
            const temperatures = [];
            for (let T = 250; T <= 400; T += 1) {
                temperatures.push(T);
            }

            // Calculate k_r0 from slider
            const k_r0 = kr0Slider * 1e12;

            // Calculate mass balance curve (green)
            const massBalanceConcentrations = temperatures.map(T => {
                const k_f = k_f0 * Math.exp(-E_f / (R * T));
                const k_r = k_r0 * Math.exp(-E_r / (R * T));
                const C_B = (tau * k_f * C_A0) / (1 + tau * (k_f + k_r));
                return C_B;
            });

            // Calculate energy balance curve (blue)
            const energyBalanceConcentrations = temperatures.map(T => {
                const C_B = (rhoCp * (T - Tf) + UA * tau * (T - Tf)) / (-delta_H_rxn);
                return C_B;
            });

            // Create traces
            const massBalanceTrace = {
                x: temperatures,
                y: massBalanceConcentrations,
                mode: 'lines',
                name: 'mass balance',
                line: {
                    color: '#008000',
                    width: 3
                }
            };

            const energyBalanceTrace = {
                x: temperatures,
                y: energyBalanceConcentrations,
                mode: 'lines',
                name: 'energy balance',
                line: {
                    color: '#0000FF',
                    width: 3
                }
            };

            // Create layout
            const layout = {
                title: {
                    text: 'Chemical Reactor Steady-State Analysis',
                    font: {
                        size: 18,
                        family: 'sans-serif'
                    },
                    x: 0.5
                },
                xaxis: {
                    title: {
                        text: 'temperature (K)',
                        font: {
                            size: 14
                        }
                    },
                    range: [250, 400],
                    gridcolor: '#e0e0e0',
                    zerolinecolor: '#cccccc'
                },
                yaxis: {
                    title: {
                        text: 'product concentration (mmol/dm³)',
                        font: {
                            size: 14
                        }
                    },
                    range: [0, 12],
                    gridcolor: '#e0e0e0',
                    zerolinecolor: '#cccccc'
                },
                showlegend: false,
                margin: {
                    l: 70,
                    r: 40,
                    t: 60,
                    b: 60
                },
                annotations: [
                    {
                        x: 360,
                        y: massBalanceConcentrations[110] || 6,
                        text: 'mass balance',
                        showarrow: false,
                        font: {
                            color: '#008000',
                            size: 14,
                            family: 'sans-serif'
                        },
                        xshift: 10,
                        yshift: 10
                    },
                    {
                        x: 280,
                        y: energyBalanceConcentrations[30] || 4,
                        text: 'energy balance',
                        showarrow: false,
                        font: {
                            color: '#0000FF',
                            size: 14,
                            family: 'sans-serif'
                        },
                        xshift: 10,
                        yshift: -10
                    }
                ],
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            // Update plot
            const data = [massBalanceTrace, energyBalanceTrace];
            Plotly.react('plot-container', data, layout, {
                responsive: true,
                displayModeBar: false
            });
        }

        // Add event listeners to sliders
        sliderUA.addEventListener('input', updateGraph);
        sliderKr0.addEventListener('input', updateGraph);
        sliderTf.addEventListener('input', updateGraph);
        sliderTau.addEventListener('input', updateGraph);

        // Initial plot
        updateGraph();
    </script>
</body>
</html>
