                <label for="radio-envelope">envelope</label>
                <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed centers">
                <label for="radio-fixed-centers">fixed centers</label>
            </div>
            <div>
                <span>envelope to show</span>
                <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
                <label for="radio-inner">inner</label>
                <input type="radio" name="envelope-type" id="radio-outer" value="outer">
                <label for="radio-outer">outer</label>
                <input type="radio" name="envelope-type" id="radio-both" value="both">
                <label for="radio-both">both</label>
            </div>
            <div>
                <label for="slider-rotation">rotation angle</label>
                <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
                <span id="label-rotation">0.000</span>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let k = 0.14;
        let phi = 0;
        let refFrame = 'epitrochoid';
        let envelopeType = 'inner';
        let scale = 150;

        function setup() {
            let canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');

            // Set up event listeners
            document.getElementById('slider-eccentricity').addEventListener('input', function(e) {
                k = parseFloat(e.target.value);
                document.getElementById('label-eccentricity').textContent = k.toFixed(3);
            });

            document.getElementById('slider-rotation').addEventListener('input', function(e) {
                phi = parseFloat(e.target.value);
                document.getElementById('label-rotation').textContent = phi.toFixed(3);
            });

            document.querySelectorAll('input[name="ref-frame"]').forEach(radio => {
                radio.addEventListener('change', function(e) {
                    refFrame = e.target.value;
                });
            });

            document.querySelectorAll('input[name="envelope-type"]').forEach(radio => {
                radio.addEventListener('change', function(e) {
                    envelopeType = e.target.value;
                });
            });
        }

        function draw() {
            background(255);

            push();
            translate(width/2, height/2);

            // Draw axes
            drawAxes();

            // Calculate curves
            let epitrochoidPoints = calculateEpitrochoid();
            let innerEnvelopePoints = calculateInnerEnvelope();
            let outerEnvelopePoints = calculateOuterEnvelope();

            // Apply transformations based on reference frame
            push();

            if (refFrame === 'epitrochoid') {
                drawEpitrochoid(epitrochoidPoints);
                drawEnvelope(innerEnvelopePoints, outerEnvelopePoints);
            } else if (refFrame === 'envelope') {
                drawEnvelope(innerEnvelopePoints, outerEnvelopePoints);
                drawEpitrochoid(epitrochoidPoints);
            } else if (refFrame === 'fixed centers') {
                drawEpitrochoid(epitrochoidPoints);

                // Orange curve rotates and translates
                let phiShaft = 2 * phi;
                let theta = -phiShaft / 3;
                let Cx = k * cos(phiShaft);
                let Cy = k * sin(phiShaft);

                push();
                translate(Cx * scale, Cy * scale);
                rotate(theta);
                drawEnvelope(innerEnvelopePoints, outerEnvelopePoints);
                pop();
            }

            pop();
            pop();
        }

        function drawAxes() {
            stroke(150);
            strokeWeight(1);

            // X-axis
            line(-width/2, 0, width/2, 0);
            // Y-axis
            line(0, -height/2, 0, height/2);

            // Tick marks and labels
            textAlign(CENTER, CENTER);
            textSize(12);
            fill(150);

            for (let i = -3; i <= 3; i++) {
                if (i !== 0) {
                    let x = i * scale;
                    line(x, -5, x, 5);
                    text(i.toFixed(1), x, 15);

                    let y = i * scale;
                    line(-5, y, 5, y);
                    text(i.toFixed(1), -15, y);
                }
            }

            // Origin
            text('0', -15, 15);
        }

        function calculateEpitrochoid() {
            let points = [];
            let resolution = 200;

            for (let i = 0; i <= resolution; i++) {
                let t = (i / resolution) * TWO_PI;
                let x = cos(t) - k * cos(3*t);
                let y = sin(t) - k * sin(3*t);
                points.push({x: x * scale, y: y * scale});
            }

            return points;
        }

        function calculateInnerEnvelope() {
            let points = [];
            let resolution = 200;
            let R = 1;
            let e = k;

            for (let i = 0; i <= resolution; i++) {
                let t = (i / resolution) * TWO_PI;
                let x = (2/3) * R * cos(t) + e * cos(2*t);
                let y = (2/3) * R * sin(t) - e * sin(2*t);
                points.push({x: x * scale, y: y * scale});
            }

            return points;
        }

        function calculateOuterEnvelope() {
            let points = [];
            let resolution = 200;
            let R = 1;
            let e = k;

            for (let i = 0; i <= resolution; i++) {
                let t = (i / resolution) * TWO_PI;
                let x = (2/3) * R * cos(t) - e * cos(2*t);
                let y = (2/3) * R * sin(t) + e * sin(2*t);
                points.push({x: x * scale, y: y * scale});
            }

            return points;
        }

        function drawEpitrochoid(points) {
            stroke(50, 100, 200);
            strokeWeight(2);
            noFill();

            beginShape();
            for (let p of points) {
                vertex(p.x, p.y);
            }
            endShape(CLOSE);
        }

        function drawEnvelope(innerPoints, outerPoints) {
            stroke(230, 150, 0);
            strokeWeight(2);
            noFill();

            if (envelopeType === 'inner' || envelopeType === 'both') {
                beginShape();
                for (let p of innerPoints) {
                    vertex(p.x, p.y);
                }
                endShape(CLOSE);
            }

            if (envelopeType === 'outer') {
                beginShape();
                for (let p of outerPoints) {
                    vertex(p.x, p.y);
                }
                endShape(CLOSE);
            } else if (envelopeType === 'both') {
                push();
                stroke(230, 150, 0, 150);
                beginShape();
                for (let p of outerPoints) {
                    vertex(p.x, p.y);
                }
                endShape(CLOSE);
                pop();
            }
        }
    </script>
</body>
</html>
