head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>stability of multilayered emulsionsh4>

            <div class="slider-group">
                <label for="slider-phi">\(\phi\)</label>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
                <span id="phi-value-display" class="value-display">0.0050span>
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-gamma-sat">\(\Gamma_{sat}\) (mg/m²)</label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
                <span id="gamma-sat-value-display" class="value-display">1.5span>
            </div>

            <div class="slider-group">
                <label for="slider-r">\(r\) (nm)</label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
                <span id="r-value-display" class="value-display">300span>
            </div>

            <div class="slider-group">
                <label for="slider-rpe">\(r_{pe}\) (nm)</label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
                <span id="rpe-value-display" class="value-display">20span>
            </div>

            <div class="slider-group">
                <label for="slider-mw">\(M_W\) (kDa)</label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
                <span id="mw-value-display" class="value-display">166span>
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-phi-axis">\(\phi\) axis max.</label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
                <span id="phi-axis-value-display" class="value-display">0.05span>
            </div>

            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
                <span id="c-axis-value-display" class="value-display">2.0span>
            </div>
        </div>

        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display">
                    <span style="color:blue;">\(y_1(\phi)\)</span>, 
                    <span style="color:red;">\(y_2(\phi)\)</span>, and 
                    <span style="color:green;">\(y_3(\phi)\)</span>
                </p>
                <p id="phimax-display">\(\phi_{max} = 0.0123\)</p>
                <p id="range-display">Stability range: 0.1234 to 0.5678 kg/m³p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Constants
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        // Get all input elements
        const phiSlider = document.getElementById('slider-phi');
        const gammaSatSlider = document.getElementById('slider-gamma-sat');
        const rSlider = document.getElementById('slider-r');
        const rpeSlider = document.getElementById('slider-rpe');
        const mwSlider = document.getElementById('slider-mw');
        const phiAxisSlider = document.getElementById('slider-phi-axis');
        const cAxisSlider = document.getElementById('slider-c-axis');

        // Get display elements
        const phiValueDisplay = document.getElementById('phi-value-display');
        const gammaSatValueDisplay = document.getElementById('gamma-sat-value-display');
        const rValueDisplay = document.getElementById('r-value-display');
        const rpeValueDisplay = document.getElementById('rpe-value-display');
        const mwValueDisplay = document.getElementById('mw-value-display');
        const phiAxisValueDisplay = document.getElementById('phi-axis-value-display');
        const cAxisValueDisplay = document.getElementById('c-axis-value-display');

        const formulaDisplay = document.getElementById('formula-display');
        const phimaxDisplay = document.getElementById('phimax-display');
        const rangeDisplay = document.getElementById('range-display');

        // Mathematical functions
        function y1(phi, gamma_sat, r) {
            return (3 * gamma_sat / r) * C_UNIT_CONVERSION * phi;
        }

        function y2(phi, gamma_sat, r) {
            return y1(phi, gamma_sat, r) / (1 - phi);
        }

        function y3(phi, gamma_sat, r, mw, rpe) {
            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (rpe ** 3);
            return A * phi + B / phi;
        }

        function calculatePhiMax(gamma_sat, r, mw, rpe) {
            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (rpe ** 3);
            return Math.sqrt(B / A);
        }

        // Generate data array
        function generateDataRange(start, end, steps) {
            const data = [];
            const step = (end - start) / (steps - 1);
            for (let i = 0; i < steps; i++) {
                data.push(start + i * step);
            }
            return data;
        }

        // Create plot data
        function createPlotData() {
            const gamma_sat = parseFloat(gammaSatSlider.value);
            const r = parseFloat(rSlider.value);
            const rpe = parseFloat(rpeSlider.value);
            const mw = parseFloat(mwSlider.value);
            const phi_max = calculatePhiMax(gamma_sat, r, mw, rpe);
            const phiRange = generateDataRange(0.001, parseFloat(phiAxisSlider.value), 100);

            const phiRangeShaded = generateDataRange(0.001, phi_max, 50);

            const y1Data = phiRange.map(phi => y1(phi, gamma_sat, r));
            const y2Data = phiRange.map(phi => y2(phi, gamma_sat, r));
            const y3Data = phiRange.map(phi => y3(phi, gamma_sat, r, mw, rpe));

            const y1Shaded = phiRangeShaded.map(phi => y1(phi, gamma_sat, r));
            const y2Shaded = phiRangeShaded.map(phi => y2(phi, gamma_sat, r));
            const y3Shaded = phiRangeShaded.map(phi => y3(phi, gamma_sat, r, mw, rpe));

            return {
                x: phiRange,
                y1: y1Data,
                y2: y2Data,
                y3: y3Data,
                xShaded: phiRangeShaded,
                y1Shaded: y1Shaded,
                y2Shaded: y2Shaded,
                y3Shaded: y3Shaded,
                phiMax: phi_max
            };
        }

        // Create plot
        function createPlot() {
            const phi_val = parseFloat(phiSlider.value);
            const gamma_sat = parseFloat(gammaSatSlider.value);
            const r = parseFloat(rSlider.value);
            const rpe = parseFloat(rpeSlider.value);
            const mw = parseFloat(mwSlider.value);
            const phi_max = calculatePhiMax(gamma_sat, r, mw, rpe);
            const data = createPlotData();

            const traces = [
                // y1 curve
                {
                    x: data.x,
                    y: data.y1,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'blue'},
                    name: 'y1'
                },
                // y2 curve
                {
                    x: data.x,
                    y: data.y2,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'red'},
                    name: 'y2'
                },
                // y3 curve
                {
                    x: data.x,
                    y: data.y3,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'green'},
                    name: 'y3'
                },
                // Shaded area traces
                {
                    x: data.xShaded,
                    y: data.y2Shaded,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'transparent'},
                    fill: 'none',
                    showlegend: false
                },
                {
                    x: data.xShaded,
                    y: data.y3Shaded,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'transparent'},
                    fill: 'tonexty',
                    fillcolor: 'rgba(173, 216, 230, 0.5)',
                    showlegend: false
                },
                // Markers
                {
                    x: [phi_val, phi_val, phi_val],
                    y: [y1(phi_val, gamma_sat, r), y2(phi_val, gamma_sat, r), y3(phi_val, gamma_sat, r, mw, rpe)],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: ['blue', 'red', 'green'],
                        size: 10
                    },
                    showlegend: false
                }
            ];

            const layout = {
                xaxis: {
                    title: 'ϕ (volume fraction)',
                    range: [0, parseFloat(phiAxisSlider.value)]
                },
                yaxis: {
                    title: 'C (kg/m³)',
                    range: [0, parseFloat(cAxisSlider.value)]
                },
                shapes: [
                    {
                        type: 'line',
                        x0: phi_val,
                        x1: phi_val,
                        y0: 0,
                        y1: parseFloat(cAxisSlider.value),
                        line: {
                            color: 'grey',
                            dash: 'dash'
                        }
                    }
                ],
                showlegend: false,
                margin: {t: 20, r: 20, b: 40, l: 50}
            };

            Plotly.newPlot('plot-container', traces, layout, {responsive: true});
        }

        // Update plot (partial update for phi slider)
        function updatePlot() {
            const phi_val = parseFloat(phiSlider.value);
            const gamma_sat = parseFloat(gammaSatSlider.value);
            const r = parseFloat(rSlider.value);
            const rpe = parseFloat(rpeSlider.value);
            const mw = parseFloat(mwSlider.value);

            const update = {
                x: [[phi_val, phi_val, phi_val]],
                y: [[y1(phi_val, gamma_sat, r), y2(phi_val, gamma_sat, r), y3(phi_val, gamma_sat, r, mw, rpe)]]
            };

            const updateMarker = 5; // Index of the marker trace

            Plotly.restyle('plot-container', update, [updateMarker]);

            const layoutUpdate = {
                'shapes[0].x0': phi_val,
                'shapes[0].x1': phi_val
            };

            Plotly.relayout('plot-container', layoutUpdate);
        }

        // Update info display
        function updateInfoDisplay() {
            const gamma_sat = parseFloat(gammaSatSlider.value);
            const r = parseFloat(rSlider.value);
            const rpe = parseFloat(rpeSlider.value);
            const mw = parseFloat(mwSlider.value);
            const phi_val = parseFloat(phiSlider.value);

            const phi_max = calculatePhiMax(gamma_sat, r, mw, rpe);
            const lower_bound = y2(phi_val, gamma_sat, r);
            const upper_bound = y3(phi_val, gamma_sat, r, mw, rpe);

            phimaxDisplay.innerHTML = `\(\phi_{max} = ${phi_max.toFixed(6)}\)`;
            rangeDisplay.innerHTML = `Stability range: ${lower_bound.toFixed(6)} to ${upper_bound.toFixed(6)} kg/m³`;

            MathJax.typesetPromise([phimaxDisplay]);
        }

        // Event handlers
        phiSlider.addEventListener('input', function() {
            phiValueDisplay.textContent = parseFloat(this.value).toFixed(4);
            updatePlot();
            updateInfoDisplay();
        });

        gammaSatSlider.addEventListener('input', function() {
            gammaSatValueDisplay.textContent = this.value;
            createPlot();
            updateInfoDisplay();
        });

        rSlider.addEventListener('input', function() {
            rValueDisplay.textContent = this.value;
            createPlot();
            updateInfoDisplay();
        });

        rpeSlider.addEventListener('input', function() {
            rpeValueDisplay.textContent = this.value;
            createPlot();
            updateInfoDisplay();
        });

        mwSlider.addEventListener('input', function() {
            mwValueDisplay.textContent = this.value;
            createPlot();
            updateInfoDisplay();
        });

        phiAxisSlider.addEventListener('input', function() {
            phiAxisValueDisplay.textContent = this.value;
            phiSlider.max = this.value;
            createPlot();
        });

        cAxisSlider.addEventListener('input', function() {
            cAxisValueDisplay.textContent = this.value;
            const layoutUpdate = {
                'yaxis.range': [0, parseFloat(this.value)],
                'shapes[0].y1': parseFloat(this.value)
            };
            Plotly.relayout('plot-container', layoutUpdate);
        });

        // Initialize
        window.addEventListener('load', function() {
            // Initialize displays
            phiValueDisplay.textContent = parseFloat(phiSlider.value).toFixed(4);
            gammaSatValueDisplay.textContent = gammaSatSlider.value;
            rValueDisplay.textContent = rSlider.value;
            rpeValueDisplay.textContent = rpeSlider.value;
            mwValueDisplay.textContent = mwSlider.value;
            phiAxisValueDisplay.textContent = phiAxisSlider.value;
            cAxisValueDisplay.textContent = cAxisSlider.value;

            // Set initial phi slider max
            phiSlider.max = phiAxisSlider.value;

            // Create initial plot
            createPlot();
            updateInfoDisplay();

            // Typeset MathJax
            MathJax.typesetPromise();
        });
    </script>
