<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-time">time relative to collision (s)</label>
                <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
                <span id="label-time">t = -10.0 sspan>
            </div>

            <div class="control-group">
                <h4>object 1h4>
                <label for="slider-m1">mass (kg)</label>
                <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
                <span id="label-m1">m₁ = 5.00 kgspan>

                <label for="slider-v1">speed (m/s)</label>
                <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
                <span id="label-v1">v₁ = 10.0 m/sspan>
            </div>

            <div class="control-group">
                <h4>object 2h4>
                <label for="slider-m2">mass (kg)</label>
                <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
                <span id="label-m2">m₂ = 3.00 kgspan>

                <label for="slider-v2">speed (m/s)</label>
                <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
                <span id="label-v2">v₂ = 5.00 m/sspan>
            </div>

            <div class="control-group">
                <label>observer velocity (m/s)</label>
                <div id="joystick-container">
                    <div id="joystick-knob"></div>
                </div>
                <div class="velocity-labels">
                    <span id="label-v0x">v₀ₓ = 1.10span>
                    <span id="label-v0y">v₀ᵧ = 1.10span>
                </div>
            </div>
        </div>

        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // Global state
        let state = {
            time: -10,
            m1: 5,
            v1: 10,
            m2: 3,
            v2: 5,
            v0x: 1.1,
            v0y: 1.1
        };

        // Physics calculations
        function calculatePhysics() {
            // CM frame calculations
            const s1_cm = state.v1;
            const s2_cm = (state.m1 / state.m2) * s1_cm;

            // Initial velocities in CM frame
            const u1_cm = createVector(s1_cm, 0);
            const u2_cm = createVector(-s2_cm, 0);

            // Final velocities in CM frame (90-degree scattering, -45 rotation)
            const v1_cm = p5.Vector.fromAngle(u1_cm.heading() - PI/4).mult(s1_cm);
            const v2_cm = p5.Vector.fromAngle(u2_cm.heading() - PI/4).mult(s2_cm);

            // Observer velocity
            const v0 = createVector(state.v0x, state.v0y);

            // Initial velocities in observer frame (for display)
            const u1_obs = p5.Vector.add(u1_cm, v0);
            const u2_obs = p5.Vector.add(u2_cm, v0);

            // Current velocities based on time
            const v1_current = state.time < 0 ? u1_cm : v1_cm;
            const v2_current = state.time < 0 ? u2_cm : v2_cm;

            // Current velocities in observer frame
            const v1_obs_current = p5.Vector.add(v1_current, v0);
            const v2_obs_current = p5.Vector.add(v2_current, v0);

            // Current positions
            const pos1_cm = p5.Vector.mult(v1_current, state.time);
            const pos2_cm = p5.Vector.mult(v2_current, state.time);

            const pos1_obs = p5.Vector.add(pos1_cm, p5.Vector.mult(v0, state.time));
            const pos2_obs = p5.Vector.add(pos2_cm, p5.Vector.mult(v0, state.time));

            // Trajectory endpoints
            const t1_start = -30;
            const t2_end = 30;

            const pos1_obs_start = p5.Vector.add(p5.Vector.mult(u1_cm, t1_start), p5.Vector.mult(v0, t1_start));
            const pos1_obs_end = p5.Vector.add(p5.Vector.mult(v1_cm, t2_end), p5.Vector.mult(v0, t2_end));
            const pos2_obs_start = p5.Vector.add(p5.Vector.mult(u2_cm, t1_start), p5.Vector.mult(v0, t1_start));
            const pos2_obs_end = p5.Vector.add(p5.Vector.mult(v2_cm, t2_end), p5.Vector.mult(v0, t2_end));

            // Display values (based on pre-collision state)
            const v0_mag = v0.mag();
            const v1_display = u1_obs.mag();
            const v2_display = u2_obs.mag();
            const energy = 0.5 * state.m1 * v1_display * v1_display + 0.5 * state.m2 * v2_display * v2_display;

            return {
                pos1_obs, pos2_obs,
                pos1_obs_start, pos1_obs_end,
                pos2_obs_start, pos2_obs_end,
                v0, v0_mag, v1_display, v2_display, energy,
                m1: state.m1, m2: state.m2
            };
        }

        // p5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                const container = document.getElementById('canvas-container');
                const canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                canvas.parent('canvas-container');
            };

            p.draw = function() {
                p.background(255);

                const physics = calculatePhysics();

                // Canvas coordinate system (center origin, y up)
                p.translate(p.width/2, p.height/2);
                p.scale(1, -1);

                // Scale factor for visualization
                const scale = 8;

                // Draw trajectories
                p.push();
                drawingContext.setLineDash([5, 5]);
                p.strokeWeight(2);

                // Particle 1 trajectory (red)
                p.stroke(255, 0, 0);
                p.line(
                    physics.pos1_obs_start.x * scale,
                    physics.pos1_obs_start.y * scale,
                    physics.pos1_obs_end.x * scale,
                    physics.pos1_obs_end.y * scale
                );

                // Particle 2 trajectory (blue)
                p.stroke(0, 0, 255);
                p.line(
                    physics.pos2_obs_start.x * scale,
                    physics.pos2_obs_start.y * scale,
                    physics.pos2_obs_end.x * scale,
                    physics.pos2_obs_end.y * scale
                );
                p.pop();

                // Draw particles
                p.noStroke();

                // Particle 1 (red)
                p.fill(255, 0, 0);
                const r1 = 10 * Math.sqrt(physics.m1 / 5);
                p.circle(
                    physics.pos1_obs.x * scale,
                    physics.pos1_obs.y * scale,
                    r1 * 2
                );

                // Particle 2 (blue)
                p.fill(0, 0, 255);
                const r2 = 10 * Math.sqrt(physics.m2 / 5);
                p.circle(
                    physics.pos2_obs.x * scale,
                    physics.pos2_obs.y * scale,
                    r2 * 2
                );

                // Draw observer velocity vector (from bottom left)
                p.push();
                p.stroke(0);
                p.strokeWeight(3);
                const arrowStart = createVector(-p.width/2 + 50, -p.height/2 + 50);
                const arrowEnd = p5.Vector.add(arrowStart, p5.Vector.mult(physics.v0, -20));

                // Arrow line
                p.line(arrowStart.x, arrowStart.y, arrowEnd.x, arrowEnd.y);

                // Arrow head
                const angle = p5.Vector.sub(arrowEnd, arrowStart).heading();
                p.push();
                p.translate(arrowEnd.x, arrowEnd.y);
                p.rotate(angle);
                p.fill(0);
                p.triangle(0, 0, -10, -5, -10, 5);
                p.pop();

                p.pop();

                // Display text values
                p.push();
                p.scale(1, -1);
                p.fill(0);
                p.noStroke();
                p.textAlign(LEFT);
                p.textSize(16);
                p.text(`v₀ = ${physics.v0_mag.toFixed(2)} m/s`, -p.width/2 + 20, p.height/2 - 120);
                p.text(`v₁ = ${physics.v1_display.toFixed(2)} m/s`, -p.width/2 + 20, p.height/2 - 90);
                p.text(`v₂ = ${physics.v2_display.toFixed(2)} m/s`, -p.width/2 + 20, p.height/2 - 60);
                p.text(`E = ${physics.energy.toFixed(2)} J`, -p.width/2 + 20, p.height/2 - 30);
                p.pop();
            };

            p.windowResized = function() {
                const container = document.getElementById('canvas-container');
                p.resizeCanvas(container.clientWidth, container.clientHeight);
            };
        };

        new p5(sketch);

        // Slider event listeners
        function setupSlider(sliderId, labelId, property, getter = val => val) {
            const slider = document.getElementById(sliderId);
            const label = document.getElementById(labelId);

            slider.addEventListener('input', function() {
                const value = parseFloat(this.value);
                if (property.includes('.')) {
                    const props = property.split('.');
                    state[props[0]][props[1]] = value;
                } else {
                    state[property] = value;
                }

                label.textContent = getter(value);

                // Special case: time slider doesn't update display values
                if (property !== 'time') {
                    updateDisplayValues();
                }
            });
        }

        setupSlider('slider-time', 'label-time', 'time', t => `t = ${t.toFixed(1)} s`);
        setupSlider('slider-m1', 'label-m1', 'm1', m => `m₁ = ${m.toFixed(2)} kg`);
        setupSlider('slider-v1', 'label-v1', 'v1', v => `v₁ = ${v.toFixed(1)} m/s`);
        setupSlider('slider-m2', 'label-m2', 'm2', m => `m₂ = ${m.toFixed(2)} kg`);
        setupSlider('slider-v2', 'label-v2', 'v2', v => `v₂ = ${v.toFixed(1)} m/s`);

        // Joystick implementation
        const joystickContainer = document.getElementById('joystick-container');
        const joystickKnob = document.getElementById('joystick-knob');
        const labelV0x = document.getElementById('label-v0x');
        const labelV0y = document.getElementById('label-v0y');

        let isDragging = false;

        function updateJoystick(x, y) {
            const rect = joystickContainer.getBoundingClientRect();
            const centerX = rect.width / 2;
            const centerY = rect.height / 2;

            // Mouse position relative to container center
            const relX = x - rect.left - centerX;
            const relY = y - rect.top - centerY;

            // Calculate distance from center
            const distance = Math.sqrt(relX * relX + relY * relY);
            const maxDistance = centerX - 20;

            // Constrain to container
            let knobX, knobY;
            if (distance <= maxDistance) {
                knobX = relX;
                knobY = relY;
            } else {
                const scale = maxDistance / distance;
                knobX = relX * scale;
                knobY = relY * scale;
            }

            // Update knob position
            joystickKnob.style.left = `${centerX + knobX}px`;
            joystickKnob.style.top = `${centerY + knobY}px`;

            // Update state
            state.v0x = (knobX / maxDistance) * 5;
            state.v0y = -(knobY / maxDistance) * 5;

            // Update labels
            labelV0x.textContent = `v₀ₓ = ${state.v0x.toFixed(2)}`;
            labelV0y.textContent = `v₀ᵧ = ${state.v0y.toFixed(2)}`;

            updateDisplayValues();
        }

        function updateDisplayValues() {
            const physics = calculatePhysics();
            // Values are displayed in the canvas text
        }

        // Mouse events
        joystickContainer.addEventListener('mousedown', function(e) {
            isDragging = true;
            updateJoystick(e.clientX, e.clientY);
        });

        document.addEventListener('mousemove', function(e) {
            if (isDragging) {
                updateJoystick(e.clientX, e.clientY);
            }
        });

        document.addEventListener('mouseup', function() {
            isDragging = false;
        });

        // Touch events for mobile
        joystickContainer.addEventListener('touchstart', function(e) {
            isDragging = true;
            const touch = e.touches[0];
            updateJoystick(touch.clientX, touch.clientY);
            e.preventDefault();
        });

        document.addEventListener('touchmove', function(e) {
            if (isDragging) {
                const touch = e.touches[0];
                updateJoystick(touch.clientX, touch.clientY);
                e.preventDefault();
            }
        });

        document.addEventListener('touchend', function() {
            isDragging = false;
        });

        // Initialize joystick position
        joystickKnob.style.left = '110px';
        joystickKnob.style.top = '110px';
    </script>
