  <div class="container">
    <!-- Control Panel Section -->
    <div class="panel">
      <div class="controls-row">
        <label for="shift-slider">shift</label>
        <input id="shift-slider" type="range" min="0" max="25" step="1" value="12" />
      </div>
      <div class="controls-row">
        <input id="text-input" type="text" value="abcd" list="phrase-list" placeholder="Type plaintext here..." />
        <datalist id="phrase-list">
          <option value="abcd"></option>
          <option value="test phrase"></option>
          <option value="to be or not to be"></option>
          <option value="the quick brown fox jumped over the lazy dog"></option>
        </datalist>
      </div>
    </div>

    <!-- Display Area Section -->
    <div class="display-area">
      <!-- Visualization Section -->
      <div class="alphabet-visualization">
        <div class="section-title">Alphabet mapping</div>
        <div id="plaintext-alphabet" class="alphabet-row" aria-label="plaintext alphabet row">
          <!-- a-z letters will be inserted here -->
        </div>
        <div id="ciphertext-alphabet" class="alphabet-row" aria-label="ciphertext alphabet row">
          <!-- shifted A-Z letters will be inserted here -->
        </div>
      </div>

      <!-- Key Section -->
      <div class="keys">
        <div class="section-title">Keys</div>
        <p id="encryption-key-display">encryption key: 12</p>
        <p id="decryption-key-display">decryption key: 14</p>
      </div>

      <!-- Text Output Section -->
      <div class="text-output">
        <div class="section-title">Text</div>
        <p id="plaintext-output"><span class="label blue">plaintext :</span> abcd</p>
        <p id="ciphertext-output"><span class="label green">ciphertext:</span> MNOP</p>
      </div>
    </div>
  </div>

<script>
  (function(){
    const lowercase = 'abcdefghijklmnopqrstuvwxyz';
    const uppercase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';

    const shiftSlider = document.getElementById('shift-slider');
    const textInput = document.getElementById('text-input');

    const plaintextAlphabetDiv = document.getElementById('plaintext-alphabet');
    const ciphertextAlphabetDiv = document.getElementById('ciphertext-alphabet');

    const encryptionKeyDisplay = document.getElementById('encryption-key-display');
    const decryptionKeyDisplay = document.getElementById('decryption-key-display');

    const plaintextOutput = document.getElementById('plaintext-output');
    const ciphertextOutput = document.getElementById('ciphertext-output');

    function escapeHTML(str) {
      return String(str)
        .replace(/&/g, '&amp;')
        .replace(/</g, '&lt;')
        .replace(/>/g, '&gt;')
        .replace(/"/g, '&quot;')
        .replace(/'/g, '&#39;');
    }

    function buildPlaintextAlphabetOnce() {
      if (plaintextAlphabetDiv.childElementCount > 0) return;
      for (let i = 0; i < 26; i++) {
        const span = document.createElement('span');
        span.className = 'letter';
        span.textContent = lowercase[i];
        plaintextAlphabetDiv.appendChild(span);
      }
    }

    function buildCiphertextAlphabet(shift) {
      ciphertextAlphabetDiv.innerHTML = '';
      for (let i = 0; i < 26; i++) {
        const span = document.createElement('span');
        span.className = 'letter';
        const mapped = uppercase[(i + shift) % 26];
        span.textContent = mapped;
        ciphertextAlphabetDiv.appendChild(span);
      }
    }

    function encrypt(text, shift) {
      let out = '';
      for (let i = 0; i < text.length; i++) {
        const ch = text[i];
        const code = ch.charCodeAt(0);
        if (code >= 97 && code <= 122) { // a-z
          const idx = code - 97;
          const shifted = (idx + shift) % 26;
          out += uppercase[shifted];
        } else {
          out += ch;
        }
      }
      return out;
    }

    function updateCipher() {
      const shiftValue = parseInt(shiftSlider.value, 10) || 0;
      const decKey = (26 - shiftValue) % 26;
      const text = textInput.value;

      // Visualization update
      buildPlaintextAlphabetOnce();
      buildCiphertextAlphabet(shiftValue);

      // Keys
      encryptionKeyDisplay.textContent = 'encryption key: ' + shiftValue;
      decryptionKeyDisplay.textContent = 'decryption key: ' + decKey;

      // Text output
      const cipher = encrypt(text, shiftValue);
      plaintextOutput.innerHTML = '<span class="label blue">plaintext :</span> ' + escapeHTML(text);
      ciphertextOutput.innerHTML = '<span class="label green">ciphertext:</span> ' + escapeHTML(cipher);
    }

    // Event listeners
    shiftSlider.addEventListener('input', updateCipher);
    textInput.addEventListener('input', updateCipher);

    // Initialize defaults on page load
    window.addEventListener('DOMContentLoaded', function() {
      shiftSlider.value = '12';
      textInput.value = 'abcd';
      updateCipher();
    });
  })();
</script>
</body>
</html>
