  </style>
</head>
<body>
  <!-- Control Panel Section -->
  <div id="control-panel">
    <label for="slider-angle">slope θ</label>
    <input type="range" id="slider-angle" min="10" max="80" step="1" value="30" />
    <div id="animation-controls">
      <p>release block</p>
      <button id="btn-play">▶</button>
      <button id="btn-pause">||</button>
      <button id="btn-reset">|<</button>
    </div>
  </div>

  <!-- Visualization Section -->
  <div id="visualization-area">
    <p id="info-display"></p>
    <!-- Placeholder canvas: will be replaced by the p5-created canvas with the same id -->
    <canvas id="p5-canvas"></canvas>
  </div>

  <script>
    // --- Global UI references ---
    const sliderAngle = document.getElementById('slider-angle');
    const btnPlay = document.getElementById('btn-play');
    const btnPause = document.getElementById('btn-pause');
    const btnReset = document.getElementById('btn-reset');
    const infoDisplay = document.getElementById('info-display');

    // --- Physics and state variables (shared with p5) ---
    let angleDeg = 30;
    let thetaRad = angleDeg * Math.PI / 180;
    const g = 9.81;

    let isPlaying = false;
    let startTimeMs = 0;
    let sAnchor_m = 0;  // displacement along slope at the time a play started (meters)
    let vAnchor = 0;    // velocity along slope at the time a play started (m/s)
    let s_m = 0;        // current displacement along slope (meters) measured from top
    let v = 0;          // current velocity along slope (m/s)
    let a = g * Math.sin(thetaRad); // acceleration along slope (m/s^2)

    // Geometry/scales
    let pxPerMeter = 50;        // will be computed from canvas/ramp geometry
    const rampLength_m = 10.0;  // physical length of ramp in meters
    let rampHypPx = 600;        // ramp hypotenuse in pixels (computed)
    let basePx = 0, heightPx = 0;
    let origin = { x: 100, y: 100 }; // bottom-left vertex of ramp in canvas coordinates (computed)
    let canvasW = 900, canvasH = 600;

    // Block geometry (in pixels along slope reference)
    let blockLen = 50;     // along-slope length
    let blockThick = 30;   // normal thickness
    let s0Pix = blockLen / 2; // center offset from top vertex along slope (pixels)

    // Vector drawing scale
    const gravVecLenPx = 90; // base length for mg arrow in pixels

    // --- Info display with MathJax ---
    function updateInfo() {
      a = g * Math.sin(thetaRad);
      const aStr = a.toFixed(3);
      const txt = `slope = \\( \\theta \\) = ${angleDeg}°; acceleration = \\( g \\sin(\\theta) \\) = ${aStr} m/s²`;
      infoDisplay.innerHTML = txt;
      if (window.MathJax && MathJax.typesetPromise) {
        MathJax.typesetPromise([infoDisplay]).catch(err => console.warn(err));
      }
    }

    // --- Motion control helpers ---
    function resetMotion(p5inst) {
      isPlaying = false;
      s_m = 0;
      v = 0;
      sAnchor_m = 0;
      vAnchor = 0;
      startTimeMs = p5inst ? p5inst.millis() : (window.performance?.now?.() || 0);
    }
    function pauseMotion() {
      isPlaying = false;
    }
    function startMotion(p5inst) {
      if (!p5inst) return;
      // resume or start from current state
      startTimeMs = p5inst.millis();
      sAnchor_m = s_m;
      vAnchor = v;
      isPlaying = true;
    }

    // --- Geometry based on canvas size and angle ---
    function updateRampGeometry() {
      // Choose margins and derive the ramp length to fit inside canvas
      const margin = 60;
      // Ramp hypotenuse constrained by canvas
      const maxHypByWidth = (canvasW - 2 * margin) * 0.95;
      const maxHypByHeight = (canvasH - 2 * margin) * 0.95;
      rampHypPx = Math.max(100, Math.min(maxHypByWidth, maxHypByHeight));

      basePx = rampHypPx * Math.cos(thetaRad);
      heightPx = rampHypPx * Math.sin(thetaRad);

      // Place origin a bit from left and bottom so everything fits
      origin.x = margin;
      origin.y = canvasH - margin;

      // Map meters to pixels along the hypotenuse
      pxPerMeter = rampHypPx / rampLength_m;

      // Update block geometry relative to ramp
      blockLen = Math.max(40, Math.min(0.10 * rampHypPx, 70));
      blockThick = Math.max(24, Math.min(0.06 * rampHypPx, 40));
      s0Pix = blockLen / 2;
    }

    // --- UI bindings ---
    sliderAngle.addEventListener('input', () => {
      angleDeg = parseInt(sliderAngle.value, 10);
      thetaRad = angleDeg * Math.PI / 180;
      updateInfo();
      // Geometry changes with angle
      updateRampGeometry();
      // Reset animation
      resetMotion(p5);
    });

    btnPlay.addEventListener('click', () => startMotion(p5));
    btnPause.addEventListener('click', () => pauseMotion());
    btnReset.addEventListener('click', () => resetMotion(p5));

    // Initialize info on load
    updateInfo();

    // --- p5 sketch ---
    let p5Canvas;

    function setup() {
      // Compute initial canvas size to fill visualization area beneath the info display
      const viz = document.getElementById('visualization-area');
      const info = document.getElementById('info-display');
      const rect = viz.getBoundingClientRect();
      const infoH = info.getBoundingClientRect().height;
      canvasW = Math.max(300, rect.width);
      canvasH = Math.max(300, rect.height - infoH);

      p5Canvas = createCanvas(canvasW, canvasH);
      // Replace the placeholder canvas with the p5 canvas, keeping the same id
      const placeholder = document.getElementById('p5-canvas');
      p5Canvas.id('p5-canvas');
      if (placeholder && placeholder.parentNode) {
        placeholder.replaceWith(p5Canvas.elt);
      }

      frameRate(60);
      textFont('system-ui, -apple-system, Segoe UI, Roboto, Arial, sans-serif');

      updateRampGeometry();
      resetMotion(this);
    }

    function windowResized() {
      // Resize canvas to always fill the visualization area beneath info
      const viz = document.getElementById('visualization-area');
      const info = document.getElementById('info-display');
      const rect = viz.getBoundingClientRect();
      const infoH = info.getBoundingClientRect().height;
      canvasW = Math.max(300, rect.width);
      canvasH = Math.max(300, rect.height - infoH);

      resizeCanvas(canvasW, canvasH);
      updateRampGeometry();
    }

    function drawBackgroundGrid() {
      push();
      stroke(30);
      const step = 40;
      for (let x = 0; x < width; x += step) {
        line(x, 0, x, height);
      }
      for (let y = 0; y < height; y += step) {
        line(0, y, width, y);
      }
      pop();
    }

    function drawAngleMarkerAtOrigin() {
      push();
      translate(origin.x, origin.y);
      noFill();
      stroke(200);
      strokeWeight(1.5);
      const r = 40;
      // Arc from ground (0 rad) to hypotenuse direction (-theta)
      arc(0, 0, 2*r, 2*r, -thetaRad, 0);
      noStroke();
      fill(220);
      textSize(14);
      textAlign(CENTER, CENTER);
      const tx = Math.cos(-thetaRad/2) * (r + 12);
      const ty = Math.sin(-thetaRad/2) * (r + 12);
      text('θ', tx, ty);
      pop();
    }

    function drawAxes() {
      // Axes origin slightly above the ramp surface near the bottom
      const n_up = { x: -Math.sin(thetaRad), y: -Math.cos(thetaRad) };
      const axesOrigin = {
        x: origin.x + n_up.x * 22,
        y: origin.y + n_up.y * 22
      };
      const u_upSlope = { x: Math.cos(thetaRad), y: -Math.sin(thetaRad) }; // from bottom to top
      const axisLen = 80;

      drawArrow(axesOrigin.x, axesOrigin.y,
                axesOrigin.x + u_upSlope.x * axisLen,
                axesOrigin.y + u_upSlope.y * axisLen,
                '#e6e6e6', 'x');
      drawArrow(axesOrigin.x, axesOrigin.y,
                axesOrigin.x + n_up.x * axisLen * 0.85,
                axesOrigin.y + n_up.y * axisLen * 0.85,
                '#e6e6e6', 'y');
    }

    function drawRamp() {
      push();
      translate(origin.x, origin.y);
      stroke('#ffd166');
      strokeWeight(2);
      fill('#1b2a49'); // lightblue-ish on dark theme
      triangle(0, 0, basePx, 0, basePx, -heightPx);
      pop();
    }

    function drawBlockAndForces() {
      // Compute top vertex (right-top) and unit vectors
      const T = { x: origin.x + basePx, y: origin.y - heightPx };
      const u_down = { x: -Math.cos(thetaRad), y: Math.sin(thetaRad) }; // down-slope (top -> bottom)
      const n_up = { x: -Math.sin(thetaRad), y: -Math.cos(thetaRad) };  // outward from plane
      const n_down = { x: -n_up.x, y: -n_up.y };

      // Current displacement along slope in pixels (from top)
      const sPix = s_m * pxPerMeter;
      // Clamp so block stays on ramp
      const sPixMax = Math.max(0, rampHypPx - blockLen);
      const sPixClamped = Math.min(sPix, sPixMax);
      const center = {
        x: T.x + (s0Pix + sPixClamped) * u_down.x,
        y: T.y + (s0Pix + sPixClamped) * u_down.y
      };

      // Draw block (oriented to the slope)
      push();
      translate(center.x, center.y);
      rotate(-thetaRad);
      rectMode(CENTER);
      stroke(0);
      strokeWeight(1.5);
      fill('#a0a6b0');
      rect(0, 0, blockLen, blockThick, 4);
      pop();

      // Force vectors
      const gravEnd = { x: center.x, y: center.y + gravVecLenPx }; // vertical down
      drawArrow(center.x, center.y, gravEnd.x, gravEnd.y, '#ff4d4d', 'F_grav = m g');

      const normalLen = gravVecLenPx * Math.cos(thetaRad);
      const normalEnd = { x: center.x + n_up.x * normalLen, y: center.y + n_up.y * normalLen };
      drawArrow(center.x, center.y, normalEnd.x, normalEnd.y, 'deepskyblue', '"normal" force');

      const netLen = gravVecLenPx * Math.sin(thetaRad);
      const netEnd = { x: center.x + u_down.x * netLen, y: center.y + u_down.y * netLen };
      drawArrow(center.x, center.y, netEnd.x, netEnd.y, 'purple', 'F_net = m g sin(θ)');

      // Decomposition: components of gravity along slope and normal (dashed)
      const compPar = { x: center.x + u_down.x * (gravVecLenPx * Math.sin(thetaRad)),
                        y: center.y + u_down.y * (gravVecLenPx * Math.sin(thetaRad)) };
      const compPerp = { x: center.x + n_down.x * (gravVecLenPx * Math.cos(thetaRad)),
                         y: center.y + n_down.y * (gravVecLenPx * Math.cos(thetaRad)) };

      push();
      stroke(0);
      strokeWeight(1.2);
      drawingContext.setLineDash([6, 6]);
      // From center to components
      line(center.x, center.y, compPar.x, compPar.y);
      line(center.x, center.y, compPerp.x, compPerp.y);
      // From component endpoints to gravity endpoint to form a rectangle (projection lines)
      line(compPar.x, compPar.y, gravEnd.x, gravEnd.y);   // along normal
      line(compPerp.x, compPerp.y, gravEnd.x, gravEnd.y); // along slope
      drawingContext.setLineDash([]);
      pop();

      // Labels for component magnitudes
      push();
      noStroke();
      fill('#dcdcdc');
      textSize(13);
      // Label for perpendicular component (m g cos θ)
      const midPerp = { x: (center.x + compPerp.x) / 2, y: (center.y + compPerp.y) / 2 };
      textAlign(LEFT, BOTTOM);
      text('m g cos(θ)', midPerp.x + 6, midPerp.y - 4);
      pop();

      // Small arc showing θ between gravity (down) and its perpendicular component
      push();
      noFill();
      stroke('#bbbbbb');
      strokeWeight(1.2);
      const r = 18;
      // angle of n_down from +x
      const ang_nDown = Math.atan2(n_down.y, n_down.x);
      const ang_grav = Math.PI / 2; // vertical down
      arc(center.x, center.y, 2*r, 2*r, ang_nDown, ang_grav);
      noStroke();
      fill('#e0e0e0');
      textSize(12);
      const ang_mid = (ang_nDown + ang_grav) / 2;
      const tx = center.x + Math.cos(ang_mid) * (r + 10);
      const ty = center.y + Math.sin(ang_mid) * (r + 10);
      text('θ', tx, ty);
      pop();
    }

    // Generic arrow drawing helper
    function drawArrow(x1, y1, x2, y2, color = '#fff', label = '') {
      push();
      stroke(color);
      strokeWeight(2);
      line(x1, y1, x2, y2);
      // Arrowhead
      const angle = Math.atan2(y2 - y1, x2 - x1);
      const ah = 8;
      translate(x2, y2);
      rotate(angle);
      line(0, 0, -ah, -ah * 0.6);
      line(0, 0, -ah, ah * 0.6);
      // Label
      noStroke();
      fill(color);
      textSize(13);
      textAlign(LEFT, CENTER);
      const offset = 6;
      const lx = Math.cos(angle) * offset - Math.sin(angle) * 10;
      const ly = Math.sin(angle) * offset + Math.cos(angle) * 10;
      text(label, lx, ly);
      pop();
    }

    function draw() {
      background('#0a0d1a');
      drawBackgroundGrid();

      // Update physics if playing
      if (isPlaying) {
        const t = (millis() - startTimeMs) / 1000; // sec since last (re)start
        s_m = sAnchor_m + vAnchor * t + 0.5 * a * t * t;
        v = vAnchor + a * t;

        // Stop at bottom
        const sPixMax = Math.max(0, rampHypPx - blockLen);
        const sMax_m = sPixMax / pxPerMeter;
        if (s_m >= sMax_m) {
          s_m = sMax_m;
          v = 0;
          isPlaying = false;
        }
      }

      drawRamp();
      drawAngleMarkerAtOrigin();
      drawAxes();
      drawBlockAndForces();
    }

    // Expose p5 required functions to global (using global mode)
    window.setup = setup;
    window.draw = draw;
    window.windowResized = windowResized;
  </script>
</body>
</html>
